/**
 * Describes the response structure from the /token endpoint.
 */
export interface TokenResponse {
  token: string;
  user_id: number;
  user_email: string;
  user_nicename: string;
  user_display_name: string;
  refresh_token: string;
  [key: string]: any; // Allow other properties if the API returns more
}

/**
 * Describes the response structure from the /validate endpoint.
 */
export interface ValidateApiResponse {
  code: string; // e.g., "jwt_auth_valid_token"
  data: {
    status: number;
  };
}

/**
 * Describes the response structure from the /refresh endpoint.
 */
export interface RefreshResponse extends TokenResponse {}

/**
 * Represents the structure of user data.
 */
export interface UserData {
  id: number;
  email: string;
  nicename: string;
  displayName: string;
}

/**
 * Interface for custom token storage strategies.
 */
export interface ITokenStorageStrategy {
  /** Retrieves an item from storage. */
  getItem(key: string): Promise<string | null> | string | null;
  /** Sets an item in storage. */
  setItem(key: string, value: string): Promise<void> | void;
  /** Removes an item from storage. */
  removeItem(key: string): Promise<void> | void;
  /** Optional: Clears all related keys from storage. */
  clearAll?(): Promise<void> | void;

