    };

    const mergedStorageKeys: StorageKeys = {
      accessToken: config.storageKeys?.accessToken || defaultStorageKeys.accessToken,
      refreshToken: config.storageKeys?.refreshToken || defaultStorageKeys.refreshToken,
      userData: config.storageKeys?.userData || defaultStorageKeys.userData
    };

