import { describe, it, expect, beforeEach, vi } from 'vitest';
import { JWTPro } from '../../src/JWTPro';
import { http, HttpResponse } from 'msw';
import { server } from '../mocks/server';

describe('Rate Limiting Support', () => {
  const baseUrl = 'https://test-wordpress.com';
  let client: JWTPro;

  beforeEach(() => {
    client = new JWTPro({
      baseUrl,
      tokenStorageStrategy: 'memory'
    });
  });

  it('should parse rate limit headers from successful responses', async () => {
    // Mock a response with rate limit headers
    server.use(
      http.post(`${baseUrl}/jwt-auth/v1/token`, () => {
        return HttpResponse.json({
          token: 'test-access-token',
          refresh_token: 'test-refresh-token',
          user_id: 1,
          user_email: 'test@example.com',
          user_nicename: 'testuser',
          user_display_name: 'Test User'
        }, {
          headers: {
            'X-RateLimit-Limit': '60',
            'X-RateLimit-Remaining': '45',
            'X-RateLimit-Reset': '1707411600'
          }
        });
      })
    );

    await client.login('testuser', 'testpass');

    const rateLimitInfo = client.getRateLimitInfo();
    expect(rateLimitInfo).toBeDefined();
    expect(rateLimitInfo).toEqual({
      limit: 60,
      remaining: 45,
      reset: 1707411600
    });
  });

  it('should handle 429 responses with rate limiting information', async () => {
    // Mock a 429 response with rate limit headers
    server.use(
      http.post(`${baseUrl}/jwt-auth/v1/token`, () => {
        return HttpResponse.json({
          code: 'jwt_auth_rate_limited',
          message: 'Rate limit exceeded',
          data: {
            status: 429
          }
        }, {
          status: 429,
          headers: {
            'X-RateLimit-Limit': '5',
            'X-RateLimit-Remaining': '0',
            'X-RateLimit-Reset': '1707411720',
            'Retry-After': '120'
          }
        });
      })
    );

    try {
      await client.login('testuser', 'testpass');
      expect(true).toBe(false); // Should not reach here
    } catch (error: any) {
      expect(error.status).toBe(429);
      expect(error.message).toContain('Rate limited - retry after 120 seconds');
      expect(error.rateLimitInfo).toEqual({
        limit: 5,
        remaining: 0,
        reset: 1707411720,
        retryAfter: 120
      });
    }

    const rateLimitInfo = client.getRateLimitInfo();
    expect(rateLimitInfo).toEqual({
      limit: 5,
      remaining: 0,
      reset: 1707411720,
      retryAfter: 120
    });
  });

  it('should return null when no rate limit headers are present', async () => {
    // Mock a response without rate limit headers
    server.use(
      http.post(`${baseUrl}/jwt-auth/v1/token`, () => {
        return HttpResponse.json({
          token: 'test-access-token',
          refresh_token: 'test-refresh-token',
          user_id: 1,
          user_email: 'test@example.com',
          user_nicename: 'testuser',
          user_display_name: 'Test User'
        });
      })
    );

    await client.login('testuser', 'testpass');

    const rateLimitInfo = client.getRateLimitInfo();
    expect(rateLimitInfo).toBeNull();
  });

  it('should update rate limit info on subsequent API calls', async () => {
    let callCount = 0;

    server.use(
      http.get(`${baseUrl}/wp/v2/posts`, () => {
        callCount++;
        return HttpResponse.json([{ id: 1, title: 'Test Post' }], {
          headers: {
            'X-RateLimit-Limit': '60',
            'X-RateLimit-Remaining': String(60 - callCount),
            'X-RateLimit-Reset': '1707411600'
          }
        });
      })
    );

    // Login first (without rate limit headers)
    await client.login('testuser', 'testpass');
    expect(client.getRateLimitInfo()).toBeNull();

    // Make API call with rate limit headers
    await client.api('GET', 'wp/v2/posts');
    expect(client.getRateLimitInfo()).toEqual({
      limit: 60,
      remaining: 59,
      reset: 1707411600
    });

    // Make another API call to see updated remaining count
    await client.api('GET', 'wp/v2/posts');
    expect(client.getRateLimitInfo()).toEqual({
      limit: 60,
      remaining: 58,
      reset: 1707411600
    });
  });
});

import { describe, it, expect, beforeEach, vi } from 'vitest';
import { JWTPro } from '../../src/JWTPro';
import { server } from '../mocks/server';
import { http, HttpResponse } from 'msw';

describe('Interceptors Integration Tests', () => {
  const baseUrl = 'https://test-wordpress.com';
  let client: JWTPro;

  beforeEach(() => {
    client = new JWTPro({
      baseUrl,
      tokenStorageStrategy: 'memory'
    });
  });

  describe('Request Interceptor Integration', () => {
    it('should modify requests before they are sent', async () => {
      const requestInterceptor = vi.fn((request) => ({
        ...request,
        headers: {
          ...request.headers,
          'X-Custom-Header': 'interceptor-value'
        }
      }));

      client.interceptors.addRequestInterceptor(requestInterceptor);

      // Add a handler to verify the intercepted request
      server.use(
        http.post(`${baseUrl}/jwt-auth/v1/token`, ({ request }) => {
          const customHeader = request.headers.get('X-Custom-Header');
          if (customHeader === 'interceptor-value') {
            return HttpResponse.json({
              token: 'interceptor-token',
              refresh_token: 'interceptor-refresh-token',
              user_id: 1,
              user_email: 'test@example.com',
              user_nicename: 'testuser',
              user_display_name: 'Test User'
            });
          }
          return HttpResponse.json(
            { code: 'invalid_request', message: 'Missing interceptor header' },
            { status: 400 }
          );
        })
      );

      await client.login('testuser', 'testpass');

      expect(requestInterceptor).toHaveBeenCalled();

      // Verify the token was received (meaning the interceptor worked)
      const token = await client.token();
      expect(token).toBe('interceptor-token');
    });

    it('should apply multiple request interceptors in order', async () => {
      const interceptor1 = vi.fn((request) => ({
        ...request,
        headers: { ...request.headers, 'X-First': 'first' }
      }));

      const interceptor2 = vi.fn((request) => ({
        ...request,
        headers: { ...request.headers, 'X-Second': 'second' }
      }));

      client.interceptors.addRequestInterceptor(interceptor1);
      client.interceptors.addRequestInterceptor(interceptor2);

            server.use(
        http.post(`${baseUrl}/jwt-auth/v1/token`, ({ request }) => {
          const firstHeader = request.headers.get('X-First');
          const secondHeader = request.headers.get('X-Second');

          if (firstHeader === 'first' && secondHeader === 'second') {
            return HttpResponse.json({
              token: 'multi-interceptor-token',
              refresh_token: 'multi-interceptor-refresh-token',
              user_id: 1,
              user_email: 'test@example.com',
              user_nicename: 'testuser',
              user_display_name: 'Test User'
            });
          }

          return HttpResponse.json(
            { code: 'invalid_request', message: 'Missing headers' },
            { status: 400 }
          );
        })
      );

      await client.login('testuser', 'testpass');

      expect(interceptor1).toHaveBeenCalled();
      expect(interceptor2).toHaveBeenCalled();

      const token = await client.token();
      expect(token).toBe('multi-interceptor-token');
    });

    it('should allow interceptors to modify API requests', async () => {
      await client.login('testuser', 'testpass');

      const apiInterceptor = vi.fn((request) => ({
        ...request,
        headers: { ...request.headers, 'X-API-Version': 'v2' }
      }));

      client.interceptors.addRequestInterceptor(apiInterceptor);

      server.use(
        http.get(`${baseUrl}/wp/v2/posts`, ({ request }) => {
          const apiVersion = request.headers.get('X-API-Version');
          if (apiVersion === 'v2') {
            return HttpResponse.json([{ id: 1, title: { rendered: 'Intercepted Post' } }]);
          }
          return HttpResponse.json(
            { code: 'invalid_version', message: 'Missing API version' },
            { status: 400 }
          );
        })
      );

      const posts = await client.api('GET', 'wp/v2/posts');

      expect(apiInterceptor).toHaveBeenCalled();
      expect(posts[0].title.rendered).toBe('Intercepted Post');
    });
  });

  describe('Response Interceptor Integration', () => {
    it('should modify responses after they are received', async () => {
      const responseInterceptor = vi.fn((response) => {
        // Just return the response - we can't modify it without consuming the body
        // The test is just checking that the interceptor gets called
        return response;
      });

      client.interceptors.addResponseInterceptor(responseInterceptor);

      await client.login('testuser', 'testpass');

      expect(responseInterceptor).toHaveBeenCalled();

      // The response should have been processed by the interceptor
      const calledArgs = responseInterceptor.mock.calls[0];
      expect(calledArgs[0]).toBeDefined(); // Response object
      expect(calledArgs.length).toBeGreaterThanOrEqual(1); // At least response object
    });

    it('should handle async response interceptors', async () => {
      const asyncResponseInterceptor = vi.fn(async (response) => {
        // Simulate async processing
        await new Promise(resolve => setTimeout(resolve, 10));
        // Return the response unchanged
        return response;
      });

      client.interceptors.addResponseInterceptor(asyncResponseInterceptor);

      await client.login('testuser', 'testpass');

      expect(asyncResponseInterceptor).toHaveBeenCalled();
    });

    it('should apply response interceptors to API calls', async () => {
      await client.login('testuser', 'testpass');

      const responseInterceptor = vi.fn((response) => response);

      client.interceptors.addResponseInterceptor(responseInterceptor);

      await client.api('GET', 'wp/v2/posts');

      expect(responseInterceptor).toHaveBeenCalled();
    });
  });

  describe('Error Interceptor Integration', () => {
    it('should handle and modify errors', async () => {
      // Test error interceptor with actual network error by using non-existent domain
      const errorInterceptor = vi.fn((error) => {
        return {
          ...error,
          intercepted: true,
          message: 'Intercepted: ' + (error.message || 'Unknown error')
        };
      });

      const networkClient = new JWTPro({
        baseUrl: 'https://non-existent-domain-12345.com',
        tokenStorageStrategy: 'memory'
      });

      networkClient.interceptors.addErrorInterceptor(errorInterceptor);

      try {
        await networkClient.login('testuser', 'testpass');
        expect(true).toBe(false); // Should not reach here
      } catch (error: any) {
        expect(errorInterceptor).toHaveBeenCalled();
        expect(error.intercepted).toBe(true);
        expect(error.message).toContain('Intercepted:');
      }
    });

    it('should handle network errors with interceptors', async () => {
      const errorInterceptor = vi.fn((error) => ({
        ...error,
        handled: true
      }));

      client.interceptors.addErrorInterceptor(errorInterceptor);

      // Create a client with invalid URL to trigger network error
      const invalidClient = new JWTPro({
        baseUrl: 'https://non-existent-domain-12345.com',
        tokenStorageStrategy: 'memory'
      });

      invalidClient.interceptors.addErrorInterceptor(errorInterceptor);

      try {
        await invalidClient.login('testuser', 'testpass');
      } catch (error: any) {
        expect(errorInterceptor).toHaveBeenCalled();
        expect(error.handled).toBe(true);
      }
    });

    it('should handle API errors with interceptors', async () => {
      await client.login('testuser', 'testpass');

      const errorInterceptor = vi.fn((error) => ({
        ...error,
        apiError: true
      }));

      client.interceptors.addErrorInterceptor(errorInterceptor);

      try {
        // Try to access without authentication (should cause 401)
        await client.api('GET', 'wp/v2/posts');
      } catch (error: any) {
        // The mock will return 401 for unauthenticated requests
        if (error.status === 401) {
          expect(errorInterceptor).toHaveBeenCalled();
        }
      }
    });
  });

  describe('Interceptor Removal Integration', () => {
    it('should remove request interceptors correctly', async () => {
      const interceptor = vi.fn((request) => ({
        ...request,
        headers: { ...request.headers, 'X-Should-Not-Appear': 'true' }
      }));

      const removeInterceptor = client.interceptors.addRequestInterceptor(interceptor);

      // First call should use interceptor
      await client.login('testuser', 'testpass');
      expect(interceptor).toHaveBeenCalledTimes(1);

      // Remove interceptor
      removeInterceptor();
      interceptor.mockClear();

      // Second call should not use interceptor
      await client.logout();
      await client.login('testuser', 'testpass');
      expect(interceptor).not.toHaveBeenCalled();
    });

    it('should remove response interceptors correctly', async () => {
      const interceptor = vi.fn((response) => response);

      const removeInterceptor = client.interceptors.addResponseInterceptor(interceptor);

      await client.login('testuser', 'testpass');
      expect(interceptor).toHaveBeenCalledTimes(1);

      removeInterceptor();
      interceptor.mockClear();

      await client.logout();
      await client.login('testuser', 'testpass');
      expect(interceptor).not.toHaveBeenCalled();
    });

    it('should remove error interceptors correctly', async () => {
      const interceptor = vi.fn((error) => error);

      const networkClient = new JWTPro({
        baseUrl: 'https://non-existent-domain-12345.com',
        tokenStorageStrategy: 'memory'
      });

      const removeInterceptor = networkClient.interceptors.addErrorInterceptor(interceptor);

      try {
        await networkClient.login('testuser', 'testpass');
        expect(true).toBe(false); // Should not reach here
      } catch (error) {
        // Expected - should have called interceptor
      }
      expect(interceptor).toHaveBeenCalledTimes(1);

      removeInterceptor();
      interceptor.mockClear();

      try {
        await networkClient.login('testuser', 'testpass');
        expect(true).toBe(false); // Should not reach here
      } catch (error) {
        // Expected - should NOT have called interceptor
      }
      expect(interceptor).not.toHaveBeenCalled();
    });
  });

  describe('Interceptor Error Handling Integration', () => {
    it('should handle interceptor errors gracefully', async () => {
      const errorInterceptor = vi.fn(() => {
        throw new Error('Interceptor error');
      });

      client.interceptors.addRequestInterceptor(errorInterceptor);

      await expect(client.login('testuser', 'testpass')).rejects.toThrow('Interceptor error');
    });

    it('should handle response interceptor errors', async () => {
      const errorInterceptor = vi.fn(() => {
        throw new Error('Response interceptor error');
      });

      client.interceptors.addResponseInterceptor(errorInterceptor);

      await expect(client.login('testuser', 'testpass')).rejects.toThrow('Response interceptor error');
    });
  });

  describe('Complex Interceptor Scenarios', () => {
    it('should handle interceptors with authentication flow', async () => {
      const requestInterceptor = vi.fn((request) => {
        // Add timestamp to all requests
        return {
          ...request,
          headers: {
            ...request.headers,
            'X-Timestamp': Date.now().toString()
          }
        };
      });

      const responseInterceptor = vi.fn((response) => {
        // Log all responses
        console.log('Response intercepted:', response.status);
        return response;
      });

      const errorInterceptor = vi.fn((error) => {
        // Add context to errors
        return {
          ...error,
          context: 'Authentication flow'
        };
      });

      client.interceptors.addRequestInterceptor(requestInterceptor);
      client.interceptors.addResponseInterceptor(responseInterceptor);
      client.interceptors.addErrorInterceptor(errorInterceptor);

      // Test successful flow
      await client.login('testuser', 'testpass');
      await client.refresh();
      await client.logout(); // logout doesn't make HTTP requests

      expect(requestInterceptor).toHaveBeenCalledTimes(2); // login, refresh (logout is local only)
      expect(responseInterceptor).toHaveBeenCalledTimes(2);

      // Test error flow with network error (HTTP errors don't trigger error interceptors)
      const networkClient = new JWTPro({
        baseUrl: 'https://non-existent-domain-12345.com',
        tokenStorageStrategy: 'memory'
      });

      networkClient.interceptors.addErrorInterceptor(errorInterceptor);

      try {
        await networkClient.login('testuser', 'testpass');
      } catch (error: any) {
        expect(error.context).toBe('Authentication flow');
      }

      expect(errorInterceptor).toHaveBeenCalledTimes(1);
    });

    it('should maintain interceptor state across different operations', async () => {
      let requestCount = 0;

      const countingInterceptor = vi.fn((request) => {
        requestCount++;
        return {
          ...request,
          headers: {
            ...request.headers,
            'X-Request-Count': requestCount.toString()
          }
        };
      });

      client.interceptors.addRequestInterceptor(countingInterceptor);

      await client.login('testuser', 'testpass');
      await client.api('GET', 'wp/v2/posts');
      await client.refresh();

      expect(requestCount).toBe(3);
      expect(countingInterceptor).toHaveBeenCalledTimes(3);
    });
  });
});

import { describe, it, expect, beforeEach, vi } from 'vitest';
import { JWTPro } from '../../src/JWTPro';

describe('Events Integration Tests', () => {
  const baseUrl = 'https://test-wordpress.com';
  let client: JWTPro;

  beforeEach(() => {
    client = new JWTPro({
      baseUrl,
      tokenStorageStrategy: 'memory'
    });
  });

  describe('Authentication Event Flow', () => {
    it('should emit events in correct order during login flow', async () => {
      const events: string[] = [];

      client.on('login', () => events.push('login'));
      client.on('error', () => events.push('error'));

      await client.login('testuser', 'testpass');

      expect(events).toEqual(['login']);
    });

    it('should emit error event on failed login', async () => {
      const errorEvents: any[] = [];

      client.on('error', (event) => errorEvents.push(event));

      try {
        await client.login('invalid', 'invalid');
      } catch (error) {
        // Expected to throw
      }

      expect(errorEvents).toHaveLength(1);
      expect(errorEvents[0].type).toBe('error');
      expect(errorEvents[0].error).toBeInstanceOf(Error);
    });

    it('should emit logout event when logging out', async () => {
      const logoutEvents: any[] = [];

      client.on('logout', (event) => logoutEvents.push(event));

      await client.login('testuser', 'testpass');
      await client.logout();

      expect(logoutEvents).toHaveLength(1);
      expect(logoutEvents[0].type).toBe('logout');
    });

    it('should emit tokenRefresh event when refreshing tokens', async () => {
      const refreshEvents: any[] = [];

      client.on('tokenRefresh', (event) => refreshEvents.push(event));

      await client.login('testuser', 'testpass');
      await client.refresh();

      expect(refreshEvents).toHaveLength(1);
      expect(refreshEvents[0].type).toBe('tokenRefresh');
      expect(refreshEvents[0].data.userData).toBeDefined();
    });
  });

  describe('Event Data Integrity', () => {
    it('should provide correct user data in login event', async () => {
      let loginEventData: any = null;

      client.on('login', (event) => {
        loginEventData = event.data;
      });

      await client.login('testuser', 'testpass');

      expect(loginEventData).toEqual({
        userData: {
          id: 1,
          email: 'test@example.com',
          nicename: 'testuser',
          displayName: 'Test User'
        }
      });
    });

    it('should provide error details in error events', async () => {
      let errorEventData: any = null;

      client.on('error', (event) => {
        errorEventData = event;
      });

      try {
        await client.login('invalid', 'invalid');
      } catch (error) {
        // Expected to throw
      }

      expect(errorEventData.error).toBeInstanceOf(Error);
      expect(errorEventData.data).toBeDefined();
      expect(errorEventData.data.status).toBe(401);
    });

    it('should provide updated user data in tokenRefresh event', async () => {
      let refreshEventData: any = null;

      client.on('tokenRefresh', (event) => {
        refreshEventData = event.data;
      });

      await client.login('testuser', 'testpass');
      await client.refresh();

      expect(refreshEventData.userData).toEqual({
        id: 1,
        email: 'test@example.com',
        nicename: 'testuser',
        displayName: 'Test User'
      });
    });
  });

  describe('Multiple Event Listeners', () => {
    it('should call all registered listeners for an event', async () => {
      const listener1 = vi.fn();
      const listener2 = vi.fn();
      const listener3 = vi.fn();

      client.on('login', listener1);
      client.on('login', listener2);
      client.on('login', listener3);

      await client.login('testuser', 'testpass');

      expect(listener1).toHaveBeenCalledTimes(1);
      expect(listener2).toHaveBeenCalledTimes(1);
      expect(listener3).toHaveBeenCalledTimes(1);
    });

    it('should handle listener removal correctly', async () => {
      const listener1 = vi.fn();
      const listener2 = vi.fn();

      const remove1 = client.on('login', listener1);
      client.on('login', listener2);

      await client.login('testuser', 'testpass');
      expect(listener1).toHaveBeenCalledTimes(1);
      expect(listener2).toHaveBeenCalledTimes(1);

      // Reset and remove first listener
      listener1.mockClear();
      listener2.mockClear();
      remove1();

      await client.logout();
      await client.login('testuser', 'testpass');

      expect(listener1).not.toHaveBeenCalled();
      expect(listener2).toHaveBeenCalledTimes(1);
    });
  });

  describe('Event Listener Error Handling', () => {
    it('should continue executing other listeners if one throws', async () => {
      const workingListener = vi.fn();
      const errorListener = vi.fn(() => {
        throw new Error('Listener error');
      });

      client.on('login', errorListener);
      client.on('login', workingListener);

      // Should not throw even though listener throws
      await expect(client.login('testuser', 'testpass')).resolves.toBeDefined();

      expect(workingListener).toHaveBeenCalled();
    });
  });

  describe('Event Timing', () => {
    it('should emit login event after tokens are stored', async () => {
      let tokenAtEventTime: string | null = null;

      client.on('login', async () => {
        tokenAtEventTime = await client.token();
      });

      await client.login('testuser', 'testpass');

      expect(tokenAtEventTime).toBe('test-access-token');
    });



    it('should emit tokenRefresh event after new tokens are stored', async () => {
      let tokenAtEventTime: string | null = null;

      client.on('tokenRefresh', async () => {
        tokenAtEventTime = await client.token();
      });

      await client.login('testuser', 'testpass');
      await client.refresh();

      expect(tokenAtEventTime).toBe('new-access-token');
    });
  });

  describe('Cross-Instance Event Isolation', () => {
    it('should not emit events across different client instances', async () => {
      const client1 = new JWTPro({ baseUrl, tokenStorageStrategy: 'memory' });
      const client2 = new JWTPro({ baseUrl, tokenStorageStrategy: 'memory' });

      const client1Listener = vi.fn();
      const client2Listener = vi.fn();

      client1.on('login', client1Listener);
      client2.on('login', client2Listener);

      await client1.login('testuser', 'testpass');

      expect(client1Listener).toHaveBeenCalledTimes(1);
      expect(client2Listener).not.toHaveBeenCalled();
    });
  });

  describe('Event Cleanup', () => {
    it('should remove all listeners on destroy', async () => {
      const listener = vi.fn();

      client.on('login', listener);
      client.destroy();

      // Manually emit event to test (since destroy should remove listeners)
      client.events.emit({
        type: 'login',
        data: { userData: { id: 1, email: 'test@test.com', nicename: 'test', displayName: 'Test' } }
      });

      expect(listener).not.toHaveBeenCalled();
    });
  });

  describe('API Error Events', () => {
    it('should emit error events for API failures', async () => {
      const errorEvents: any[] = [];

      client.on('error', (event) => errorEvents.push(event));

      try {
        // Try to make API call without authentication
        await client.api('GET', 'wp/v2/posts');
      } catch (error) {
        // Expected to throw
      }

      expect(errorEvents).toHaveLength(1);
      expect(errorEvents[0].type).toBe('error');
    });
  });

  describe('Complex Event Scenarios', () => {
    it('should handle rapid successive authentication operations', async () => {
      const events: string[] = [];

      client.on('login', () => events.push('login'));
      client.on('logout', () => events.push('logout'));
      client.on('tokenRefresh', () => events.push('tokenRefresh'));
      client.on('error', () => events.push('error'));

      // Rapid operations
      await client.login('testuser', 'testpass');
      await client.refresh();
      await client.logout();

      expect(events).toEqual(['login', 'tokenRefresh', 'logout']);
    });

    it('should maintain event order during error scenarios', async () => {
      const events: string[] = [];

      client.on('login', () => events.push('login'));
      client.on('error', () => events.push('error'));

      // Valid login
      await client.login('testuser', 'testpass');

      // Invalid login
      try {
        await client.login('invalid', 'invalid');
      } catch (error) {
        // Expected to throw
      }

      expect(events).toEqual(['login', 'error']);
    });
  });
});

