// Advanced Usage Example (ESM)
import { JWTPro } from '@jwtpro/client';

// Initialize the client
const client = new JWTPro({
  baseUrl: 'https://your-wordpress-site.com',
  tokenStorageStrategy: 'localStorage'
});

// Set up event listeners
setupEventListeners();

// Set up interceptors
setupInterceptors();

// Event listeners for authentication events
function setupEventListeners() {
  // Login event
  client.on('login', (event) => {
    console.log('User logged in:', event.data.userData);
    document.getElementById('login-status').textContent = `Logged in as ${event.data.userData.displayName}`;
    document.getElementById('login-form').style.display = 'none';
    document.getElementById('user-info').style.display = 'block';
  });

  // Logout event
  client.on('logout', () => {
    console.log('User logged out');
    document.getElementById('login-status').textContent = 'Logged out';
    document.getElementById('login-form').style.display = 'block';
    document.getElementById('user-info').style.display = 'none';
  });

  // Token refresh event
  client.on('tokenRefresh', (event) => {
    console.log('Token refreshed at:', new Date(event.timestamp).toLocaleTimeString());
    document.getElementById('token-info').textContent = 'Token refreshed at ' +
      new Date(event.timestamp).toLocaleTimeString();
  });

  // Error event
  client.on('error', (event) => {
    console.error('Auth error:', event.error.message);
    const errorDiv = document.getElementById('error-messages');
    errorDiv.textContent = `Error: ${event.error.message}`;
    errorDiv.style.display = 'block';

    // Hide error after 5 seconds
    setTimeout(() => {
      errorDiv.style.display = 'none';
    }, 5000);
  });
}

// Set up request/response interceptors
function setupInterceptors() {
  // Add request timestamps and custom headers
  const requestInterceptor = client.interceptors.addRequestInterceptor((request) => {
    console.log(`Request to ${request.url} at ${new Date().toISOString()}`);

    // Add a custom header to every request
    if (!request.headers) request.headers = {};
    request.headers['X-Client-Version'] = '1.0.0';

    return request;
  });

  // Log response times
  const responseInterceptor = client.interceptors.addResponseInterceptor(async (response, request) => {
    console.log(`Response from ${request.url} received at ${new Date().toISOString()}`);
    console.log(`Response status: ${response.status}`);

    // Example: Automatically validate token after successful API calls
    // This helps ensure our token is still valid without client-side decoding
    if (response.status === 200 && !request.url.includes('/validate')) {
      validateTokenStatus();
    }

    return response;
  });

  // Handle and transform errors
  const errorInterceptor = client.interceptors.addErrorInterceptor(async (error, request) => {
    console.error(`Request to ${request.url} failed:`, error);

    // Example: Detect 401 errors and suggest token refresh
    if (error.status === 401) {
      const errorDiv = document.getElementById('error-messages');
      errorDiv.textContent = 'Your session has expired. Please refresh your token or log in again.';
      errorDiv.style.display = 'block';

      // Show token refresh button
      document.getElementById('refresh-button').style.display = 'block';
    }

    // You could transform the error here if needed
    return {
      ...error,
      friendlyMessage: 'Something went wrong with the request. Please try again.'
    };
  });
}

// Validate the token status - using the shorter alias
async function validateTokenStatus() {
  try {
    const isValid = await client.validate();
    const statusElement = document.getElementById('token-status');
    if (isValid) {
      statusElement.textContent = 'Token is valid';
      statusElement.style.color = 'green';
    } else {
      statusElement.textContent = 'Token is invalid or expired';
      statusElement.style.color = 'red';
      // Show refresh button
      document.getElementById('refresh-button').style.display = 'block';
    }
  } catch (error) {
    console.error('Token validation error:', error);
  }
}

// Example login function
async function login(username, password) {
  try {
    await client.login(username, password);
    // Check token status after login
    validateTokenStatus();
  } catch (error) {
    console.error('Login failed:', error);
  }
}

// Example of manual token refresh - using the shorter alias
async function manuallyRefreshToken() {
  try {
    await client.refresh();
    console.log('Token manually refreshed');
    // Validate token again after refresh
    validateTokenStatus();
    // Hide refresh button
    document.getElementById('refresh-button').style.display = 'none';
  } catch (error) {
    console.error('Manual token refresh failed:', error);
  }
}

// Logout function
function logout() {
  client.logout();
}

// Example of handling 401 errors manually - using shorter aliases
async function handleAuthenticatedRequest() {
  try {
    // Make API request using the shorter alias
    const posts = await client.api('GET', 'wp/v2/posts');
    console.log('Posts retrieved:', posts);
  } catch (error) {
    // If unauthorized, try refreshing the token
    if (error.status === 401) {
      console.log('Unauthorized, trying to refresh token...');
      try {
        await client.refresh();
        // Retry the request after token refresh
        const posts = await client.api('GET', 'wp/v2/posts');
        console.log('Posts retrieved after token refresh:', posts);
      } catch (refreshError) {
        console.error('Token refresh failed, redirecting to login...');
        // Show login form
        document.getElementById('login-form').style.display = 'block';
        document.getElementById('user-info').style.display = 'none';
      }
    } else {
      console.error('Request failed:', error);
    }
  }
}

// Rate limiting example
async function demonstrateRateLimiting() {
  try {
    // Make an API request and check rate limiting info
    const posts = await client.api('GET', 'wp/v2/posts');

    // Check rate limit information
    const rateLimitInfo = client.getRateLimitInfo();
    if (rateLimitInfo) {
      console.log('Rate Limit Info:', rateLimitInfo);

      const rateLimitDiv = document.getElementById('rate-limit-info');
      if (rateLimitDiv) {
        rateLimitDiv.innerHTML = `
          <strong>Rate Limit Status:</strong><br>
          Limit: ${rateLimitInfo.limit} requests<br>
          Remaining: ${rateLimitInfo.remaining} requests<br>
          Resets at: ${new Date(rateLimitInfo.reset * 1000).toLocaleTimeString()}<br>
          ${rateLimitInfo.retryAfter ? `Retry after: ${rateLimitInfo.retryAfter} seconds` : ''}
        `;
        rateLimitDiv.style.display = 'block';
      }

      // Warn if close to rate limit
      if (rateLimitInfo.remaining < 10) {
        console.warn(`Warning: Only ${rateLimitInfo.remaining} requests remaining!`);
        const warningDiv = document.getElementById('warning-messages');
        if (warningDiv) {
          warningDiv.textContent = `Rate limit warning: Only ${rateLimitInfo.remaining} requests remaining`;
          warningDiv.style.display = 'block';
        }
      }
    } else {
      console.log('No rate limiting information available');
    }

    console.log('API request successful:', posts);
  } catch (error) {
    // Handle rate limiting errors specifically
    if (error.status === 429) {
      console.log('Rate limited!', error);

      const errorDiv = document.getElementById('error-messages');
      if (errorDiv) {
        errorDiv.textContent = `Rate limited! ${error.rateLimitInfo?.retryAfter ? `Please wait ${error.rateLimitInfo.retryAfter} seconds.` : 'Please try again later.'}`;
        errorDiv.style.display = 'block';
      }

      // Automatic retry with exponential backoff
      if (error.rateLimitInfo?.retryAfter) {
        console.log(`Waiting ${error.rateLimitInfo.retryAfter} seconds before retry...`);
        setTimeout(() => {
          console.log('Retrying after rate limit...');
          demonstrateRateLimiting();
        }, error.rateLimitInfo.retryAfter * 1000);
      }
    } else {
      console.error('API request failed:', error);
    }
  }
}

// Clean up resources when done
function cleanup() {
  client.destroy();
}

// DOM event listeners
  document.addEventListener('DOMContentLoaded', () => {
    // Check if already logged in - using shorter aliases
    client.check().then((isAuthenticated) => {
      if (isAuthenticated) {
        client.user().then((userData) => {
          document.getElementById(
            'login-status'
          ).textContent = `Logged in as ${userData.displayName}`
          document.getElementById('login-form').style.display = 'none'
          document.getElementById('user-info').style.display = 'block'
          validateTokenStatus()
        })
      }
    })

    // Set up login form
    const loginForm = document.getElementById('login-form')
    if (loginForm) {
      loginForm.addEventListener('submit', (e) => {
