// Rate Limiting Example - Advanced usage with JWT Auth Pro
import { JWTPro } from '@jwtpro/client'

// Initialize client
const client = new JWTPro({
  baseUrl: 'https://your-wordpress-site.com',
  tokenStorageStrategy: 'memory',
})

/**
 * Smart request wrapper that handles rate limiting automatically
 */
class RateLimitedClient {
  constructor(jwtClient) {
    this.client = jwtClient
    this.requestQueue = []
    this.processing = false
    this.isRateLimited = false
  }

  async makeRequest(method, path, data = null, headers = {}) {
    return new Promise((resolve, reject) => {
      this.requestQueue.push({
        method,
        path,
        data,
        headers,
        resolve,
        reject,
        timestamp: Date.now(),
      })
      this.processQueue()
    })
  }

  async processQueue() {
    if (this.processing || this.requestQueue.length === 0) return

    this.processing = true
    console.log(`📋 Processing queue with ${this.requestQueue.length} requests`)

    while (this.requestQueue.length > 0) {
      const rateLimitInfo = this.client.getRateLimitInfo()

      // Check if we need to wait for rate limit reset
      if (this.shouldWaitForRateLimit(rateLimitInfo)) {
        const waitTime = this.calculateWaitTime(rateLimitInfo)
        console.log(`⏳ Rate limit reached. Waiting ${waitTime}ms...`)
        await this.sleep(waitTime)
      }

      const request = this.requestQueue.shift()

      try {
        console.log(`🚀 Making request: ${request.method} ${request.path}`)
        const result = await this.client.api(
          request.method,
          request.path,
          request.data,
          request.headers
        )

        // Log updated rate limit info
        this.logRateLimitInfo()

        request.resolve(result)
        this.isRateLimited = false
      } catch (error) {
        if (error.status === 429) {
          console.log(
            `🚫 Request rate limited: ${request.method} ${request.path}`
          )
          this.handleRateLimitError(error, request)
        } else {
          request.reject(error)
        }
      }

      // Small delay between requests to be respectful
      await this.sleep(200)
    }

    this.processing = false
    console.log('✅ Queue processing complete')
  }

  shouldWaitForRateLimit(rateLimitInfo) {
    if (!rateLimitInfo) return false

    // If we have very few requests remaining, wait for reset
    return rateLimitInfo.remaining <= 2
  }

  calculateWaitTime(rateLimitInfo) {
    if (!rateLimitInfo) return 0

    const now = Date.now()
    const resetTime = rateLimitInfo.reset * 1000
    const timeUntilReset = resetTime - now

    // Add a small buffer to ensure the limit has reset
    return Math.max(timeUntilReset + 1000, 0)
  }

  handleRateLimitError(error, request) {
    this.isRateLimited = true

    if (error.rateLimitInfo?.retryAfter) {
      // Re-queue the request for retry
      console.log(
        `📝 Re-queuing request for retry in ${error.rateLimitInfo.retryAfter} seconds`
      )

      setTimeout(() => {
        this.requestQueue.unshift(request) // Add back to front of queue
        this.processQueue()
      }, error.rateLimitInfo.retryAfter * 1000)
    } else {
      request.reject(error)
    }
  }

  logRateLimitInfo() {
    const rateLimitInfo = this.client.getRateLimitInfo()
    if (rateLimitInfo) {
      const resetTime = new Date(rateLimitInfo.reset * 1000)
      console.log(
        `📊 Rate Limit: ${rateLimitInfo.remaining}/${
          rateLimitInfo.limit
        } remaining (resets at ${resetTime.toLocaleTimeString()})`
      )

      if (rateLimitInfo.remaining <= 5) {
        console.log('⚠️  Warning: Approaching rate limit!')
      }
    }
  }

  sleep(ms) {
    return new Promise((resolve) => setTimeout(resolve, ms))
  }

  // Get rate limit info
  getRateLimitInfo() {
    return this.client.getRateLimitInfo()
  }

  // Check if currently rate limited
  isCurrentlyRateLimited() {
    return this.isRateLimited
  }

  // Get queue status
  getQueueStatus() {
    return {
      queueLength: this.requestQueue.length,
      processing: this.processing,
      rateLimited: this.isRateLimited,
    }
  }
}

/**
 * Example usage
 */
async function demonstrateRateLimiting() {
  try {
    console.log('🔐 Logging in...')
    await client.login('username', 'password')
    console.log('✅ Login successful')

    // Create rate-limited client wrapper
    const rateLimitedClient = new RateLimitedClient(client)

    console.log(
      '\n🧪 Testing rate limiting with multiple concurrent requests...'
    )

    // Make multiple requests to demonstrate rate limiting
    const requests = [
      rateLimitedClient.makeRequest('GET', 'wp/v2/posts', { per_page: 5 }),
      rateLimitedClient.makeRequest('GET', 'wp/v2/pages', { per_page: 3 }),
      rateLimitedClient.makeRequest('GET', 'wp/v2/users'),
      rateLimitedClient.makeRequest('GET', 'wp/v2/media', { per_page: 2 }),
      rateLimitedClient.makeRequest('GET', 'wp/v2/comments', { per_page: 10 }),
    ]

    // Execute all requests
    const results = await Promise.allSettled(requests)

    console.log('\n📈 Request Results:')
    results.forEach((result, index) => {
      if (result.status === 'fulfilled') {
        console.log(
          `✅ Request ${index + 1}: Success (${
            result.value.length || 'N/A'
          } items)`
        )
      } else {
        console.log(
          `❌ Request ${index + 1}: Failed - ${result.reason.message}`
        )
      }
    })

    // Show final rate limit status
    console.log('\n📊 Final Rate Limit Status:')
    const finalRateLimit = rateLimitedClient.getRateLimitInfo()
    if (finalRateLimit) {
      console.log(
        `   • Requests remaining: ${finalRateLimit.remaining}/${finalRateLimit.limit}`
      )
      console.log(
        `   • Reset time: ${new Date(
          finalRateLimit.reset * 1000
        ).toLocaleString()}`
      )
    }

    console.log('\n🔓 Logging out...')
    await client.logout()
    console.log('✅ Logout successful')
  } catch (error) {
    console.error('❌ Demo failed:', error.message)

    if (error.status === 429) {
      console.log('\n🚫 Rate Limited! This is expected in the demo.')
      console.log(
        'In production, the RateLimitedClient would handle this automatically.'
      )
    }
  }
}

/**
 * Example of monitoring rate limits without making requests
 */
async function monitorRateLimits() {
  console.log('\n🔍 Rate Limit Monitoring Example')

  try {
    // Make a simple request to get initial rate limit info
    await client.api('GET', 'wp/v2/posts', { per_page: 1 })

    const rateLimitInfo = client.getRateLimitInfo()
    if (rateLimitInfo) {
      console.log('📊 Current Rate Limit Status:')
      console.log(`   • Limit: ${rateLimitInfo.limit} requests per window`)
      console.log(`   • Remaining: ${rateLimitInfo.remaining} requests`)
      console.log(
        `   • Window resets: ${new Date(
          rateLimitInfo.reset * 1000
        ).toLocaleString()}`
      )

      // Calculate usage percentage
      const usagePercentage = (
        ((rateLimitInfo.limit - rateLimitInfo.remaining) /
          rateLimitInfo.limit) *
        100
      ).toFixed(1)
      console.log(`   • Usage: ${usagePercentage}% of rate limit used`)

      // Time until reset
      const timeUntilReset = rateLimitInfo.reset * 1000 - Date.now()
      const minutesUntilReset = Math.ceil(timeUntilReset / 1000 / 60)
      console.log(`   • Time until reset: ${minutesUntilReset} minutes`)

      // Recommendations
      if (rateLimitInfo.remaining < 10) {
        console.log('⚠️  Recommendation: Consider throttling requests')
      } else if (rateLimitInfo.remaining < 5) {
        console.log(
          '🚨 Recommendation: Stop making requests and wait for reset'
        )
      } else {
        console.log('✅ Rate limit status is healthy')
      }
    } else {
      console.log('ℹ️  No rate limiting detected on this WordPress site')
    }
  } catch (error) {
    console.error('Error monitoring rate limits:', error.message)
  }
}

// Run the examples
async function runExamples() {
  console.log('🚀 JWT Auth Pro Rate Limiting Demo\n')

  try {
    await demonstrateRateLimiting()
    await monitorRateLimits()
  } catch (error) {
    console.error('Example failed:', error)
  }

  console.log('\n✨ Demo complete!')
}

// Execute if run directly
if (import.meta.url === `file://${process.argv[1]}`) {
  runExamples().catch(console.error)
}

export { RateLimitedClient, demonstrateRateLimiting, monitorRateLimits }

