import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';
import path from 'path';

// Get WP site URL from environment or default to localhost
const WP_SITE_URL = process.env.WP_SITE_URL || 'http://localhost:8888';

export default defineConfig({
    plugins: [react()],
    build: {
        outDir: 'dist',
        rollupOptions: {
            input: 'admin/App.tsx',
            output: {
                entryFileNames: 'js/[name].js',
                chunkFileNames: 'js/[name]-[hash].js',
                assetFileNames: 'assets/[name][extname]'
            }
        },
        cssMinify: 'esbuild',
        cssCodeSplit: false,
        // Suppress build warnings
        minify: true,
        reportCompressedSize: false
    },
    resolve: {
        alias: {
            '@': path.resolve(__dirname, './admin')
        }
    },
    css: {
        postcss: './postcss.config.js'
    },
    server: {
        port: 3000,
        cors: true,
        strictPort: true,
        hmr: {
            protocol: 'ws',
            host: 'localhost',
            port: 3000
        },
        proxy: {
            '/wp-json': {
                target: WP_SITE_URL,
                changeOrigin: true
            }
        }
    }
});

