    54→            'methods' => 'POST',
    55→            'callback' => [$this, 'refreshToken'],
    56→            'permission_callback' => [$this, 'verifyProAccess'],
    57→            'args' => [
    58→                'refresh_token' => [
    59→                    'required' => true,
    60→                    'type' => 'string',
    61→                ],
    62→            ],
    63→        ]);
    64→    }
