    66→    public function verifyProAccess(): bool|WP_Error
    67→    {
    68→        $license = LicenseService::getInstance()->getFreemius();
    69→
    70→        if ($license->can_use_premium_code()) {
    71→            return true;
    72→        }
    73→
    74→        return new WP_Error(
