    89→    public function validateToken(WP_REST_Request $request): WP_Error|stdClass
    90→    {
    91→        $token = $this->extractToken($request);
    92→
    93→        if (is_wp_error($token)) {
    94→            return $token;
    95→        }
    96→
    97→        $tokenValidation = (new ValidateTokenAction())->execute($token);
    98→
    99→        if (is_wp_error($tokenValidation)) {
   100→            return $tokenValidation;
   101→        }
   102→
   103→        return (object) [
   104→            'code' => 'jwt_auth_valid_token',
   105→            'data' => ['status' => 200],
   106→        ];
   107→    }
   108→
   109→    public function refreshToken(WP_REST_Request $request): array|WP_Error
   110→    {
   111→        $refreshToken = sanitize_text_field($request->get_param('refresh_token'));
   112→
   113→        return (new TokenRefreshRequestHandler())->execute($refreshToken);
   114→    }
