   121→    public function addCorsSupport(): void
   122→    {
   123→        $enableCors = apply_filters(
   124→            'jwt_auth_token_enable_cors',
   125→            defined('JWT_AUTH_CORS_ENABLE')
   126→            ? constant('JWT_AUTH_CORS_ENABLE')
   127→            : $this->settingsService->getSetting('token_settings', 'enable_cors')
   128→        );
   129→
   130→        if (!$enableCors) {
   131→            return;
   132→        }
   133→
   134→        $headers = apply_filters(
   135→            'jwt_auth_cors_allow_headers',
   136→            'Access-Control-Allow-Headers, Content-Type, Authorization'
   137→        );
   138→
   139→        header(sprintf('Access-Control-Allow-Headers: %s', $headers));
   140→    }
   141→
   142→    private function extractToken(WP_REST_Request $request): string|WP_Error
   143→    {
   144→        $auth_header = $request->get_header('Authorization');
   145→
   146→        if (!$auth_header) {
   147→            return new WP_Error(
   148→                'jwt_auth_no_auth_header',
   149→                'Authorization header not found.',
   150→                ['status' => 403]
   151→            );
   152→        }
   153→
   154→        $token = str_replace('Bearer ', '', $auth_header);
   155→
   156→        if (empty(trim($token))) {
   157→            return new WP_Error(
   158→                'jwt_auth_bad_auth_header',
   159→                'Authorization header malformed.',
   160→                ['status' => 403]
   161→            );
   162→        }
   163→
   164→        return $token;
   165→    }
   166→
   167→    /**
   168→     * Handle rate limit errors and return proper REST response
   169→     * @param mixed $result
   170→     * @param mixed $server
   171→     * @param mixed $request
   172→     */
   173→    public function handleRateLimitError($result, $server, $request): mixed
   174→    {
   175→        if ($this->authenticateAction->hasError()) {
   176→            $error = $this->authenticateAction->getError();
   177→            if ($error && $error->get_error_code() === 'jwt_auth_rate_limited') {
   178→                $data = $error->get_error_data();
   179→                $response = new WP_REST_Response([
   180→                    'code' => 'jwt_auth_rate_limited',
   181→                    'message' => $error->get_error_message(),
   182→                    'data' => ['status' => 429],
   183→                ], 429);
   184→
   185→                if (isset($data['retry_after'])) {
   186→                    $response->header('Retry-After', $data['retry_after']);
   187→                }
   188→
   189→                return $response;
   190→            }
   191→        }
   192→
   193→        return $result;
   194→    }
   195→}
