import { Page, APIRequestContext } from '@playwright/test';

/**
 * Test data management for JWT Auth Pro E2E tests
 */
export class TestDataManager {
  constructor(
    private page: Page,
    private request: APIRequestContext
  ) {}

  /**
   * Create test JWT tokens via WordPress API
   */
  async createTestTokens(count: number = 5) {
    const tokens = [];

    for (let i = 0; i < count; i++) {
      try {
        const response = await this.request.post('/wp-json/jwt-auth/v1/token', {
          data: {
            username: 'admin',
            password: 'password'
          }
        });

        if (response.ok()) {
          const tokenData = await response.json();
          tokens.push(tokenData);
        }
      } catch (error) {
        console.warn(`Failed to create test token ${i + 1}:`, error);
      }

      // Small delay to avoid rate limiting
      await this.page.waitForTimeout(100);
    }

    return tokens;
  }

  /**
   * Create test user for token generation
   */
  async createTestUser(username: string = 'testuser', role: string = 'subscriber') {
    try {
      const response = await this.request.post('/wp-json/wp/v2/users', {
        data: {
          username,
          email: `${username}@test.local`,
          password: 'testpass123',
          roles: [role]
        },
        headers: {
          'X-WP-Nonce': await this.getWordPressNonce()
        }
      });

      if (response.ok()) {
        return await response.json();
      }
    } catch (error) {
      console.warn('Failed to create test user:', error);
    }

    return null;
  }

  /**
   * Get WordPress nonce for authenticated requests
   */
  async getWordPressNonce(): Promise<string> {
    // Navigate to admin if not already there
    if (!this.page.url().includes('/wp-admin')) {
      await this.page.goto('/wp-admin');
    }

    // Extract nonce from page
    const nonce = await this.page.evaluate(() => {
      // Try multiple possible nonce sources
      return (window as any).wpApiSettings?.nonce || 
             (window as any).jwtAuthPro?.nonce ||
             (document.querySelector('meta[name="jwt-auth-nonce"]') as HTMLMetaElement)?.content ||
             '';
    });

    if (!nonce) {
      throw new Error('Could not retrieve WordPress nonce');
    }

    return nonce;
  }

  /**
   * Seed analytics data by creating and using tokens
   */
  async seedAnalyticsData() {
    const tokens = await this.createTestTokens(10);

    // Use some tokens to generate analytics data
    for (const token of tokens.slice(0, 5)) {
      if (token.token) {
        try {
          // Validate token to create usage analytics
          await this.request.post('/wp-json/jwt-auth/v1/token/validate', {
            headers: {
              'Authorization': `Bearer ${token.token}`
            }
          });

          await this.page.waitForTimeout(50);
        } catch (error) {
          // Expected to fail for some tokens
        }
      }
    }

    return tokens;
  }

  /**
   * Clean up test data
   */
  async cleanup() {
    try {
      // Try to call cleanup endpoint if it exists
      await this.request.post('/wp-json/jwt-auth-pro/v1/test/cleanup', {
        headers: {
          'X-WP-Nonce': await this.getWordPressNonce()
        }
      });
    } catch (error) {
      // Cleanup is best-effort
      console.warn('Test cleanup warning:', error);
    }
  }

  /**
   * Wait for data to be processed (useful after creating test data)
   */
  async waitForDataProcessing(ms: number = 1000) {
    await this.page.waitForTimeout(ms);
  }
}

