export class DatabaseHelper {
  constructor(
    private page: Page,
    private request: APIRequestContext
  ) {}

  /**
   * Execute a WordPress database query via REST API (if custom endpoint exists)
   */
  async executeQuery(query: string) {
    try {
      const response = await this.request.post('/wp-json/jwt-auth-pro/v1/test/db-query', {
        data: { query },
        headers: {
          'X-WP-Nonce': await this.getWordPressNonce()
        }
      });

      if (response.ok()) {
        return await response.json();
      }
    } catch (error) {
      console.warn('Database query failed:', error);
    }

    return null;
  }

  /**
   * Get token count from database
   */
  async getTokenCount(status?: 'active' | 'expired' | 'revoked') {
    const statusFilter = status ? `WHERE status = '${status}'` : '';
    const query = `SELECT COUNT(*) as count FROM jwt_auth_tokens ${statusFilter}`;

    const result = await this.executeQuery(query);
    return result?.data?.[0]?.count || 0;
  }

  /**
   * Get analytics data count
   */
  async getAnalyticsCount() {
    const query = 'SELECT COUNT(*) as count FROM jwt_auth_analytics';
    const result = await this.executeQuery(query);
    return result?.data?.[0]?.count || 0;
  }

  /**
   * Clean up test data directly from database
   */
  async cleanupTestData() {
    const queries = [
      'DELETE FROM jwt_auth_tokens WHERE user_id IN (SELECT ID FROM wp_users WHERE user_login LIKE "test%")',
      'DELETE FROM jwt_auth_analytics WHERE created_at < DATE_SUB(NOW(), INTERVAL 1 HOUR)',
      'DELETE FROM wp_users WHERE user_login LIKE "test%"'
    ];

    for (const query of queries) {
      await this.executeQuery(query);
    }
  }

  /**
   * Reset database to clean state
   */
  async resetDatabase() {
    const queries = [
      'TRUNCATE TABLE jwt_auth_tokens',
      'TRUNCATE TABLE jwt_auth_analytics',
      'TRUNCATE TABLE jwt_auth_analytics_summary'
    ];

    for (const query of queries) {
      await this.executeQuery(query);
    }
  }

  /**
   * Get WordPress nonce
   */
  private async getWordPressNonce(): Promise<string> {
    if (!this.page.url().includes('/wp-admin')) {
      await this.page.goto('/wp-admin');
    }

    const nonce = await this.page.evaluate(() => {
      return (window as any).wpApiSettings?.nonce || 
             (window as any).jwtAuthPro?.nonce ||
             '';
    });

    if (!nonce) {
      throw new Error('Could not retrieve WordPress nonce');
    }

    return nonce;
  }
}

