import { expect, Locator, Page } from '@playwright/test';

/**
 * Custom assertions for JWT Auth Pro E2E tests
 */
export class JWTAssertions {
  constructor(private page: Page) {}

  /**
   * Assert that a data table has loaded with content
   */
  async expectTableToHaveData(tableSelector: string, minRows: number = 1) {
    const table = this.page.locator(tableSelector);
    await expect(table).toBeVisible();

    // Wait for table to load
    await this.page.waitForTimeout(1000);

    const rows = table.locator('tbody tr, tr');
    const rowCount = await rows.count();

    expect(rowCount).toBeGreaterThanOrEqual(minRows);
  }

  /**
   * Assert that analytics charts are rendered
   */
  async expectChartsToBeRendered() {
    // Look for Recharts components
    const charts = this.page.locator('.recharts-wrapper, .recharts-surface, [data-testid*="chart"]');
    await expect(charts.first()).toBeVisible({ timeout: 10000 });
  }

  /**
   * Assert that navigation works
   */
  async expectNavigationToWork(navSelector: string, expectedTabs: string[]) {
    const nav = this.page.locator(navSelector);
    await expect(nav).toBeVisible();

    for (const tab of expectedTabs) {
      const tabElement = nav.locator(`text=${tab}, [data-testid="${tab.toLowerCase()}-tab"]`);
      if (await tabElement.count() > 0) {
        await expect(tabElement).toBeVisible();
      }
    }
  }

  /**
   * Assert that form submission works
   */
  async expectFormSubmissionToWork(
    formSelector: string, 
    submitSelector: string,
    successSelector: string
  ) {
    const form = this.page.locator(formSelector);
    await expect(form).toBeVisible();

    const submitButton = this.page.locator(submitSelector);
    await submitButton.click();

    // Wait for success indicator
    await expect(this.page.locator(successSelector)).toBeVisible({ timeout: 10000 });
  }

  /**
   * Assert that filters work correctly
   */
  async expectFiltersToWork(
    filterSelector: string,
    filterValue: string,
    resultSelector: string
  ) {
    // Apply filter
    const filter = this.page.locator(filterSelector);
    await filter.selectOption(filterValue);

    // Wait for results to update
    await this.page.waitForTimeout(1000);

    // Verify results changed
    const results = this.page.locator(resultSelector);
    await expect(results).toBeVisible();
  }

  /**
   * Assert that error handling works
   */
  async expectErrorHandling(
    triggerAction: () => Promise<void>,
    errorSelector: string
  ) {
    await triggerAction();

    // Wait for error message
    await expect(this.page.locator(errorSelector)).toBeVisible({ timeout: 5000 });
  }

  /**
   * Assert loading states work
   */
  async expectLoadingStates(
    triggerSelector: string,
    loadingSelector: string,
    contentSelector: string
  ) {
    // Trigger action that causes loading
    await this.page.click(triggerSelector);

    // Verify loading state appears
    await expect(this.page.locator(loadingSelector)).toBeVisible();

    // Verify content loads
    await expect(this.page.locator(contentSelector)).toBeVisible({ timeout: 10000 });

    // Verify loading state disappears
    await expect(this.page.locator(loadingSelector)).not.toBeVisible();
  }

  /**
   * Assert that data persists after page reload
   */
  async expectDataToPersist(
    dataSelector: string,
    expectedContent: string | RegExp
  ) {
    // Get initial content
    const initialContent = await this.page.locator(dataSelector).textContent();

    // Reload page
    await this.page.reload();

    // Wait for page to load
    await this.page.waitForLoadState('networkidle');

    // Verify content is still there
    if (typeof expectedContent === 'string') {
      await expect(this.page.locator(dataSelector)).toContainText(expectedContent);
    } else {
      await expect(this.page.locator(dataSelector)).toContainText(expectedContent);
    }
  }

  /**
   * Assert pagination works
   */
  async expectPaginationToWork(
    nextButtonSelector: string,
    pageContentSelector: string
  ) {
    // Get initial content
    const initialContent = await this.page.locator(pageContentSelector).textContent();

    // Click next page
    await this.page.click(nextButtonSelector);
    await this.page.waitForTimeout(1000);

    // Verify content changed
    const newContent = await this.page.locator(pageContentSelector).textContent();
    expect(newContent).not.toBe(initialContent);
  }
}

