import { test, expect } from '../fixtures/admin-user';
import { WordPressHelper } from '../helpers/wordpress';
import { TestDataManager } from '../fixtures/test-data';
import { JWTAssertions } from '../helpers/assertions';

test.describe('JWT Auth Pro - Dashboard Analytics', () => {
  let helper: WordPressHelper;
  let testData: TestDataManager;
  let assertions: JWTAssertions;

  test.beforeEach(async ({ jwtAdminPage }) => {
    helper = new WordPressHelper(jwtAdminPage, jwtAdminPage.request);
    testData = new TestDataManager(jwtAdminPage, jwtAdminPage.request);
    assertions = new JWTAssertions(jwtAdminPage);
  });

  test('Dashboard loads with React app', async ({ jwtAdminPage }) => {
    // Check if React app is present
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await expect(appContainer).toBeVisible();

    // Wait for app to initialize
    await jwtAdminPage.waitForTimeout(2000);

    // Look for dashboard content
    const hasDashboard = await jwtAdminPage.locator('h1, h2, [data-testid*="dashboard"]').count() > 0;
    expect(hasDashboard).toBeTruthy();
  });

  test('Analytics metrics display', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    // Create some test data first
    await testData.seedAnalyticsData();
    await testData.waitForDataProcessing();

    // Reload to see fresh data
    await jwtAdminPage.reload();
    await helper.waitForReactApp();

    // Look for metric displays (numbers, counters, etc.)
    const metrics = jwtAdminPage.locator(
      '[data-testid*="metric"], [data-testid*="count"], .metric, .counter, .stat'
    );

    if (await metrics.count() > 0) {
      await expect(metrics.first()).toBeVisible();
    } else {
      // If no specific metric elements, look for any numeric displays
      const numbers = jwtAdminPage.locator('text=/\\d+/');
      expect(await numbers.count()).toBeGreaterThan(0);
    }
  });

  test('Charts render when data is available', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    // Create test data
    await testData.seedAnalyticsData();
    await testData.waitForDataProcessing();

    // Reload to see fresh data
    await jwtAdminPage.reload();
    await helper.waitForReactApp();

    // Look for chart components (Recharts, Chart.js, D3, etc.)
    const chartSelectors = [
      '.recharts-wrapper',
      '.recharts-surface',
      'canvas',
      'svg',
      '[data-testid*="chart"]',
      '.chart-container'
    ];

    let hasCharts = false;
    for (const selector of chartSelectors) {
      if (await jwtAdminPage.locator(selector).count() > 0) {
        hasCharts = true;
        await expect(jwtAdminPage.locator(selector).first()).toBeVisible();
        break;
      }
    }

    // If no charts found, that's also acceptable (plugin might not have charts yet)
    if (!hasCharts) {
      console.log('No charts found - this is acceptable for current implementation');
    }
  });

  test('Navigation works between dashboard sections', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();

    // Look for navigation elements
    const navSelectors = [
      'nav',
      '.nav',
      '.navigation',
      '.tabs',
      '[role="navigation"]',
      '[data-testid*="nav"]'
    ];

    let navigation = null;
    for (const selector of navSelectors) {
      const nav = jwtAdminPage.locator(selector);
      if (await nav.count() > 0) {
        navigation = nav;
        break;
      }
    }

    if (navigation) {
      await expect(navigation).toBeVisible();

      // Try to find clickable navigation items
      const navItems = navigation.locator('a, button, [role="tab"]');
      const itemCount = await navItems.count();

      if (itemCount > 1) {
        // Click on different nav items to test navigation
        for (let i = 0; i < Math.min(itemCount, 3); i++) {
          await navItems.nth(i).click();
          await jwtAdminPage.waitForTimeout(500);

          // Verify we didn't get an error
          const hasError = await jwtAdminPage.locator('text=/error/i').count() > 0;
          expect(hasError).toBeFalsy();
        }
      }
    } else {
      console.log('No navigation found - single page dashboard');
    }
  });

  test('Time period filters work (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();

    // Look for time period filters
    const filterSelectors = [
      'select[data-testid*="period"]',
      'select[data-testid*="time"]',
      'select[data-testid*="filter"]',
      'button:has-text("7d")',
      'button:has-text("30d")',
      'button:has-text("90d")'
    ];

    let filter = null;
    for (const selector of filterSelectors) {
      const filterElement = jwtAdminPage.locator(selector);
      if (await filterElement.count() > 0) {
        filter = filterElement;
        break;
      }
    }

    if (filter) {
      // Get initial content
      const initialContent = await jwtAdminPage.locator('body').textContent();

      // Try to interact with filter
      if (await filter.getAttribute('tag') === 'select') {
        const options = filter.locator('option');
        if (await options.count() > 1) {
          await filter.selectOption({ index: 1 });
        }
      } else {
        await filter.click();
      }

      await jwtAdminPage.waitForTimeout(1000);

      // Verify something changed (could be content, loading state, etc.)
      const newContent = await jwtAdminPage.locator('body').textContent();
      // Content might be the same if no data, so this is a soft check
    } else {
      console.log('No time period filters found');
    }
  });

  test.afterEach(async ({ jwtAdminPage }) => {
    // Clean up test data
    if (testData) {
      await testData.cleanup();
    }
  });
});

