import { test, expect } from '../fixtures/admin-user';
import { WordPressHelper } from '../helpers/wordpress';
import { TestDataManager } from '../fixtures/test-data';
import { JWTAssertions } from '../helpers/assertions';

test.describe('JWT Auth Pro - Token Management', () => {
  let helper: WordPressHelper;
  let testData: TestDataManager;
  let assertions: JWTAssertions;

  test.beforeEach(async ({ jwtAdminPage }) => {
    helper = new WordPressHelper(jwtAdminPage, jwtAdminPage.request);
    testData = new TestDataManager(jwtAdminPage, jwtAdminPage.request);
    assertions = new JWTAssertions(jwtAdminPage);
  });

  test('Token list page loads', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();

    // Try to navigate to tokens page
    const tokenNavSelectors = [
      '[data-testid="tokens-tab"]',
      'nav a:has-text("Token")',
      'button:has-text("Token")',
      'a:has-text("Token")'
    ];

    let tokensNav = null;
    for (const selector of tokenNavSelectors) {
      const nav = jwtAdminPage.locator(selector);
      if (await nav.count() > 0) {
        tokensNav = nav;
        break;
      }
    }

    if (tokensNav) {
      await tokensNav.click();
      await jwtAdminPage.waitForTimeout(1000);
    }

    // Look for token list or table
    const tokenListSelectors = [
      'table',
      '.token-list',
      '[data-testid*="token"]',
      '.data-table'
    ];

    let hasList = false;
    for (const selector of tokenListSelectors) {
      if (await jwtAdminPage.locator(selector).count() > 0) {
        hasList = true;
        await expect(jwtAdminPage.locator(selector)).toBeVisible();
        break;
      }
    }

    if (!hasList) {
      // No token list found - might not be implemented yet
      console.log('Token list not found - feature may not be implemented');
    }
  });

  test('Token table displays data', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    // Create test tokens first
    await testData.createTestTokens(3);
    await testData.waitForDataProcessing();

    await helper.waitForReactApp();

    // Navigate to tokens if needed
    await helper.navigateToTab('tokens');

    // Look for table with token data
    const table = jwtAdminPage.locator('table');

    if (await table.count() > 0) {
      await expect(table).toBeVisible();

      // Look for table rows
      const rows = table.locator('tbody tr, tr');
      const rowCount = await rows.count();

      if (rowCount > 0) {
        // Verify we have some token data
        expect(rowCount).toBeGreaterThan(0);

        // Check for common token table columns
        const commonColumns = ['user', 'status', 'created', 'expires', 'last used'];
        for (const column of commonColumns) {
          const hasColumn = await jwtAdminPage.locator(`th:has-text("${column}"), td:has-text("${column}")`).count() > 0;
          // Don't assert - just log what we find
          if (hasColumn) {
            console.log(`Found column: ${column}`);
          }
        }
      }
    } else {
      console.log('No token table found');
    }
  });

  test('Token status filtering works (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await testData.createTestTokens(5);
    await testData.waitForDataProcessing();

    await helper.waitForReactApp();
    await helper.navigateToTab('tokens');

    // Look for status filter
    const statusFilterSelectors = [
      'select[data-testid*="status"]',
      'select[data-testid*="filter"]',
      'select:has(option:has-text("Active"))',
      'select:has(option:has-text("Expired"))'
    ];

    let statusFilter = null;
    for (const selector of statusFilterSelectors) {
      const filter = jwtAdminPage.locator(selector);
      if (await filter.count() > 0) {
        statusFilter = filter;
        break;
      }
    }

    if (statusFilter) {
      // Get initial row count
      const table = jwtAdminPage.locator('table');
      const initialRows = await table.locator('tbody tr, tr').count();

      // Try filtering by "Active" if option exists
      const activeOption = statusFilter.locator('option:has-text("Active")');
      if (await activeOption.count() > 0) {
        await statusFilter.selectOption('active');
        await jwtAdminPage.waitForTimeout(1000);

        // Verify filtering occurred (rows might change)
        const filteredRows = await table.locator('tbody tr, tr').count();
        // Don't assert specific numbers, just that we can filter
        console.log(`Filtered from ${initialRows} to ${filteredRows} rows`);
      }
    } else {
      console.log('No status filter found');
    }
  });

  test('Search functionality works (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await testData.createTestTokens(3);
    await testData.waitForDataProcessing();

    await helper.waitForReactApp();
    await helper.navigateToTab('tokens');

    // Look for search input
    const searchSelectors = [
      'input[data-testid*="search"]',
      'input[placeholder*="search"]',
      'input[type="search"]',
      'input[name*="search"]'
    ];

    let searchInput = null;
    for (const selector of searchSelectors) {
      const input = jwtAdminPage.locator(selector);
      if (await input.count() > 0) {
        searchInput = input;
        break;
      }
    }

    if (searchInput) {
      // Try searching for "admin" user
      await searchInput.fill('admin');
      await jwtAdminPage.keyboard.press('Enter');
      await jwtAdminPage.waitForTimeout(1000);

      // Verify search was applied (look for results or loading)
      const hasResults = await jwtAdminPage.locator('table tr, .search-results').count() > 0;
      expect(hasResults).toBeTruthy();
    } else {
      console.log('No search functionality found');
    }
  });

  test('Token actions work (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await testData.createTestTokens(2);
    await testData.waitForDataProcessing();

    await helper.waitForReactApp();
    await helper.navigateToTab('tokens');

    // Look for action buttons or menus
    const actionSelectors = [
      'button:has-text("Revoke")',
      'button:has-text("Delete")',
      'button:has-text("View")',
      '[data-testid*="action"]',
      '.action-menu',
      '.dropdown'
    ];

    let actionElement = null;
    for (const selector of actionSelectors) {
      const element = jwtAdminPage.locator(selector);
      if (await element.count() > 0) {
        actionElement = element;
        break;
      }
    }

    if (actionElement) {
      // Try clicking the first action (should be safe)
      await actionElement.first().click();
      await jwtAdminPage.waitForTimeout(500);

      // Look for confirmation dialog or success message
      const hasConfirmation = await jwtAdminPage.locator(
        '.modal, .dialog, .confirm, .toast, .alert'
      ).count() > 0;

      if (hasConfirmation) {
        console.log('Action triggered confirmation/dialog');

        // If there's a cancel button, click it to avoid actual changes
        const cancelButton = jwtAdminPage.locator('button:has-text("Cancel")');
        if (await cancelButton.count() > 0) {
          await cancelButton.click();
        }
      }
    } else {
      console.log('No token actions found');
    }
  });

  test('Pagination works (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    // Create many tokens to trigger pagination
    await testData.createTestTokens(15);
    await testData.waitForDataProcessing();

    await helper.waitForReactApp();
    await helper.navigateToTab('tokens');

    // Look for pagination controls
    const paginationSelectors = [
      '.pagination',
      '[data-testid*="pagination"]',
      'button:has-text("Next")',
      'button:has-text("Previous")',
      '.page-numbers'
    ];

    let pagination = null;
    for (const selector of paginationSelectors) {
      const element = jwtAdminPage.locator(selector);
      if (await element.count() > 0) {
        pagination = element;
        break;
      }
    }

    if (pagination) {
      // Try to find next page button
      const nextButton = jwtAdminPage.locator('button:has-text("Next"), .next');
      if (await nextButton.count() > 0 && await nextButton.isEnabled()) {
        const initialContent = await jwtAdminPage.locator('table').textContent();

        await nextButton.click();
        await jwtAdminPage.waitForTimeout(1000);

        const newContent = await jwtAdminPage.locator('table').textContent();
        expect(newContent).not.toBe(initialContent);
      }
    } else {
      console.log('No pagination found');
    }
  });

  test.afterEach(async ({ jwtAdminPage }) => {
    // Clean up test data
    if (testData) {
      await testData.cleanup();
    }
  });
});

