      if (await saveButton.count() > 0) {
        await saveButton.click();
        await jwtAdminPage.waitForTimeout(1000);

        // Reload page
        await jwtAdminPage.reload();
        await helper.waitForReactApp();
        await helper.navigateToTab('settings');

        // Check if value persisted
