        if (await saveButton.count() > 0) {
          await saveButton.click();
        }
      }
    } else {
      console.log('No testable settings fields found');
    }
  });

  test('Invalid settings show validation errors', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
