      // Try to save
      const saveButton = jwtAdminPage.locator('button:has-text("Save")');
      if (await saveButton.count() > 0) {
        await saveButton.click();
        await jwtAdminPage.waitForTimeout(1000);

        // Look for error messages
        const errorSelectors = [
          '.error',
          '.invalid',
          '.validation-error',
          '[data-testid*="error"]',
          '.field-error'
