import { Page } from '@playwright/test';

/**
 * Plugin detection helper to determine what features are actually available
 */
export class PluginDetector {
  constructor(private page: Page) {}

  /**
   * Check if JWT Auth Pro plugin is installed and activated
   */
  async isPluginActive(): Promise<boolean> {
    // Check for the React app container
    const hasApp = await this.page.locator('#jwt-auth-pro-app').count() > 0;

    if (hasApp) {
      // Check if the app has actual content (not just empty div)
      const hasContent = await this.page.locator('#jwt-auth-pro-app *').count() > 0;
      return hasContent;
    }

    return false;
  }

  /**
   * Check what admin pages/tabs are actually available
   */
  async getAvailableTabs(): Promise<string[]> {
    const tabs: string[] = [];

    // Common tab selectors
    const tabSelectors = [
      'nav a',
      '[role="tab"]',
      '.nav-tab',
      '[data-testid*="tab"]',
      'button[role="tab"]'
    ];

    for (const selector of tabSelectors) {
      const elements = this.page.locator(selector);
      const count = await elements.count();

      for (let i = 0; i < count; i++) {
        const text = await elements.nth(i).textContent();
        if (text && text.trim()) {
          tabs.push(text.trim().toLowerCase());
        }
      }
    }

    return [...new Set(tabs)]; // Remove duplicates
  }

  /**
   * Check what features are available on the current page
   */
  async detectAvailableFeatures(): Promise<{
    hasTable: boolean;
    hasForm: boolean;
    hasCharts: boolean;
    hasFilters: boolean;
    hasSearch: boolean;
    hasPagination: boolean;
    hasActions: boolean;
  }> {
    const features = {
      hasTable: await this.page.locator('table').count() > 0,
      hasForm: await this.page.locator('form').count() > 0,
      hasCharts: await this.page.locator('.recharts-wrapper, canvas, svg[class*="chart"]').count() > 0,
      hasFilters: await this.page.locator('select, .filter, [data-testid*="filter"]').count() > 0,
      hasSearch: await this.page.locator('input[type="search"], input[placeholder*="search"]').count() > 0,
      hasPagination: await this.page.locator('.pagination, [data-testid*="pagination"]').count() > 0,
      hasActions: await this.page.locator('button:has-text("Delete"), button:has-text("Edit"), button:has-text("View")').count() > 0
    };

    return features;
  }

  /**
   * Get current page information
   */
  async getCurrentPageInfo(): Promise<{
    url: string;
    title: string;
    hasReactApp: boolean;
    elementCount: number;
  }> {
    return {
      url: this.page.url(),
      title: await this.page.title(),
      hasReactApp: await this.page.locator('#jwt-auth-pro-app').count() > 0,
      elementCount: await this.page.locator('*').count()
    };
  }

  /**
   * Take a screenshot for debugging
   */
  async debugScreenshot(name: string = 'debug') {
    await this.page.screenshot({ 
      path: `e2e-debug-${name}-${Date.now()}.png`,
      fullPage: true 
    });
  }

  /**
   * Get page content summary for debugging
   */
  async getPageSummary(): Promise<string> {
    const info = await this.getCurrentPageInfo();
    const features = await this.detectAvailableFeatures();
    const tabs = await this.getAvailableTabs();

    return `
Page Info:
- URL: ${info.url}
- Title: ${info.title}  
- Has React App: ${info.hasReactApp}
- Element Count: ${info.elementCount}

Available Features:
- Tables: ${features.hasTable}
- Forms: ${features.hasForm}
- Charts: ${features.hasCharts}
- Filters: ${features.hasFilters}
- Search: ${features.hasSearch}
- Pagination: ${features.hasPagination}
- Actions: ${features.hasActions}

Available Tabs: ${tabs.join(', ') || 'None found'}
`;
  }
}

