import { PluginDetector } from '../helpers/plugin-detector';

test.describe('JWT Auth Pro - Plugin Discovery', () => {
  test('Discover what features are actually available', async ({ jwtAdminPage }) => {
    const detector = new PluginDetector(jwtAdminPage);

    // Get basic page info
    const pageInfo = await detector.getCurrentPageInfo();
    console.log('\n=== PAGE INFO ===');
    console.log(`URL: ${pageInfo.url}`);
    console.log(`Title: ${pageInfo.title}`);
    console.log(`Has React App: ${pageInfo.hasReactApp}`);
    console.log(`Element Count: ${pageInfo.elementCount}`);

    // Check if plugin is actually active
    const isActive = await detector.isPluginActive();
    console.log(`\nPlugin Active: ${isActive}`);

    if (!isActive) {
      // Take a screenshot to see what's actually on the page
      await detector.debugScreenshot('plugin-not-active');

      // Check if we can find any JWT-related content
      const jwtContent = await jwtAdminPage.locator('text=/jwt/i, text=/token/i, text=/auth/i').count();
      console.log(`JWT-related content found: ${jwtContent} elements`);

      // Check page source for clues
      const pageContent = await jwtAdminPage.content();
      const hasPluginClass = pageContent.includes('jwt-auth-pro');
      const hasWordPress = pageContent.includes('wp-admin');
      console.log(`Has plugin CSS class: ${hasPluginClass}`);
      console.log(`Is WordPress admin: ${hasWordPress}`);
    }

    // Discover available features
    const features = await detector.detectAvailableFeatures();
    console.log('\n=== AVAILABLE FEATURES ===');
    Object.entries(features).forEach(([key, value]) => {
      console.log(`${key}: ${value}`);
    });

    // Try to find available tabs/navigation
    const tabs = await detector.getAvailableTabs();
    console.log(`\n=== AVAILABLE TABS ===`);
    console.log(tabs.length > 0 ? tabs.join(', ') : 'No tabs found');

    // Get full page summary
    const summary = await detector.getPageSummary();
    console.log('\n=== FULL SUMMARY ===');
    console.log(summary);

    // The test always passes - this is just for discovery
    expect(true).toBeTruthy();
  });

  test('Check WordPress admin integration', async ({ adminPage }) => {
    console.log('\n=== WORDPRESS ADMIN CHECK ===');

    // Navigate to plugins page to see if JWT Auth Pro is listed
    await adminPage.goto('/wp-admin/plugins.php');

