    console.log(`JWT-related plugins found: ${jwtPlugin}`);

    if (jwtPlugin > 0) {
      const pluginText = await adminPage.locator('tr[data-slug*="jwt"], tr:has-text("JWT"), tr:has-text("Auth")').first().textContent();
      console.log(`Plugin info: ${pluginText}`);
    }

    // Check if there's a JWT Auth Pro menu item
    await adminPage.goto('/wp-admin');
    const menuItems = await adminPage.locator('#adminmenu a').count();
    console.log(`Total admin menu items: ${menuItems}`);

    const jwtMenuItem = await adminPage.locator('#adminmenu a:has-text("JWT"), #adminmenu a:has-text("Auth")').count();
    console.log(`JWT-related menu items: ${jwtMenuItem}`);

    // Try to access the plugin page directly and see what happens
    await adminPage.goto('/wp-admin/admin.php?page=jwt-auth-pro');
    const pageTitle = await adminPage.title();
    const currentUrl = adminPage.url();

    console.log(`Direct access - Title: ${pageTitle}`);
    console.log(`Direct access - URL: ${currentUrl}`);

    // Check for error messages or plugin not found
    const errorMessages = await adminPage.locator(
      'text="not found", text="invalid", text="error", .notice-error'
    ).count();
    console.log(`Error messages found: ${errorMessages}`);

    if (errorMessages > 0) {
      const errorText = await adminPage.locator(
        'text="not found", text="invalid", text="error", .notice-error'
      ).first().textContent();
      console.log(`Error message: ${errorText}`);
    }

    expect(true).toBeTruthy();
  });

  test('Examine actual page content', async ({ jwtAdminPage }) => {
