    // Get the actual HTML content
    const content = await jwtAdminPage.content();

    // Look for specific indicators
    const indicators = {
      'React App Container': content.includes('jwt-auth-pro-app'),
      'WordPress Admin': content.includes('wp-admin'),
      'Plugin Styles': content.includes('jwt-auth') || content.includes('jwt_auth'),
      'Admin Bar': content.includes('wpadminbar'),
      'Plugin Scripts': content.includes('jwt') && content.includes('.js'),
      'Error Message': content.includes('not found') || content.includes('invalid'),
    };

    Object.entries(indicators).forEach(([key, value]) => {
      console.log(`${key}: ${value}`);
    });

    // Extract and show any text content from the main content area
    const mainContent = await jwtAdminPage.locator('#wpbody-content, .wrap, main').first().textContent();
    console.log(`\nMain content preview: ${mainContent?.substring(0, 200)}...`);

    // Take a screenshot for visual inspection
    await jwtAdminPage.screenshot({ 
      path: `e2e-content-analysis-${Date.now()}.png`,
      fullPage: true 
    });

    expect(true).toBeTruthy();
  });
});

