import { createRoot } from 'react-dom/client';
import { createContext, useContext, useState } from 'react';
import { HashRouter } from 'react-router-dom';
import Index from './pages/Index';
import './styles/index.css';

interface TabContextType {
  currentTab: string;
  setCurrentTab: (tab: string) => void;
}

export const TabContext = createContext<TabContextType | undefined>(undefined);

export function useTab() {
  const context = useContext(TabContext);
  if (!context) console.log('useTab must be used within TabProvider');
  return context;
}

function App() {
  const [currentTab, setCurrentTab] = useState(() => {
    return new URLSearchParams(window.location.search).get('tab') || 'home';
  });

  return (
    <HashRouter>
      <TabContext.Provider value={{ currentTab, setCurrentTab }}>
        <Index />
      </TabContext.Provider>
    </HashRouter>
  );
}

// Initialize the app
(() => {
  const container = document.getElementById('jwt-auth-pro-app');
  if (container && !container.hasAttribute('data-jwt-root-initialized')) {
    const root = createRoot(container);
    root.render(<App />);
    container.setAttribute('data-jwt-root-initialized', 'true');
  }
})();

export default App;

