import { Token, TokenListParams, BulkActionParams } from '../types/token';
import {
  ApiResponse,
  TokenListResponse,
  Settings,
  SettingsOptions,
  SystemInfo,
  TokenDetails,
  TokenRelatedResponse,
  TokenUsageHistoryResponse
} from '../types/api';
import { DashboardSummary, HistoricalData } from '../types/analytics';
import { TokenStats } from '../types/metrics';


const API_NAMESPACE = '/wp-json/jwt-auth/v1';
const ADMIN_BASE = `${API_NAMESPACE}/admin`;
const ANALYTICS_BASE = `${API_NAMESPACE}/analytics`;

// Helper function to get common headers
const getHeaders = () => {
  const headers: HeadersInit = {
    'Content-Type': 'application/json',
  };

  if (window.wpApiSettings?.nonce) {
    headers['X-WP-Nonce'] = window.wpApiSettings.nonce;
  }

  return headers;
};

// Generic fetch wrapper with error handling
async function fetchAPI<T>(path: string, options: RequestInit = {}): Promise<T> {
  const response = await fetch(path, {
    ...options,
    credentials: 'same-origin',
    headers: getHeaders(),
  });

  if (!response.ok) {
    throw new Error(`HTTP error! status: ${response.status}`);
  }

  const data = await response.json();

  // For delete operations, success message is enough
  if (options.method === 'DELETE' && data.success) {
    return data as T;
  }

  // For other operations, check for data property
  if (!data.success || (!data.data && options.method !== 'DELETE')) {
    handleApiError(data, 'Invalid API response format');
    throw new Error(data.message || 'Invalid API response format');
  }

  return options.method === 'DELETE' ? data : data.data as T;
}

const handleApiError = (response: ApiResponse<unknown>, defaultMessage: string) => {
  throw new Error(response.message ?? defaultMessage);
};

export const analyticsAPI = {
  getDashboardSummary: async () => {
    try {
      const data = await fetchAPI<DashboardSummary>(`${ANALYTICS_BASE}/summary`, {
        method: 'GET',
      });
      return data;
    } catch (error) {
      console.error('Error fetching dashboard summary:', error);
      throw error;
    }
  },

  getAllTimeStats: async () => {
    try {
      const data = await fetchAPI<TokenStats>(`${ANALYTICS_BASE}/all-time`, {
        method: 'GET',
      });
      return data;
    } catch (error) {
      console.error('Error fetching all-time stats:', error);
      throw error;
    }
  },

  getHistoricalData: async (period: '24h' | '7d' | '30d' | '90d' = '7d') => {
    try {
      const data = await fetchAPI<HistoricalData[]>(`${ANALYTICS_BASE}/historical?period=${period}`, {
        method: 'GET',
      });
      return data;
    } catch (error) {
      console.error('Error fetching historical data:', error);
      throw error;
    }
  },
};

export const tokenAPI = {
  list: async (params: TokenListParams) => {
    try {
      const queryParams = new URLSearchParams();
      if (params.page) queryParams.append('page', params.page.toString());
      if (params.per_page) queryParams.append('per_page', params.per_page.toString());
      if (params.status) queryParams.append('status', params.status);
      if (params.username) queryParams.append('username', params.username);

      // Format dates to ISO string and send only the date part
      if (params.created_at) {
        queryParams.append('created_at', params.created_at.split('T')[0]);
      }
      if (params.expires_at) {
        queryParams.append('expires_at', params.expires_at.split('T')[0]);
      }

      const queryString = queryParams.toString();
      const path = `${ADMIN_BASE}/tokens${queryString ? `?${queryString}` : ''}`;

      const data = await fetchAPI<TokenListResponse>(path, {
        method: 'GET',
      });

      return data;
    } catch (error) {
      console.error('Error fetching tokens:', error);
      throw error;
    }
  },

  get: async (id: number): Promise<Token> => {
    try {
      const data = await fetchAPI<Token>(`${ADMIN_BASE}/tokens/${id}`, {
        method: 'GET',
      });
      return data;
    } catch (error) {
      console.error('Error fetching token:', error);
      throw error;
    }
  },

  revoke: async (id: number): Promise<Token> => {
    try {
      const data = await fetchAPI<Token>(`${ADMIN_BASE}/tokens/${id}`, {
        method: 'PUT',
      });
      return data;
    } catch (error) {
      console.error('Error revoking token:', error);
      throw error;
    }
  },

  bulk: async (params: BulkActionParams): Promise<{ success: number[]; failed: number[] }> => {
    try {
      const data = await fetchAPI<{ success: number[]; failed: number[] }>(
        `${ADMIN_BASE}/tokens/bulk`,
        {
          method: 'POST',
          body: JSON.stringify(params),
        }
      );
      return data;
    } catch (error) {
      console.error('Error performing bulk action:', error);
      throw error;
    }
  },

  getDetails: async (id: number): Promise<TokenDetails> => {
    try {
      const data = await fetchAPI<TokenDetails>(`${ADMIN_BASE}/tokens/${id}/details`, {
        method: 'GET',
      });
      return data;
    } catch (error) {
      console.error('Error fetching token details:', error);
      throw error;
    }
  },

  getRelatedTokens: async (id: number, page = 1, perPage = 10): Promise<TokenRelatedResponse> => {
    try {
      const data = await fetchAPI<TokenRelatedResponse>(
        `${ADMIN_BASE}/tokens/${id}/related?page=${page}&per_page=${perPage}`,
        {
          method: 'GET',
        }
      );
      return data;
    } catch (error) {
      console.error('Error fetching related tokens:', error);
      throw error;
    }
  },

  getUsageHistory: async (id: number, page = 1, perPage = 10): Promise<TokenUsageHistoryResponse> => {
    try {
      const data = await fetchAPI<TokenUsageHistoryResponse>(
        `${ADMIN_BASE}/tokens/${id}/usage-history?page=${page}&per_page=${perPage}`,
        {
          method: 'GET',
        }
      );
      return data;
    } catch (error) {
      console.error('Error fetching token usage history:', error);
      throw error;
    }
  },

  delete: async (id: number): Promise<{ success: boolean; message: string }> => {
    try {
      const response = await fetchAPI<{ success: boolean; message: string }>(
        `${ADMIN_BASE}/tokens/${id}`,
        {
          method: 'DELETE',
        }
      );
      return response;
    } catch (error) {
      console.error('Error deleting token:', error);
      throw error;
    }
  }
};

export const settingsAPI = {
  get: async (): Promise<Settings> => {
    try {
      const response = await fetch('/wp-json/wp/v2/settings', {
        method: 'GET',
        headers: getHeaders(),
        credentials: 'same-origin',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data: { jwt_auth_pro_settings: Settings } = await response.json();
      return data.jwt_auth_pro_settings;
    } catch (error) {
      console.error('Error fetching settings:', error);
      throw error;
    }
  },

  getOptions: async (): Promise<SettingsOptions> => {
    try {
      const data = await fetchAPI<SettingsOptions>(`${ADMIN_BASE}/settings/options`, {
        method: 'GET',
      });
      return data;
    } catch (error) {
      console.error('Error fetching settings options:', error);
      throw error;
    }
  },

  update: async (settings: Settings): Promise<Settings> => {
    try {
      const response = await fetch('/wp-json/wp/v2/settings', {
        method: 'POST',
        headers: getHeaders(),
        credentials: 'same-origin',
        body: JSON.stringify({
          jwt_auth_pro_settings: {
            data_management: settings.data_management,
            token_settings: settings.token_settings,
            user_settings: settings.user_settings,
            rate_limit_settings: settings.rate_limit_settings
          }
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      return data.jwt_auth_pro_settings;
    } catch (error) {
      console.error('Error updating settings:', error);
      throw error;
    }
  },
};

export const systemAPI = {
  getInfo: async (): Promise<SystemInfo> => {
    try {
      const data = await fetchAPI<SystemInfo>(`${ADMIN_BASE}/system/info`, {
        method: 'GET',
      });
      return data;
    } catch (error) {
      console.error('Error fetching system info:', error);
      throw error;
    }
  },
};

