import { useState } from 'react';
import TableFilters from '@/components/tokens/table-filters';
import { TokensTable } from '@/components/tokens/tokens-table';
import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';

interface FilterState {
  status: 'all' | 'active' | 'expired' | 'revoked';
  username: string;
  creationDate: Date | undefined;
  expireDate: Date | undefined;
  revokedDate: Date | undefined;
}

const initialFilters: FilterState = {
  status: 'active',
  username: '',
  creationDate: undefined,
  expireDate: undefined,
  revokedDate: undefined
};

export default function TokensPage() {
  const [filters, setFilters] = useState<FilterState>(initialFilters);

  return (
    <PageWrapper>
      <Header
        title="Token Management"
        description="View and manage all JWT tokens in your system. Search, filter, and take actions like revocation or renewal on individual or multiple tokens."
      />
      <div className="jwt-bg-background jwt-rounded-lg jwt-border">
        <TableFilters onFiltersChange={setFilters} />
        <TokensTable filters={filters} />
      </div>
    </PageWrapper>
  );
}

