    56→  totalItems: number;
    57→  pageSize: number;
    58→}
    59→
    60→const pageSizeOptions = [10, 25, 50, 100];
    61→
    62→interface DateTimeCellProps {
    63→  value: string | null;
    64→}
    65→
    66→export const DateTimeCell = ({ value }: DateTimeCellProps) => {
    67→  if (!value) return '-';
    68→
    69→  // Match time patterns like: 2:35 am, 2:35 PM, 14:35, etc.
    70→  const timeRegex = /\d{1,2}:\d{2}(?:\s*[AaPp][Mm])?$/;
    71→  const timeMatch = value.match(timeRegex);
    72→
    73→  if (!timeMatch) return '-';
    74→
    75→  const timeIndex = value.lastIndexOf(timeMatch[0]);
    76→  const formatted = {
    77→    date: value.substring(0, timeIndex).trim(),
    78→    time: timeMatch[0]
    79→  };
    80→
    81→  return (
    82→    <div className="jwt-flex jwt-flex-col">
    83→      <span>{formatted.date}</span>
    84→      <span className="jwt-text-muted-foreground jwt-text-sm">{formatted.time}</span>
    85→    </div>
    86→  );
    87→};
    88→
    89→export function TokensTable({ filters }: TokensTableProps) {
    90→  const [searchParams, setSearchParams] = useSearchParams();
    91→  const [data, setData] = React.useState<Token[]>([]);
    92→  const [loading, setLoading] = React.useState(true);
    93→  const [selectedToken, setSelectedToken] = React.useState<Token | null>(null);
    94→  const [isRevokeDialogOpen, setIsRevokeDialogOpen] = React.useState(false);
    95→  const { toast } = useToast();
    96→  const [pagination, setPagination] = React.useState<PaginationState>({
    97→    currentPage: 1,
    98→    totalPages: 1,
    99→    totalItems: 0,
   100→    pageSize: 10
   101→  });
   102→
   103→  const handleViewDetails = (token: Token) => {
   104→    // Preserve current filters and add tokenId
   105→    const newParams = new URLSearchParams(searchParams);
   106→    newParams.set('tokenId', token.id.toString());
   107→    newParams.set('status', filters.status);
   108→    if (filters.username) newParams.set('username', filters.username);
   109→    if (filters.creationDate) newParams.set('creationDate', filters.creationDate.toISOString());
   110→    if (filters.expireDate) newParams.set('expireDate', filters.expireDate.toISOString());
   111→
   112→    setSearchParams(newParams);
   113→  };
   114→
   115→  const columns: ColumnDef<Token>[] = [
   116→    {
   117→      accessorKey: 'user_nicename',
   118→      header: 'Username',
   119→      cell: ({ row }) => {
   120→        const user_link = row.original.user_link;
   121→        const user_nicename = row.original.user_nicename;
   122→        return user_link ? (
   123→          <a href={user_link} className="jwt-text-primary hover:jwt-underline">
   124→            {user_nicename}
   125→          </a>
   126→        ) : (
   127→          user_nicename
   128→        );
   129→      }
   130→    },
   131→    {
   132→      accessorKey: 'issued_at',
   133→      header: 'Created',
   134→      cell: ({ row }) => <DateTimeCell value={row.original.issued_at} />
   135→    },
   136→    {
   137→      accessorKey: 'expires_at',
   138→      header: 'Expired',
   139→      cell: ({ row }) => <DateTimeCell value={row.original.expires_at} />
   140→    },
   141→    {
   142→      accessorKey: 'last_used_at',
   143→      header: 'Last Used',
   144→      cell: ({ row }) =>
   145→        row.original.last_used_at ? <DateTimeCell value={row.original.last_used_at} /> : 'Never'
   146→    },
   147→    {
   148→      accessorKey: 'status',
   149→      header: 'Status',
   150→      cell: ({ row }) => {
   151→        const status = row.original.status;
   152→        const badgeStyles = {
   153→          Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
   154→          Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
   155→          Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
   156→        };
   157→        return (
