import { Token } from './token';
import { PaginationData } from './pagination';

export interface ApiResponse<T> {
  success: boolean;
  data: T;
  message?: string;
}

export interface TokenListResponse {
  items: Token[];
  pagination: PaginationData;
}

export interface SystemVersionInfo {
  version: string;
  status: 'optimal' | 'warning' | 'error';
}

export interface PluginInfo {
  name: string;
  version: string;
  status: 'active' | 'inactive';
  author: string;
}

export interface ThemeInfo {
  name: string;
  version: string;
  author: string;
  status: 'active';
}

export interface ServerInfo {
  software: string;
  os: string;
  architecture: string;
  ip: string;
  hostname: string;
  ssl_installed: boolean;
}

export interface PHPConfig {
  memory_limit: string;
  max_execution_time: string;
  post_max_size: string;
  upload_max_filesize: string;
  max_input_vars: string;
  allow_url_fopen: string;
  display_errors: string;
  max_input_time: string;
  session: {
    save_handler: string;
    save_path: string;
  };
  opcache_enabled: boolean;
}

export interface WordPressConfig {
  version: string;
  site_url: string;
  home_url: string;
  is_multisite: boolean;
  max_upload_size: string;
  memory_limit: string;
  permalink_structure: string;
  language: string;
  timezone: {
    string: string;
    gmt_offset: string;
  };
  admin_email: string;
  environment_type: string;
}

export interface DatabaseInfo {
  version: string;
  charset: string;
  collate: string;
  prefix: string;
  tables: {
    base_prefix: string;
    prefix_length: number;
  };
}

export interface WordPressConstants {
  WP_DEBUG: boolean;
  WP_DEBUG_LOG: boolean;
  WP_DEBUG_DISPLAY: boolean;
  SCRIPT_DEBUG: boolean;
  WP_CACHE: boolean;
  CONCATENATE_SCRIPTS: boolean;
  COMPRESS_SCRIPTS: boolean;
  COMPRESS_CSS: boolean;
  WP_LOCAL_DEV: boolean;
}

export interface SystemInfo {
  php_version: SystemVersionInfo;
  wordpress_version: SystemVersionInfo;
  mysql_version: SystemVersionInfo;
  server: ServerInfo;
  php: PHPConfig;
  wordpress: WordPressConfig;
  database: DatabaseInfo;
  constants: WordPressConstants;
  theme: ThemeInfo;
  plugins: {
    total: number;
    active: number;
    items: PluginInfo[];
  };
}

export interface Settings {
  data_management: {
    delete_on_deactivation: boolean;
    analytics_retention_days: number;
    anonymize_ip: boolean;
  };
  token_settings: {
    jwt_expiration: number;
    refresh_token_expiration: number;
    signing_algorithm: string;
    enable_cors: boolean;
  };
  user_settings: {
    revoke_on_password_change: boolean;
    revoke_on_role_change: boolean;
    revoke_on_email_change: boolean;
    delete_on_user_delete: boolean;
  };
  rate_limit_settings: {
    max_requests: number;
    window_minutes: number;
    enabled: boolean;
  };
}

export interface SettingOption {
  value: number | string;
  label: string;
}

export interface SettingsOptions {
  analytics_retention: SettingOption[];
  jwt_expiration: SettingOption[];
  refresh_token_expiration: SettingOption[];
  signing_algorithm: SettingOption[];
}

export interface TokenUsageEvent {
  id: number;
  event_type: string;
  event_status: number;
  failure_reason: string | null;
  user_id: number | null;
  token_id: number | null;
  token_family: string | null;
  ip_address: string | null;
  user_agent: string | null;
  country_code: string | null;
  request_path: string | null;
  request_method: string | null;
  response_time: number | null;
  blog_id: number;
  timestamp: string;
}

export interface TokenDetails {
  token: Token;
  has_related_tokens: boolean;
}

export interface TokenRelatedResponse {
  items: Token[];
  pagination: PaginationData;
}

export interface TokenUsageHistoryResponse {
  items: TokenUsageEvent[];
  pagination: PaginationData;
}

export interface TokenStats {
  total_tokens: number;
  active_tokens: number;
  revoked_tokens: number;
  expired_tokens: number;
  token_growth_rate: number;
  total_requests: number;
  successful_requests: number;
  failed_requests: number;
  requests_growth_rate: number;
  total_tokens_today: number;
  total_tokens_yesterday: number;
  total_requests_today: number;
  total_requests_yesterday: number;
}

