     1→import { useEffect, useState, useMemo } from 'react';
     2→import { Header } from '@/components/ui/header';
     3→import { PageWrapper } from '@/components/ui/page-wrapper';
     4→import { analyticsAPI } from '@/services/api';
     5→import type { TokenStats } from '@/types/metrics';
     6→import type { HistoricalData } from '@/types/analytics';
     7→import { TokenMetricsCard } from '@/components/tokens/token-metrics-card';
     8→import { Key, Users, Rocket, BookOpen, LifeBuoy, ArrowRight } from 'lucide-react';
     9→import { getEventMetrics, getTokenMetrics, getTotalsMetrics } from '@/lib/metrics';
    10→import { Card, CardContent, CardHeader, CardTitle, CardFooter } from '@/components/ui/card';
    11→import { AnalyticsCharts } from '@/components/analytics/analytics-charts';
    12→
    13→interface QuickLinkProps {
    14→  title: string;
    15→  description: string;
    16→  icon: React.ComponentType<{ className?: string }>;
    17→  href: string;
    18→  linkText: string;
    19→}
    20→
    21→function QuickLink({ title, description, icon: Icon, href, linkText }: QuickLinkProps) {
    22→  return (
    23→    <Card>
    24→      <CardHeader>
    25→        <div className="jwt-flex jwt-items-center jwt-space-x-2">
    26→          <div className="jwt-p-2 jwt-bg-primary/10 jwt-rounded-full">
    27→            <Icon className="jwt-h-4 jwt-w-4 jwt-text-primary" />
    28→          </div>
    29→          <CardTitle className="jwt-text-base jwt-font-medium">{title}</CardTitle>
    30→        </div>
    31→      </CardHeader>
    32→      <CardContent>
    33→        <p className="jwt-text-sm jwt-text-muted-foreground">{description}</p>
    34→      </CardContent>
    35→      <CardFooter>
    36→        <a
    37→          href={href}
    38→          className="jwt-flex jwt-items-center jwt-text-sm jwt-text-primary hover:jwt-text-primary/80"
    39→        >
    40→          {linkText} <ArrowRight className="jwt-h-4 jwt-w-4 jwt-ml-1" />
    41→        </a>
    42→      </CardFooter>
    43→    </Card>
    44→  );
    45→}
    46→
    47→function MetricsSection({
    48→  isLoading,
    49→  allTimeStats
    50→}: {
    51→  isLoading: boolean;
    52→  allTimeStats: TokenStats | null;
    53→}) {
    54→  const metrics = useMemo(() => {
    55→    const tokenMetrics = getTokenMetrics(allTimeStats);
    56→    const eventMetrics = getEventMetrics(allTimeStats);
    57→    const totalsMetrics = getTotalsMetrics(allTimeStats);
    58→
    59→    return {
    60→      tokenMetrics,
    61→      eventMetrics,
    62→      totalsMetrics
    63→    };
    64→  }, [allTimeStats]);
    65→
    66→  return (
    67→    <div className="jwt-grid jwt-gap-4 jwt-grid-cols-1 sm:jwt-grid-cols-2">
    68→      <TokenMetricsCard
    69→        isLoading={isLoading}
    70→        title="Total Tokens All Time"
    71→        icon={Key}
    72→        mainMetric={metrics.tokenMetrics.mainMetric}
    73→        metrics={metrics.tokenMetrics.metrics}
    74→        gridCols={3}
    75→        totalsMetrics={metrics.totalsMetrics[0].totals}
    76→      />
    77→
    78→      <TokenMetricsCard
    79→        isLoading={isLoading}
    80→        title="Token Events All Time"
    81→        icon={Users}
    82→        mainMetric={metrics.eventMetrics.mainMetric}
    83→        metrics={metrics.eventMetrics.metrics}
    84→        gridCols={2}
    85→        totalsMetrics={metrics.totalsMetrics[1].totals}
    86→      />
    87→    </div>
    88→  );
    89→}
    90→
    91→const quickLinks = [
    92→  {
    93→    title: 'Quick Start',
    94→    description: 'Get start guide to help you get started with JWT Auth Pro.',
    95→    icon: Rocket,
    96→    href: 'https://docs.jwtauth.pro/introduction/quickstart?utm_source=jwt-auth-pro&utm_medium=admin-dashboard&utm_campaign=quickstart-guide',
    97→    linkText: 'Get Started'
    98→  },
    99→  {
   100→    title: 'Documentation',
   101→    description: 'All the documentation you need to get started with JWT Auth Pro.',
   102→    icon: BookOpen,
   103→    href: 'https://docs.jwtauth.pro/?utm_source=jwt-auth-pro&utm_medium=admin-dashboard&utm_campaign=documentation',
   104→    linkText: 'View Documentation'
   105→  },
   106→  {
   107→    title: 'Support',
   108→    description: 'Need help? Contact support for any issues you may have.',
   109→    icon: LifeBuoy,
   110→    href: 'https://jwtauth.freshdesk.com/support/tickets/new?utm_source=jwt-auth-pro&utm_medium=admin-dashboard&utm_campaign=support-ticket',
   111→    linkText: 'Create a Ticket'
   112→  }
   113→] as const;
   114→
   115→export default function HomePage() {
   116→  const [isLoading, setIsLoading] = useState(true);
   117→  const [allTimeStats, setAllTimeStats] = useState<TokenStats | null>(null);
   118→  const [historicalData, setHistoricalData] = useState<HistoricalData[]>([]);
   119→  const [period, setPeriod] = useState<'7d' | '30d' | '90d'>('7d');
   120→  const isWhitelabeled = window.wpApiSettings.is_whitelabeled;
   121→
   122→  useEffect(() => {
   123→    const controller = new AbortController();
   124→
   125→    async function fetchData() {
   126→      try {
   127→        setIsLoading(true);
   128→        const [stats, histData] = await Promise.all([
   129→          analyticsAPI.getAllTimeStats(),
   130→          analyticsAPI.getHistoricalData(period)
   131→        ]);
   132→
   133→        if (!controller.signal.aborted) {
   134→          setAllTimeStats(stats);
   135→          setHistoricalData(histData || []);
   136→        }
   137→      } catch (error) {
   138→        if (!controller.signal.aborted) {
   139→          console.error('Failed to fetch analytics data:', error);
   140→        }
   141→      } finally {
   142→        if (!controller.signal.aborted) {
   143→          setIsLoading(false);
   144→        }
   145→      }
   146→    }
   147→
   148→    void fetchData();
   149→
   150→    return () => {
   151→      controller.abort();
   152→    };
   153→  }, [period]);
   154→
   155→  return (
   156→    <PageWrapper>
   157→      <Header
   158→        title="Dashboard Overview"
   159→        description="Monitor your JWT authentication activity and system health at a glance. View analytics and key security metrics through interactive charts."
   160→      />
   161→
   162→      <div className="jwt-space-y-6">
   163→        <MetricsSection isLoading={isLoading} allTimeStats={allTimeStats} />
   164→
   165→        {!isLoading && historicalData.length > 0 && (
   166→          <AnalyticsCharts
   167→            historicalData={historicalData}
   168→            defaultPeriod={period}
   169→            onPeriodChange={setPeriod}
   170→          />
   171→        )}
   172→
   173→        {!isWhitelabeled && (
   174→          <div className="jwt-grid jwt-gap-6 jwt-grid-cols-1 md:jwt-grid-cols-2 lg:jwt-grid-cols-3">
   175→            {quickLinks.map((link) => (
   176→              <QuickLink key={link.title} {...link} />
   177→            ))}
   178→          </div>
   179→        )}
   180→      </div>
   181→    </PageWrapper>
   182→  );
   183→}
   184→

