declare global {
  interface JWTProAuthParams {
    slug: string;
    formattedPluginFile: string;
    nonce: string;
    i18n: {
      general_error: string;
    };
  }

  interface WPApiSettings {
    root: string;
    nonce: string;
    is_whitelabeled: boolean;
    preload?: {
      [key: string]: {
        body: any;
        headers: {
          'X-WP-Nonce': string;
        };
      };
    };
  }

  interface Window {
    wpApiSettings: WPApiSettings;
    JWTProAuthParams: JWTProAuthParams;
    ajaxurl: string;
  }
}

export {};

