    19→
    20→interface TableFiltersProps {
    21→  onFiltersChange: (filters: FilterState) => void;
    22→}
    23→
    24→interface FilterState {
    25→  status: 'all' | 'active' | 'expired' | 'revoked';
    26→  username: string;
    27→  creationDate: Date | undefined;
    28→  expireDate: Date | undefined;
    29→  revokedDate: Date | undefined;
    30→}
    31→
    32→const initialState: FilterState = {
    33→  status: 'active',
    34→  username: '',
    35→  creationDate: undefined,
    36→  expireDate: undefined,
    37→  revokedDate: undefined
    38→};
    39→
    40→export default function TableFilters({ onFiltersChange }: TableFiltersProps) {
    41→  const [filters, setFilters] = React.useState<FilterState>(initialState);
    42→  const [openStates, setOpenStates] = React.useState({
    43→    creation: false,
    44→    expire: false,
    45→    revoked: false
    46→  });
    47→  const [isClearFilters, setIsClearFilters] = React.useState(false);
    48→  const [isFirstRender, setIsFirstRender] = React.useState(true);
    49→
    50→  useDebounce(
    51→    filters.username,
    52→    (debouncedValue) => {
    53→      if (isClearFilters || isFirstRender) {
    54→        setIsClearFilters(false);
    55→        setIsFirstRender(false);
    56→        return;
    57→      }
    58→      if (debouncedValue !== initialState.username || filters.username === '') {
    59→        onFiltersChange({ ...filters, username: debouncedValue });
    60→      }
    61→    },
    62→    { delay: 500, minLength: 3 }
    63→  );
    64→
    65→  const handleFilterChange = React.useCallback(
    66→    (key: keyof FilterState, value: any) => {
    67→      const newFilters = { ...filters, [key]: value };
    68→      setFilters(newFilters);
    69→
    70→      if (key !== 'username') {
    71→        onFiltersChange(newFilters);
    72→      }
    73→
    74→      if (key === 'creationDate') {
    75→        setOpenStates((prev) => ({ ...prev, creation: false }));
    76→      } else if (key === 'expireDate') {
    77→        setOpenStates((prev) => ({ ...prev, expire: false }));
    78→      } else if (key === 'revokedDate') {
    79→        setOpenStates((prev) => ({ ...prev, revoked: false }));
    80→      }
    81→    },
    82→    [filters, onFiltersChange]
    83→  );
    84→
    85→  const handleClearFilters = React.useCallback(() => {
    86→    setIsClearFilters(true);
    87→    setFilters(initialState);
    88→    setOpenStates({
    89→      creation: false,
    90→      expire: false,
    91→      revoked: false
    92→    });
    93→    onFiltersChange(initialState);
    94→  }, [onFiltersChange]);
    95→
    96→  const formatDate = React.useCallback((date: Date | undefined) => {
    97→    if (!date) return null;
    98→    return format(date, 'MM/dd/yyyy');
    99→  }, []);
   100→
   101→  return (
   102→    <div className="jwt-border-b">
   103→      <div className="jwt-p-4 jwt-grid jwt-grid-cols-1 md:jwt-grid-cols-2 lg:jwt-grid-cols-3 xl:jwt-grid-cols-5 jwt-gap-4">
   104→        {/* Username Search */}
   105→        <div>
   106→          <Label htmlFor="username_search" className="jwt-sr-only">
   107→            Username
   108→          </Label>
   109→          <div className="jwt-relative jwt-mt-1.5">
   110→            <Search className="jwt-absolute jwt-left-2 jwt-top-3 jwt-h-4 jwt-w-4 jwt-text-muted-foreground" />
   111→            <Input
   112→              id="username_search"
   113→              value={filters.username}
   114→              onChange={(e) => handleFilterChange('username', e.target.value)}
   115→              placeholder="Username"
   116→              className="jwt-pl-8 !jwt-py-[19px] !jwt-mt-0"
   117→            />
   118→          </div>
   119→        </div>
   120→
   121→        {/* Status Filter */}
   122→        <div>
   123→          <Label htmlFor="status" className="jwt-sr-only">
   124→            Status
   125→          </Label>
   126→          <Select
   127→            value={filters.status}
   128→            onValueChange={(value) => handleFilterChange('status', value)}
   129→          >
   130→            <SelectTrigger id="status" className="jwt-w-full jwt-mt-1.5">
   131→              <SelectValue placeholder="Select status" />
   132→            </SelectTrigger>
   133→            <SelectContent>
   134→              <SelectItem value="all">All</SelectItem>
   135→              <SelectItem value="active">Active</SelectItem>
   136→              <SelectItem value="expired">Expired</SelectItem>
   137→              <SelectItem value="revoked">Revoked</SelectItem>
   138→            </SelectContent>
   139→          </Select>
   140→        </div>
   141→
   142→        {/* Creation Date */}
   143→        <div>
   144→          <Label className="jwt-sr-only">Creation Date</Label>
   145→          <Popover
   146→            open={openStates.creation}
   147→            onOpenChange={(open) => setOpenStates((prev) => ({ ...prev, creation: open }))}
   148→          >
   149→            <PopoverTrigger asChild>
   150→              <Button
   151→                variant="outline"
   152→                className={cn(
   153→                  'jwt-w-full jwt-justify-start jwt-text-left jwt-font-normal jwt-mt-1.5',
   154→                  !filters.creationDate && 'jwt-text-muted-foreground'
   155→                )}
   156→              >
   157→                <CalendarIcon className="jwt-mr-2 jwt-h-4 jwt-w-4" />
   158→                {formatDate(filters.creationDate) || <span>Created Date</span>}
   159→              </Button>
   160→            </PopoverTrigger>
   161→            <PopoverContent className="jwt-w-auto jwt-p-0" align="start">
   162→              <Calendar
   163→                mode="single"
   164→                selected={filters.creationDate}
   165→                onSelect={(date) => handleFilterChange('creationDate', date)}
   166→                initialFocus
   167→              />
   168→            </PopoverContent>
   169→          </Popover>
   170→        </div>
   171→
   172→        {/* Expire Date */}
   173→        <div>
   174→          <Label className="jwt-sr-only">Expire Date</Label>
   175→          <Popover
   176→            open={openStates.expire}
   177→            onOpenChange={(open) => setOpenStates((prev) => ({ ...prev, expire: open }))}
   178→          >
   179→            <PopoverTrigger asChild>
   180→              <Button
   181→                variant="outline"
   182→                className={cn(
   183→                  'jwt-w-full jwt-justify-start jwt-text-left jwt-font-normal jwt-mt-1.5',
   184→                  !filters.expireDate && 'jwt-text-muted-foreground'
   185→                )}
   186→              >
   187→                <CalendarIcon className="jwt-mr-2 jwt-h-4 jwt-w-4" />
   188→                {formatDate(filters.expireDate) || <span>Expire Date</span>}
   189→              </Button>
   190→            </PopoverTrigger>
   191→            <PopoverContent className="jwt-w-auto jwt-p-0" align="start">
   192→              <Calendar
   193→                mode="single"
   194→                selected={filters.expireDate}
   195→                onSelect={(date) => handleFilterChange('expireDate', date)}
   196→                initialFocus
   197→              />
   198→            </PopoverContent>
   199→          </Popover>
   200→        </div>
   201→
   202→        {/* Revoked Date */}
   203→        {/* <div>
   204→          <Label className="jwt-sr-only">Revoked Date</Label>
   205→          <Popover
   206→            open={openStates.revoked}
   207→            onOpenChange={(open) => setOpenStates((prev) => ({ ...prev, revoked: open }))}
   208→          >
   209→            <PopoverTrigger asChild>
   210→              <Button
   211→                variant="outline"
   212→                className={cn(
   213→                  'jwt-w-full jwt-justify-start jwt-text-left jwt-font-normal jwt-mt-1.5',
   214→                  !filters.revokedDate && 'jwt-text-muted-foreground'
   215→                )}
   216→              >
   217→                <CalendarIcon className="jwt-mr-2 jwt-h-4 jwt-w-4" />
   218→                {formatDate(filters.revokedDate) || <span>Revoked Date</span>}
