import * as React from 'react';
import { format } from 'date-fns';
import { Calendar as CalendarIcon, Search } from 'lucide-react';

import { cn } from '@/lib/utils';
import { Button } from '@/components/ui/button';
import { Calendar } from '@/components/ui/calendar';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from '@/components/ui/select';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useDebounce } from '@/components/hooks/use-debounce';

interface TableFiltersProps {
  onFiltersChange: (filters: FilterState) => void;
}

interface FilterState {
  status: 'all' | 'active' | 'expired' | 'revoked';
  username: string;
  creationDate: Date | undefined;
  expireDate: Date | undefined;
  revokedDate: Date | undefined;
}

const initialState: FilterState = {
  status: 'active',
  username: '',
  creationDate: undefined,
  expireDate: undefined,
  revokedDate: undefined
};

export default function TableFilters({ onFiltersChange }: TableFiltersProps) {
  const [filters, setFilters] = React.useState<FilterState>(initialState);
  const [openStates, setOpenStates] = React.useState({
    creation: false,
    expire: false,
    revoked: false
  });
  const [isClearFilters, setIsClearFilters] = React.useState(false);
  const [isFirstRender, setIsFirstRender] = React.useState(true);

  useDebounce(
    filters.username,
    (debouncedValue) => {
      if (isClearFilters || isFirstRender) {
        setIsClearFilters(false);
        setIsFirstRender(false);
        return;
      }
      if (debouncedValue !== initialState.username || filters.username === '') {
        onFiltersChange({ ...filters, username: debouncedValue });
      }
    },
    { delay: 500, minLength: 3 }
  );

  const handleFilterChange = React.useCallback(
    (key: keyof FilterState, value: any) => {
      const newFilters = { ...filters, [key]: value };
      setFilters(newFilters);

      if (key !== 'username') {
        onFiltersChange(newFilters);
      }

      if (key === 'creationDate') {
        setOpenStates((prev) => ({ ...prev, creation: false }));
      } else if (key === 'expireDate') {
        setOpenStates((prev) => ({ ...prev, expire: false }));
      } else if (key === 'revokedDate') {
        setOpenStates((prev) => ({ ...prev, revoked: false }));
      }
    },
    [filters, onFiltersChange]
  );

  const handleClearFilters = React.useCallback(() => {
    setIsClearFilters(true);
    setFilters(initialState);
    setOpenStates({
      creation: false,
      expire: false,
      revoked: false
    });
    onFiltersChange(initialState);
  }, [onFiltersChange]);

  const formatDate = React.useCallback((date: Date | undefined) => {
    if (!date) return null;
    return format(date, 'MM/dd/yyyy');
  }, []);

  return (
    <div className="jwt-border-b">
      <div className="jwt-p-4 jwt-grid jwt-grid-cols-1 md:jwt-grid-cols-2 lg:jwt-grid-cols-3 xl:jwt-grid-cols-5 jwt-gap-4">
        {/* Username Search */}
        <div>
          <Label htmlFor="username_search" className="jwt-sr-only">
            Username
          </Label>
          <div className="jwt-relative jwt-mt-1.5">
            <Search className="jwt-absolute jwt-left-2 jwt-top-3 jwt-h-4 jwt-w-4 jwt-text-muted-foreground" />
            <Input
              id="username_search"
              value={filters.username}
              onChange={(e) => handleFilterChange('username', e.target.value)}
              placeholder="Username"
              className="jwt-pl-8 !jwt-py-[19px] !jwt-mt-0"
            />
          </div>
        </div>

        {/* Status Filter */}
        <div>
          <Label htmlFor="status" className="jwt-sr-only">
            Status
          </Label>
          <Select
            value={filters.status}
            onValueChange={(value) => handleFilterChange('status', value)}
          >
            <SelectTrigger id="status" className="jwt-w-full jwt-mt-1.5">
              <SelectValue placeholder="Select status" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All</SelectItem>
              <SelectItem value="active">Active</SelectItem>
              <SelectItem value="expired">Expired</SelectItem>
              <SelectItem value="revoked">Revoked</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Creation Date */}
        <div>
          <Label className="jwt-sr-only">Creation Date</Label>
          <Popover
            open={openStates.creation}
            onOpenChange={(open) => setOpenStates((prev) => ({ ...prev, creation: open }))}
          >
            <PopoverTrigger asChild>
              <Button
                variant="outline"
                className={cn(
                  'jwt-w-full jwt-justify-start jwt-text-left jwt-font-normal jwt-mt-1.5',
                  !filters.creationDate && 'jwt-text-muted-foreground'
                )}
              >
                <CalendarIcon className="jwt-mr-2 jwt-h-4 jwt-w-4" />
                {formatDate(filters.creationDate) || <span>Created Date</span>}
              </Button>
            </PopoverTrigger>
            <PopoverContent className="jwt-w-auto jwt-p-0" align="start">
              <Calendar
                mode="single"
                selected={filters.creationDate}
                onSelect={(date) => handleFilterChange('creationDate', date)}
                initialFocus
              />
            </PopoverContent>
          </Popover>
        </div>

        {/* Expire Date */}
        <div>
          <Label className="jwt-sr-only">Expire Date</Label>
          <Popover
            open={openStates.expire}
            onOpenChange={(open) => setOpenStates((prev) => ({ ...prev, expire: open }))}
          >
            <PopoverTrigger asChild>
              <Button
                variant="outline"
                className={cn(
                  'jwt-w-full jwt-justify-start jwt-text-left jwt-font-normal jwt-mt-1.5',
                  !filters.expireDate && 'jwt-text-muted-foreground'
                )}
              >
                <CalendarIcon className="jwt-mr-2 jwt-h-4 jwt-w-4" />
                {formatDate(filters.expireDate) || <span>Expire Date</span>}
              </Button>
            </PopoverTrigger>
            <PopoverContent className="jwt-w-auto jwt-p-0" align="start">
              <Calendar
                mode="single"
                selected={filters.expireDate}
                onSelect={(date) => handleFilterChange('expireDate', date)}
                initialFocus
              />
            </PopoverContent>
          </Popover>
        </div>

        {/* Revoked Date */}
        {/* <div>
          <Label className="jwt-sr-only">Revoked Date</Label>
          <Popover
            open={openStates.revoked}
            onOpenChange={(open) => setOpenStates((prev) => ({ ...prev, revoked: open }))}
          >
            <PopoverTrigger asChild>
              <Button
                variant="outline"
                className={cn(
                  'jwt-w-full jwt-justify-start jwt-text-left jwt-font-normal jwt-mt-1.5',
                  !filters.revokedDate && 'jwt-text-muted-foreground'
                )}
              >
                <CalendarIcon className="jwt-mr-2 jwt-h-4 jwt-w-4" />
                {formatDate(filters.revokedDate) || <span>Revoked Date</span>}
              </Button>
            </PopoverTrigger>
            <PopoverContent className="jwt-w-auto jwt-p-0" align="start">
              <Calendar
                mode="single"
                selected={filters.revokedDate}
                onSelect={(date) => handleFilterChange('revokedDate', date)}
                initialFocus
              />
            </PopoverContent>
          </Popover>
        </div> */}

        {/* Clear Filters Button */}
        <div className="jwt-flex jwt-items-end">
          <Button onClick={handleClearFilters} size="default" className="jwt-w-full">
            Clear Filters
          </Button>
        </div>
      </div>
    </div>
  );
}

