  prefix: 'jwt-',
  darkMode: ["class"],
  content: ['./admin/**/*.{js,jsx,ts,tsx}'],
  theme: {
    container: {
      center: true,
      padding: "2rem",
      screens: {
        "2xl": "1400px",
      },
