import { useEffect, useState, useCallback } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Ban, Trash2 } from 'lucide-react';

import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { Button } from '@/components/ui/button';
import { tokenAPI } from '@/services/api';
import type { TokenDetails, Token } from '@/types/token';
import type { TokenUsageEvent } from '@/types/api';
import { DateTimeCell } from '@/components/tokens/tokens-table';
import { useToast } from '@/components/hooks/use-toast';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow
} from '@/components/ui/table';
import { cn } from '@/lib/utils';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { SubHeading } from '@/components/ui/sub-heading';
import { Pagination } from '@/components/ui/pagination';

export default function TokenDetailsPage() {
  const [searchParams, setSearchParams] = useSearchParams();
  const tokenId = searchParams.get('tokenId');
  const [loading, setLoading] = useState(true);
  const [tokenDetails, setTokenDetails] = useState<TokenDetails | null>(null);
  const [relatedTokens, setRelatedTokens] = useState<Token[]>([]);
  const [usageHistory, setUsageHistory] = useState<TokenUsageEvent[]>([]);
  const [relatedTokensPage, setRelatedTokensPage] = useState(1);
  const [usageHistoryPage, setUsageHistoryPage] = useState(1);
  const [relatedTokensPagination, setRelatedTokensPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const [usageHistoryPagination, setUsageHistoryPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const { toast } = useToast();
  const navigate = useNavigate();
  const [openRevokeDialog, setOpenRevokeDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const perPage = 20;

  const fetchUsageHistory = useCallback(
    async (id: number, page: number) => {
      try {
        const usageData = await tokenAPI.getUsageHistory(id, page, perPage);
        setUsageHistory(usageData.items);
        setUsageHistoryPagination({
          total: usageData.pagination.total,
          total_pages: usageData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching usage history:', error);
        toast({
          title: 'Error',
          description: 'Failed to load usage history',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchRelatedTokens = useCallback(
    async (id: number, page: number) => {
      try {
        const relatedData = await tokenAPI.getRelatedTokens(id, page, perPage);
        setRelatedTokens(relatedData.items);
        setRelatedTokensPagination({
          total: relatedData.pagination.total,
          total_pages: relatedData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching related tokens:', error);
        toast({
          title: 'Error',
          description: 'Failed to load related tokens',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchTokenDetails = useCallback(
    async (id: number) => {
      try {
        const details = await tokenAPI.getDetails(id);
        setTokenDetails(details);

        if (details.has_related_tokens) {
          await fetchRelatedTokens(id, 1);
        }

        await fetchUsageHistory(id, 1);
      } catch (error) {
        console.error('Error fetching token details:', error);
        toast({
          title: 'Error',
          description: 'Failed to load token data',
          variant: 'destructive'
        });
      }
    },
    [toast, fetchRelatedTokens, fetchUsageHistory]
  );

  useEffect(() => {
    if (!tokenId) return;

    setLoading(true);
    fetchTokenDetails(parseInt(tokenId, 10)).finally(() => setLoading(false));
  }, [tokenId, fetchTokenDetails]);

  useEffect(() => {
    if (!tokenId || !tokenDetails?.has_related_tokens) return;

    fetchRelatedTokens(parseInt(tokenId, 10), relatedTokensPage);
  }, [tokenId, relatedTokensPage, tokenDetails?.has_related_tokens, fetchRelatedTokens]);

  useEffect(() => {
    if (!tokenId) return;

    fetchUsageHistory(parseInt(tokenId, 10), usageHistoryPage);
  }, [tokenId, usageHistoryPage, fetchUsageHistory]);

  const handleBack = () => {
    const newParams = new URLSearchParams(searchParams);
    newParams.delete('tokenId');
    setSearchParams(newParams);
  };

  const handleTokenSelect = (selectedTokenId: string | number) => {
    const newParams = new URLSearchParams(searchParams);
    newParams.set('tokenId', selectedTokenId.toString());
    setSearchParams(newParams);
    // Reset pagination when selecting a new token
    setRelatedTokensPage(1);
    setUsageHistoryPage(1);
  };

  const handleRevoke = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.revoke(parseInt(tokenId, 10));
      const details = await tokenAPI.getDetails(parseInt(tokenId, 10));
      setTokenDetails(details);
      setOpenRevokeDialog(false);
      toast({
        title: 'Token Revoked',
        description: 'The token has been successfully revoked.'
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to revoke the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleDelete = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.delete(parseInt(tokenId, 10));
      setOpenDeleteDialog(false);
      toast({
        title: 'Token Deleted',
        description: 'The token has been successfully deleted.'
      });
      const newParams = new URLSearchParams(searchParams);
      newParams.delete('tokenId');
      navigate(`/tokens?${newParams.toString()}`);
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to delete the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleRelatedTokensPageChange = (newPage: number) => {
    setRelatedTokensPage(newPage);
  };

  const handleUsageHistoryPageChange = (newPage: number) => {
    setUsageHistoryPage(newPage);
  };

  if (loading) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Loading...</div>
      </PageWrapper>
    );
  }

  if (!tokenDetails) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Token not found</div>
      </PageWrapper>
    );
  }

  const { token } = tokenDetails;

  const FieldType = (props: { label: string; children: React.ReactNode }) => {
    return (
      <div className="!jwt-space-y-1">
        <h4 className="!jwt-text-gray-900 !jwt-font-medium jwt-text-muted-foreground">
          {props.label}
        </h4>
        <div className="text-sm jwt-text-gray-900 ">{props.children}</div>
      </div>
    );
  };

  return (
    <PageWrapper>
      <Header
        title={
          <div className="jwt-flex jwt-items-center jwt-gap-3">
            <Button
              variant="outline"
              size="sm"
              onClick={handleBack}
              className="jwt-h-8 jwt-w-8 jwt-p-0"
            >
              <ArrowLeft className="jwt-h-4 jwt-w-4" />
            </Button>
            <span>Token Details</span>
          </div>
        }
        description="Detailed information about the selected token."
      >
        <div className="jwt-flex jwt-flex-col sm:jwt-flex-row jwt-gap-2">
          {tokenDetails?.token.status === 'Active' && (
            <Dialog open={openRevokeDialog} onOpenChange={setOpenRevokeDialog}>
              <DialogTrigger asChild>
                <Button variant="outline" className="jwt-w-full sm:jwt-w-auto">
                  <Ban className="jwt-h-4 jwt-w-4" />
                  Revoke Token
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Revoke Token</DialogTitle>
                  <DialogDescription>
                    Are you sure you want to revoke this token? This action will prevent the token
                    from being used for authentication.
                  </DialogDescription>
                </DialogHeader>
                <DialogFooter className="sm:jwt-justify-end">
                  <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                    <Button variant="outline" onClick={() => setOpenRevokeDialog(false)}>
                      Cancel
                    </Button>
                    <Button variant="outline" onClick={handleRevoke}>
                      Revoke
                    </Button>
                  </div>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          )}

          <Dialog open={openDeleteDialog} onOpenChange={setOpenDeleteDialog}>
            <DialogTrigger asChild>
              <Button variant="destructive" className="jwt-w-full sm:jwt-w-auto">
                <Trash2 className="jwt-h-4 jwt-w-4" />
                Delete Token
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Delete Token</DialogTitle>
                <DialogDescription>
                  Are you sure you want to delete this token?
                  <br />
                  When deleting a token the whole tokens family is deleted as well.
                  <br />
                  <br />
                  <strong>This action cannot be undone.</strong>
                </DialogDescription>
              </DialogHeader>
              <DialogFooter className="sm:jwt-justify-end">
                <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                  <Button variant="outline" onClick={() => setOpenDeleteDialog(false)}>
                    Cancel
                  </Button>
                  <Button variant="destructive" onClick={handleDelete}>
                    Delete
                  </Button>
                </div>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </Header>

      <div className="jwt-space-y-8">
        {/* User Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="User Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-4">
              <FieldType label="Username">
                {token.user_link ? (
                  <a href={token.user_link} className="jwt-text-primary hover:jwt-underline">
                    {token.user_display_name}
                  </a>
                ) : (
                  token.user_nicename
                )}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User ID">{token.user_id}</FieldType>
            </div>
          </div>
        </div>

        {/* Token Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Token Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Status">
                <span
                  className={cn(
                    'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                    'jwt-font-medium jwt-border jwt-text-xs',
                    {
                      'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                        token.status === 'Active',
                      'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                        token.status === 'Revoked',
                      'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200':
                        token.status === 'Expired'
                    }
                  )}
                >
                  {token.status}
                </span>
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Family">{token.token_family}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Hash">{token.hash}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Hash">{token.refresh_token_hash}</FieldType>
            </div>
          </div>
        </div>

        {/* Dates */}
        <div className="jwt-space-y-4">
          <SubHeading title="Dates" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Created">
                <DateTimeCell value={token.issued_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Expires">
                <DateTimeCell value={token.expires_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Last Used">
                {token.last_used_at ? <DateTimeCell value={token.last_used_at} /> : <p>Never</p>}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Expires">
                <DateTimeCell value={token.refresh_token_expires_at} />
              </FieldType>
            </div>
            {token.revoked_at && (
              <div className="jwt-space-y-2">
                <FieldType label="Revoked">
                  <DateTimeCell value={token.revoked_at} />
                </FieldType>
              </div>
            )}
          </div>
        </div>

        {/* Client Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Client Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="IP Address">{token.ip_address}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User Agent">{token.user_agent}</FieldType>
            </div>
          </div>
        </div>

        {/* Related Tokens */}
        {tokenDetails.has_related_tokens && relatedTokens.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Related Tokens</CardTitle>
              <CardDescription>Other tokens from the same token family</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-overflow-x-auto">
                <Table>
                  <TableHeader className="jwt-bg-gray-50">
                    <TableRow className="hover:jwt-bg-transparent">
                      <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Created</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Expires</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Last Used</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">IP Address</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {relatedTokens.map((relatedToken) => {
                      const badgeStyles = {
                        Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
                        Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
                        Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
                      };

                      return (
                        <TableRow
                          key={relatedToken.id}
                          className="hover:jwt-bg-muted/50 jwt-cursor-pointer"
                          onClick={() => handleTokenSelect(relatedToken.id)}
                        >
                          <TableCell className="jwt-py-5">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                badgeStyles[relatedToken.status as keyof typeof badgeStyles]
                              )}
                            >
                              {relatedToken.status}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.issued_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.expires_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            {relatedToken.last_used_at ? (
                              <DateTimeCell value={relatedToken.last_used_at} />
                            ) : (
                              'Never'
                            )}
                          </TableCell>
                          <TableCell className="jwt-py-5">{relatedToken.ip_address}</TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                {relatedTokensPagination.total_pages > 1 && (
                  <div className="jwt-mt-4">
                    <Pagination
                      page={relatedTokensPage}
                      totalPages={relatedTokensPagination.total_pages}
                      onPageChange={handleRelatedTokensPageChange}
                    />
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Usage History */}
        {usageHistory.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Usage History</CardTitle>
              <CardDescription>
                All the events that have been logged for this token.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-space-y-4">
                <div className="jwt-overflow-x-auto">
                  <Table>
                    <TableHeader className="jwt-bg-gray-50">
                      <TableRow className="hover:jwt-bg-transparent">
                        <TableHead className="jwt-text-xs jwt-font-medium">Event Type</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Method</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Path</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">IP</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Date</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {usageHistory.map((event) => (
                        <TableRow key={event.id}>
                          <TableCell className="jwt-py-4">
                            <span className="jwt-capitalize">
                              {event.event_type.replace(/_/g, ' ')}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                {
                                  'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                                    event.event_status === 1,
                                  'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                                    event.event_status === 2
                                }
                              )}
                            >
                              {event.event_status === 1 ? 'Success' : 'Failed'}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded jwt-px-2 jwt-py-1',
                                'jwt-text-xs jwt-font-medium',
                                {
                                  'jwt-bg-blue-100 jwt-text-blue-700':
                                    event.request_method === 'GET',
                                  'jwt-bg-green-100 jwt-text-green-700':
                                    event.request_method === 'POST',
                                  'jwt-bg-yellow-100 jwt-text-yellow-700':
                                    event.request_method === 'PUT',
                                  'jwt-bg-red-100 jwt-text-red-700':
                                    event.request_method === 'DELETE'
                                }
                              )}
                            >
                              {event.request_method}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4 jwt-max-w-[200px] jwt-truncate">
                            {event.request_path}
                          </TableCell>
                          <TableCell className="jwt-py-4">{event.ip_address}</TableCell>
                          <TableCell className="jwt-py-4">{event.timestamp}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                  {usageHistoryPagination.total_pages > 1 && (
                    <div className="jwt-mt-4">
                      <Pagination
                        page={usageHistoryPage}
                        totalPages={usageHistoryPagination.total_pages}
                        onPageChange={handleUsageHistoryPageChange}
                      />
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </PageWrapper>
  );
}

import { useEffect, useState, useCallback } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Ban, Trash2 } from 'lucide-react';

import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { Button } from '@/components/ui/button';
import { tokenAPI } from '@/services/api';
import type { TokenDetails, Token } from '@/types/token';
import type { TokenUsageEvent } from '@/types/api';
import { DateTimeCell } from '@/components/tokens/tokens-table';
import { useToast } from '@/components/hooks/use-toast';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow
} from '@/components/ui/table';
import { cn } from '@/lib/utils';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { SubHeading } from '@/components/ui/sub-heading';
import { Pagination } from '@/components/ui/pagination';

export default function TokenDetailsPage() {
  const [searchParams, setSearchParams] = useSearchParams();
  const tokenId = searchParams.get('tokenId');
  const [loading, setLoading] = useState(true);
  const [tokenDetails, setTokenDetails] = useState<TokenDetails | null>(null);
  const [relatedTokens, setRelatedTokens] = useState<Token[]>([]);
  const [usageHistory, setUsageHistory] = useState<TokenUsageEvent[]>([]);
  const [relatedTokensPage, setRelatedTokensPage] = useState(1);
  const [usageHistoryPage, setUsageHistoryPage] = useState(1);
  const [relatedTokensPagination, setRelatedTokensPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const [usageHistoryPagination, setUsageHistoryPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const { toast } = useToast();
  const navigate = useNavigate();
  const [openRevokeDialog, setOpenRevokeDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const perPage = 20;

  const fetchUsageHistory = useCallback(
    async (id: number, page: number) => {
      try {
        const usageData = await tokenAPI.getUsageHistory(id, page, perPage);
        setUsageHistory(usageData.items);
        setUsageHistoryPagination({
          total: usageData.pagination.total,
          total_pages: usageData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching usage history:', error);
        toast({
          title: 'Error',
          description: 'Failed to load usage history',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchRelatedTokens = useCallback(
    async (id: number, page: number) => {
      try {
        const relatedData = await tokenAPI.getRelatedTokens(id, page, perPage);
        setRelatedTokens(relatedData.items);
        setRelatedTokensPagination({
          total: relatedData.pagination.total,
          total_pages: relatedData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching related tokens:', error);
        toast({
          title: 'Error',
          description: 'Failed to load related tokens',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchTokenDetails = useCallback(
    async (id: number) => {
      try {
        const details = await tokenAPI.getDetails(id);
        setTokenDetails(details);

        if (details.has_related_tokens) {
          await fetchRelatedTokens(id, 1);
        }

        await fetchUsageHistory(id, 1);
      } catch (error) {
        console.error('Error fetching token details:', error);
        toast({
          title: 'Error',
          description: 'Failed to load token data',
          variant: 'destructive'
        });
      }
    },
    [toast, fetchRelatedTokens, fetchUsageHistory]
  );

  useEffect(() => {
    if (!tokenId) return;

    setLoading(true);
    fetchTokenDetails(parseInt(tokenId, 10)).finally(() => setLoading(false));
  }, [tokenId, fetchTokenDetails]);

  useEffect(() => {
    if (!tokenId || !tokenDetails?.has_related_tokens) return;

    fetchRelatedTokens(parseInt(tokenId, 10), relatedTokensPage);
  }, [tokenId, relatedTokensPage, tokenDetails?.has_related_tokens, fetchRelatedTokens]);

  useEffect(() => {
    if (!tokenId) return;

    fetchUsageHistory(parseInt(tokenId, 10), usageHistoryPage);
  }, [tokenId, usageHistoryPage, fetchUsageHistory]);

  const handleBack = () => {
    const newParams = new URLSearchParams(searchParams);
    newParams.delete('tokenId');
    setSearchParams(newParams);
  };

  const handleTokenSelect = (selectedTokenId: string | number) => {
    const newParams = new URLSearchParams(searchParams);
    newParams.set('tokenId', selectedTokenId.toString());
    setSearchParams(newParams);
    // Reset pagination when selecting a new token
    setRelatedTokensPage(1);
    setUsageHistoryPage(1);
  };

  const handleRevoke = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.revoke(parseInt(tokenId, 10));
      const details = await tokenAPI.getDetails(parseInt(tokenId, 10));
      setTokenDetails(details);
      setOpenRevokeDialog(false);
      toast({
        title: 'Token Revoked',
        description: 'The token has been successfully revoked.'
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to revoke the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleDelete = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.delete(parseInt(tokenId, 10));
      setOpenDeleteDialog(false);
      toast({
        title: 'Token Deleted',
        description: 'The token has been successfully deleted.'
      });
      const newParams = new URLSearchParams(searchParams);
      newParams.delete('tokenId');
      navigate(`/tokens?${newParams.toString()}`);
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to delete the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleRelatedTokensPageChange = (newPage: number) => {
    setRelatedTokensPage(newPage);
  };

  const handleUsageHistoryPageChange = (newPage: number) => {
    setUsageHistoryPage(newPage);
  };

  if (loading) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Loading...</div>
      </PageWrapper>
    );
  }

  if (!tokenDetails) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Token not found</div>
      </PageWrapper>
    );
  }

  const { token } = tokenDetails;

  const FieldType = (props: { label: string; children: React.ReactNode }) => {
    return (
      <div className="jwt-space-y-1">
        <h4 className="jwt-text-sm jwt-font-medium jwt-text-gray-600">
          {props.label}
        </h4>
        <div className="jwt-text-sm jwt-text-gray-900">{props.children}</div>
      </div>
    );
  };

  return (
    <PageWrapper>
      <Header
        title={
          <div className="jwt-flex jwt-items-center jwt-gap-3">
            <Button
              variant="outline"
              size="sm"
              onClick={handleBack}
              className="jwt-h-8 jwt-w-8 jwt-p-0"
            >
              <ArrowLeft className="jwt-h-4 jwt-w-4" />
            </Button>
            <span>Token Details</span>
          </div>
        }
        description="Detailed information about the selected token."
      >
        <div className="jwt-flex jwt-flex-col sm:jwt-flex-row jwt-gap-2">
          {tokenDetails?.token.status === 'Active' && (
            <Dialog open={openRevokeDialog} onOpenChange={setOpenRevokeDialog}>
              <DialogTrigger asChild>
                <Button variant="outline" className="jwt-w-full sm:jwt-w-auto">
                  <Ban className="jwt-h-4 jwt-w-4" />
                  Revoke Token
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Revoke Token</DialogTitle>
                  <DialogDescription>
                    Are you sure you want to revoke this token? This action will prevent the token
                    from being used for authentication.
                  </DialogDescription>
                </DialogHeader>
                <DialogFooter className="sm:jwt-justify-end">
                  <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                    <Button variant="outline" onClick={() => setOpenRevokeDialog(false)}>
                      Cancel
                    </Button>
                    <Button variant="outline" onClick={handleRevoke}>
                      Revoke
                    </Button>
                  </div>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          )}

          <Dialog open={openDeleteDialog} onOpenChange={setOpenDeleteDialog}>
            <DialogTrigger asChild>
              <Button variant="destructive" className="jwt-w-full sm:jwt-w-auto">
                <Trash2 className="jwt-h-4 jwt-w-4" />
                Delete Token
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Delete Token</DialogTitle>
                <DialogDescription>
                  Are you sure you want to delete this token?
                  <br />
                  When deleting a token the whole tokens family is deleted as well.
                  <br />
                  <br />
                  <strong>This action cannot be undone.</strong>
                </DialogDescription>
              </DialogHeader>
              <DialogFooter className="sm:jwt-justify-end">
                <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                  <Button variant="outline" onClick={() => setOpenDeleteDialog(false)}>
                    Cancel
                  </Button>
                  <Button variant="destructive" onClick={handleDelete}>
                    Delete
                  </Button>
                </div>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </Header>

      <div className="jwt-space-y-8">
        {/* User Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="User Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-4">
              <FieldType label="Username">
                {token.user_link ? (
                  <a href={token.user_link} className="jwt-text-primary hover:jwt-underline">
                    {token.user_display_name}
                  </a>
                ) : (
                  token.user_nicename
                )}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User ID">{token.user_id}</FieldType>
            </div>
          </div>
        </div>

        {/* Token Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Token Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Status">
                <span
                  className={cn(
                    'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                    'jwt-font-medium jwt-border jwt-text-xs',
                    {
                      'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                        token.status === 'Active',
                      'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                        token.status === 'Revoked',
                      'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200':
                        token.status === 'Expired'
                    }
                  )}
                >
                  {token.status}
                </span>
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Family">{token.token_family}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Hash">{token.hash}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Hash">{token.refresh_token_hash}</FieldType>
            </div>
          </div>
        </div>

        {/* Dates */}
        <div className="jwt-space-y-4">
          <SubHeading title="Dates" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Created">
                <DateTimeCell value={token.issued_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Expires">
                <DateTimeCell value={token.expires_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Last Used">
                {token.last_used_at ? <DateTimeCell value={token.last_used_at} /> : <p>Never</p>}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Expires">
                <DateTimeCell value={token.refresh_token_expires_at} />
              </FieldType>
            </div>
            {token.revoked_at && (
              <div className="jwt-space-y-2">
                <FieldType label="Revoked">
                  <DateTimeCell value={token.revoked_at} />
                </FieldType>
              </div>
            )}
          </div>
        </div>

        {/* Client Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Client Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="IP Address">{token.ip_address}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User Agent">{token.user_agent}</FieldType>
            </div>
          </div>
        </div>

        {/* Related Tokens */}
        {tokenDetails.has_related_tokens && relatedTokens.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Related Tokens</CardTitle>
              <CardDescription>Other tokens from the same token family</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-overflow-x-auto">
                <Table>
                  <TableHeader className="jwt-bg-gray-50">
                    <TableRow className="hover:jwt-bg-transparent">
                      <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Created</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Expires</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Last Used</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">IP Address</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {relatedTokens.map((relatedToken) => {
                      const badgeStyles = {
                        Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
                        Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
                        Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
                      };

                      return (
                        <TableRow
                          key={relatedToken.id}
                          className="hover:jwt-bg-muted/50 jwt-cursor-pointer"
                          onClick={() => handleTokenSelect(relatedToken.id)}
                        >
                          <TableCell className="jwt-py-5">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                badgeStyles[relatedToken.status as keyof typeof badgeStyles]
                              )}
                            >
                              {relatedToken.status}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.issued_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.expires_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            {relatedToken.last_used_at ? (
                              <DateTimeCell value={relatedToken.last_used_at} />
                            ) : (
                              'Never'
                            )}
                          </TableCell>
                          <TableCell className="jwt-py-5">{relatedToken.ip_address}</TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                {relatedTokensPagination.total_pages > 1 && (
                  <div className="jwt-mt-4">
                    <Pagination
                      page={relatedTokensPage}
                      totalPages={relatedTokensPagination.total_pages}
                      onPageChange={handleRelatedTokensPageChange}
                    />
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Usage History */}
        {usageHistory.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Usage History</CardTitle>
              <CardDescription>
                All the events that have been logged for this token.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-space-y-4">
                <div className="jwt-overflow-x-auto">
                  <Table>
                    <TableHeader className="jwt-bg-gray-50">
                      <TableRow className="hover:jwt-bg-transparent">
                        <TableHead className="jwt-text-xs jwt-font-medium">Event Type</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Method</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Path</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">IP</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Date</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {usageHistory.map((event) => (
                        <TableRow key={event.id}>
                          <TableCell className="jwt-py-4">
                            <span className="jwt-capitalize">
                              {event.event_type.replace(/_/g, ' ')}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                {
                                  'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                                    event.event_status === 1,
                                  'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                                    event.event_status === 2
                                }
                              )}
                            >
                              {event.event_status === 1 ? 'Success' : 'Failed'}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded jwt-px-2 jwt-py-1',
                                'jwt-text-xs jwt-font-medium',
                                {
                                  'jwt-bg-blue-100 jwt-text-blue-700':
                                    event.request_method === 'GET',
                                  'jwt-bg-green-100 jwt-text-green-700':
                                    event.request_method === 'POST',
                                  'jwt-bg-yellow-100 jwt-text-yellow-700':
                                    event.request_method === 'PUT',
                                  'jwt-bg-red-100 jwt-text-red-700':
                                    event.request_method === 'DELETE'
                                }
                              )}
                            >
                              {event.request_method}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4 jwt-max-w-[200px] jwt-truncate">
                            {event.request_path}
                          </TableCell>
                          <TableCell className="jwt-py-4">{event.ip_address}</TableCell>
                          <TableCell className="jwt-py-4">{event.timestamp}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                  {usageHistoryPagination.total_pages > 1 && (
                    <div className="jwt-mt-4">
                      <Pagination
                        page={usageHistoryPage}
                        totalPages={usageHistoryPagination.total_pages}
                        onPageChange={handleUsageHistoryPageChange}
                      />
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </PageWrapper>
  );
}

import { useEffect, useState, useCallback } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Ban, Trash2 } from 'lucide-react';

import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { Button } from '@/components/ui/button';
import { tokenAPI } from '@/services/api';
import type { TokenDetails, Token } from '@/types/token';
import type { TokenUsageEvent } from '@/types/api';
import { DateTimeCell } from '@/components/tokens/tokens-table';
import { useToast } from '@/components/hooks/use-toast';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow
} from '@/components/ui/table';
import { cn } from '@/lib/utils';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { SubHeading } from '@/components/ui/sub-heading';
import { Pagination } from '@/components/ui/pagination';

export default function TokenDetailsPage() {
  const [searchParams, setSearchParams] = useSearchParams();
  const tokenId = searchParams.get('tokenId');
  const [loading, setLoading] = useState(true);
  const [tokenDetails, setTokenDetails] = useState<TokenDetails | null>(null);
  const [relatedTokens, setRelatedTokens] = useState<Token[]>([]);
  const [usageHistory, setUsageHistory] = useState<TokenUsageEvent[]>([]);
  const [relatedTokensPage, setRelatedTokensPage] = useState(1);
  const [usageHistoryPage, setUsageHistoryPage] = useState(1);
  const [relatedTokensPagination, setRelatedTokensPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const [usageHistoryPagination, setUsageHistoryPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const { toast } = useToast();
  const navigate = useNavigate();
  const [openRevokeDialog, setOpenRevokeDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const perPage = 20;

  const fetchUsageHistory = useCallback(
    async (id: number, page: number) => {
      try {
        const usageData = await tokenAPI.getUsageHistory(id, page, perPage);
        setUsageHistory(usageData.items);
        setUsageHistoryPagination({
          total: usageData.pagination.total,
          total_pages: usageData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching usage history:', error);
        toast({
          title: 'Error',
          description: 'Failed to load usage history',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchRelatedTokens = useCallback(
    async (id: number, page: number) => {
      try {
        const relatedData = await tokenAPI.getRelatedTokens(id, page, perPage);
        setRelatedTokens(relatedData.items);
        setRelatedTokensPagination({
          total: relatedData.pagination.total,
          total_pages: relatedData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching related tokens:', error);
        toast({
          title: 'Error',
          description: 'Failed to load related tokens',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchTokenDetails = useCallback(
    async (id: number) => {
      try {
        const details = await tokenAPI.getDetails(id);
        setTokenDetails(details);

        if (details.has_related_tokens) {
          await fetchRelatedTokens(id, 1);
        }

        await fetchUsageHistory(id, 1);
      } catch (error) {
        console.error('Error fetching token details:', error);
        toast({
          title: 'Error',
          description: 'Failed to load token data',
          variant: 'destructive'
        });
      }
    },
    [toast, fetchRelatedTokens, fetchUsageHistory]
  );

  useEffect(() => {
    if (!tokenId) return;

    setLoading(true);
    fetchTokenDetails(parseInt(tokenId, 10)).finally(() => setLoading(false));
  }, [tokenId, fetchTokenDetails]);

  useEffect(() => {
    if (!tokenId || !tokenDetails?.has_related_tokens) return;

    fetchRelatedTokens(parseInt(tokenId, 10), relatedTokensPage);
  }, [tokenId, relatedTokensPage, tokenDetails?.has_related_tokens, fetchRelatedTokens]);

  useEffect(() => {
    if (!tokenId) return;

    fetchUsageHistory(parseInt(tokenId, 10), usageHistoryPage);
  }, [tokenId, usageHistoryPage, fetchUsageHistory]);

  const handleBack = () => {
    const newParams = new URLSearchParams(searchParams);
    newParams.delete('tokenId');
    setSearchParams(newParams);
  };

  const handleTokenSelect = (selectedTokenId: string | number) => {
    const newParams = new URLSearchParams(searchParams);
    newParams.set('tokenId', selectedTokenId.toString());
    setSearchParams(newParams);
    // Reset pagination when selecting a new token
    setRelatedTokensPage(1);
    setUsageHistoryPage(1);
  };

  const handleRevoke = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.revoke(parseInt(tokenId, 10));
      const details = await tokenAPI.getDetails(parseInt(tokenId, 10));
      setTokenDetails(details);
      setOpenRevokeDialog(false);
      toast({
        title: 'Token Revoked',
        description: 'The token has been successfully revoked.'
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to revoke the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleDelete = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.delete(parseInt(tokenId, 10));
      setOpenDeleteDialog(false);
      toast({
        title: 'Token Deleted',
        description: 'The token has been successfully deleted.'
      });
      const newParams = new URLSearchParams(searchParams);
      newParams.delete('tokenId');
      navigate(`/tokens?${newParams.toString()}`);
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to delete the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleRelatedTokensPageChange = (newPage: number) => {
    setRelatedTokensPage(newPage);
  };

  const handleUsageHistoryPageChange = (newPage: number) => {
    setUsageHistoryPage(newPage);
  };

  if (loading) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Loading...</div>
      </PageWrapper>
    );
  }

  if (!tokenDetails) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Token not found</div>
      </PageWrapper>
    );
  }

  const { token } = tokenDetails;

  const FieldType = (props: { label: string; children: React.ReactNode }) => {
    return (
      <div className="jwt-space-y-1">
        <h4 className="jwt-text-sm jwt-font-medium jwt-text-gray-600">
          {props.label}
        </h4>
        <div className="jwt-text-sm jwt-text-gray-900">{props.children}</div>
      </div>
    );
  };

  return (
    <PageWrapper>
      <Header
        title={
          <div className="jwt-flex jwt-items-center jwt-gap-3">
            <Button
              variant="outline"
              size="sm"
              onClick={handleBack}
              className="jwt-h-8 jwt-w-8 jwt-p-0"
            >
              <ArrowLeft className="jwt-h-4 jwt-w-4" />
            </Button>
            <span>Token Details</span>
          </div>
        }
        description="Detailed information about the selected token."
      >
        <div className="jwt-flex jwt-flex-col sm:jwt-flex-row jwt-gap-2">
          {tokenDetails?.token.status === 'Active' && (
            <Dialog open={openRevokeDialog} onOpenChange={setOpenRevokeDialog}>
              <DialogTrigger asChild>
                <Button variant="outline" className="jwt-w-full sm:jwt-w-auto">
                  <Ban className="jwt-h-4 jwt-w-4" />
                  Revoke Token
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Revoke Token</DialogTitle>
                  <DialogDescription>
                    Are you sure you want to revoke this token? This action will prevent the token
                    from being used for authentication.
                  </DialogDescription>
                </DialogHeader>
                <DialogFooter className="sm:jwt-justify-end">
                  <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                    <Button variant="outline" onClick={() => setOpenRevokeDialog(false)}>
                      Cancel
                    </Button>
                    <Button variant="outline" onClick={handleRevoke}>
                      Revoke
                    </Button>
                  </div>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          )}

          <Dialog open={openDeleteDialog} onOpenChange={setOpenDeleteDialog}>
            <DialogTrigger asChild>
              <Button variant="destructive" className="jwt-w-full sm:jwt-w-auto">
                <Trash2 className="jwt-h-4 jwt-w-4" />
                Delete Token
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Delete Token</DialogTitle>
                <DialogDescription>
                  Are you sure you want to delete this token?
                  <br />
                  When deleting a token the whole tokens family is deleted as well.
                  <br />
                  <br />
                  <strong>This action cannot be undone.</strong>
                </DialogDescription>
              </DialogHeader>
              <DialogFooter className="sm:jwt-justify-end">
                <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                  <Button variant="outline" onClick={() => setOpenDeleteDialog(false)}>
                    Cancel
                  </Button>
                  <Button variant="destructive" onClick={handleDelete}>
                    Delete
                  </Button>
                </div>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </Header>

      <div className="jwt-space-y-8">
        {/* User Information */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-4">
          <SubHeading title="User Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-4">
              <FieldType label="Username">
                {token.user_link ? (
                  <a href={token.user_link} className="jwt-text-primary hover:jwt-underline">
                    {token.user_display_name}
                  </a>
                ) : (
                  token.user_nicename
                )}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User ID">{token.user_id}</FieldType>
            </div>
          </div>
        </div>

        {/* Token Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Token Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Status">
                <span
                  className={cn(
                    'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                    'jwt-font-medium jwt-border jwt-text-xs',
                    {
                      'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                        token.status === 'Active',
                      'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                        token.status === 'Revoked',
                      'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200':
                        token.status === 'Expired'
                    }
                  )}
                >
                  {token.status}
                </span>
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Family">{token.token_family}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Hash">{token.hash}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Hash">{token.refresh_token_hash}</FieldType>
            </div>
          </div>
        </div>

        {/* Dates */}
        <div className="jwt-space-y-4">
          <SubHeading title="Dates" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Created">
                <DateTimeCell value={token.issued_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Expires">
                <DateTimeCell value={token.expires_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Last Used">
                {token.last_used_at ? <DateTimeCell value={token.last_used_at} /> : <p>Never</p>}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Expires">
                <DateTimeCell value={token.refresh_token_expires_at} />
              </FieldType>
            </div>
            {token.revoked_at && (
              <div className="jwt-space-y-2">
                <FieldType label="Revoked">
                  <DateTimeCell value={token.revoked_at} />
                </FieldType>
              </div>
            )}
          </div>
        </div>

        {/* Client Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Client Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="IP Address">{token.ip_address}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User Agent">{token.user_agent}</FieldType>
            </div>
          </div>
        </div>

        {/* Related Tokens */}
        {tokenDetails.has_related_tokens && relatedTokens.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Related Tokens</CardTitle>
              <CardDescription>Other tokens from the same token family</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-overflow-x-auto">
                <Table>
                  <TableHeader className="jwt-bg-gray-50">
                    <TableRow className="hover:jwt-bg-transparent">
                      <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Created</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Expires</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Last Used</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">IP Address</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {relatedTokens.map((relatedToken) => {
                      const badgeStyles = {
                        Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
                        Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
                        Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
                      };

                      return (
                        <TableRow
                          key={relatedToken.id}
                          className="hover:jwt-bg-muted/50 jwt-cursor-pointer"
                          onClick={() => handleTokenSelect(relatedToken.id)}
                        >
                          <TableCell className="jwt-py-5">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                badgeStyles[relatedToken.status as keyof typeof badgeStyles]
                              )}
                            >
                              {relatedToken.status}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.issued_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.expires_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            {relatedToken.last_used_at ? (
                              <DateTimeCell value={relatedToken.last_used_at} />
                            ) : (
                              'Never'
                            )}
                          </TableCell>
                          <TableCell className="jwt-py-5">{relatedToken.ip_address}</TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                {relatedTokensPagination.total_pages > 1 && (
                  <div className="jwt-mt-4">
                    <Pagination
                      page={relatedTokensPage}
                      totalPages={relatedTokensPagination.total_pages}
                      onPageChange={handleRelatedTokensPageChange}
                    />
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Usage History */}
        {usageHistory.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Usage History</CardTitle>
              <CardDescription>
                All the events that have been logged for this token.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-space-y-4">
                <div className="jwt-overflow-x-auto">
                  <Table>
                    <TableHeader className="jwt-bg-gray-50">
                      <TableRow className="hover:jwt-bg-transparent">
                        <TableHead className="jwt-text-xs jwt-font-medium">Event Type</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Method</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Path</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">IP</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Date</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {usageHistory.map((event) => (
                        <TableRow key={event.id}>
                          <TableCell className="jwt-py-4">
                            <span className="jwt-capitalize">
                              {event.event_type.replace(/_/g, ' ')}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                {
                                  'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                                    event.event_status === 1,
                                  'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                                    event.event_status === 2
                                }
                              )}
                            >
                              {event.event_status === 1 ? 'Success' : 'Failed'}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded jwt-px-2 jwt-py-1',
                                'jwt-text-xs jwt-font-medium',
                                {
                                  'jwt-bg-blue-100 jwt-text-blue-700':
                                    event.request_method === 'GET',
                                  'jwt-bg-green-100 jwt-text-green-700':
                                    event.request_method === 'POST',
                                  'jwt-bg-yellow-100 jwt-text-yellow-700':
                                    event.request_method === 'PUT',
                                  'jwt-bg-red-100 jwt-text-red-700':
                                    event.request_method === 'DELETE'
                                }
                              )}
                            >
                              {event.request_method}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4 jwt-max-w-[200px] jwt-truncate">
                            {event.request_path}
                          </TableCell>
                          <TableCell className="jwt-py-4">{event.ip_address}</TableCell>
                          <TableCell className="jwt-py-4">{event.timestamp}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                  {usageHistoryPagination.total_pages > 1 && (
                    <div className="jwt-mt-4">
                      <Pagination
                        page={usageHistoryPage}
                        totalPages={usageHistoryPagination.total_pages}
                        onPageChange={handleUsageHistoryPageChange}
                      />
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </PageWrapper>
  );
}

import { useEffect, useState, useCallback } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Ban, Trash2 } from 'lucide-react';

import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { Button } from '@/components/ui/button';
import { tokenAPI } from '@/services/api';
import type { TokenDetails, Token } from '@/types/token';
import type { TokenUsageEvent } from '@/types/api';
import { DateTimeCell } from '@/components/tokens/tokens-table';
import { useToast } from '@/components/hooks/use-toast';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow
} from '@/components/ui/table';
import { cn } from '@/lib/utils';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { SubHeading } from '@/components/ui/sub-heading';
import { Pagination } from '@/components/ui/pagination';

export default function TokenDetailsPage() {
  const [searchParams, setSearchParams] = useSearchParams();
  const tokenId = searchParams.get('tokenId');
  const [loading, setLoading] = useState(true);
  const [tokenDetails, setTokenDetails] = useState<TokenDetails | null>(null);
  const [relatedTokens, setRelatedTokens] = useState<Token[]>([]);
  const [usageHistory, setUsageHistory] = useState<TokenUsageEvent[]>([]);
  const [relatedTokensPage, setRelatedTokensPage] = useState(1);
  const [usageHistoryPage, setUsageHistoryPage] = useState(1);
  const [relatedTokensPagination, setRelatedTokensPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const [usageHistoryPagination, setUsageHistoryPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const { toast } = useToast();
  const navigate = useNavigate();
  const [openRevokeDialog, setOpenRevokeDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const perPage = 20;

  const fetchUsageHistory = useCallback(
    async (id: number, page: number) => {
      try {
        const usageData = await tokenAPI.getUsageHistory(id, page, perPage);
        setUsageHistory(usageData.items);
        setUsageHistoryPagination({
          total: usageData.pagination.total,
          total_pages: usageData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching usage history:', error);
        toast({
          title: 'Error',
          description: 'Failed to load usage history',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchRelatedTokens = useCallback(
    async (id: number, page: number) => {
      try {
        const relatedData = await tokenAPI.getRelatedTokens(id, page, perPage);
        setRelatedTokens(relatedData.items);
        setRelatedTokensPagination({
          total: relatedData.pagination.total,
          total_pages: relatedData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching related tokens:', error);
        toast({
          title: 'Error',
          description: 'Failed to load related tokens',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchTokenDetails = useCallback(
    async (id: number) => {
      try {
        const details = await tokenAPI.getDetails(id);
        setTokenDetails(details);

        if (details.has_related_tokens) {
          await fetchRelatedTokens(id, 1);
        }

        await fetchUsageHistory(id, 1);
      } catch (error) {
        console.error('Error fetching token details:', error);
        toast({
          title: 'Error',
          description: 'Failed to load token data',
          variant: 'destructive'
        });
      }
    },
    [toast, fetchRelatedTokens, fetchUsageHistory]
  );

  useEffect(() => {
    if (!tokenId) return;

    setLoading(true);
    fetchTokenDetails(parseInt(tokenId, 10)).finally(() => setLoading(false));
  }, [tokenId, fetchTokenDetails]);

  useEffect(() => {
    if (!tokenId || !tokenDetails?.has_related_tokens) return;

    fetchRelatedTokens(parseInt(tokenId, 10), relatedTokensPage);
  }, [tokenId, relatedTokensPage, tokenDetails?.has_related_tokens, fetchRelatedTokens]);

  useEffect(() => {
    if (!tokenId) return;

    fetchUsageHistory(parseInt(tokenId, 10), usageHistoryPage);
  }, [tokenId, usageHistoryPage, fetchUsageHistory]);

  const handleBack = () => {
    const newParams = new URLSearchParams(searchParams);
    newParams.delete('tokenId');
    setSearchParams(newParams);
  };

  const handleTokenSelect = (selectedTokenId: string | number) => {
    const newParams = new URLSearchParams(searchParams);
    newParams.set('tokenId', selectedTokenId.toString());
    setSearchParams(newParams);
    // Reset pagination when selecting a new token
    setRelatedTokensPage(1);
    setUsageHistoryPage(1);
  };

  const handleRevoke = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.revoke(parseInt(tokenId, 10));
      const details = await tokenAPI.getDetails(parseInt(tokenId, 10));
      setTokenDetails(details);
      setOpenRevokeDialog(false);
      toast({
        title: 'Token Revoked',
        description: 'The token has been successfully revoked.'
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to revoke the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleDelete = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.delete(parseInt(tokenId, 10));
      setOpenDeleteDialog(false);
      toast({
        title: 'Token Deleted',
        description: 'The token has been successfully deleted.'
      });
      const newParams = new URLSearchParams(searchParams);
      newParams.delete('tokenId');
      navigate(`/tokens?${newParams.toString()}`);
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to delete the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleRelatedTokensPageChange = (newPage: number) => {
    setRelatedTokensPage(newPage);
  };

  const handleUsageHistoryPageChange = (newPage: number) => {
    setUsageHistoryPage(newPage);
  };

  if (loading) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Loading...</div>
      </PageWrapper>
    );
  }

  if (!tokenDetails) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Token not found</div>
      </PageWrapper>
    );
  }

  const { token } = tokenDetails;

  const FieldType = (props: { label: string; children: React.ReactNode }) => {
    return (
      <div className="jwt-space-y-1">
        <h4 className="jwt-text-sm jwt-font-medium jwt-text-gray-600">
          {props.label}
        </h4>
        <div className="jwt-text-sm jwt-text-gray-900">{props.children}</div>
      </div>
    );
  };

  return (
    <PageWrapper>
      <Header
        title={
          <div className="jwt-flex jwt-items-center jwt-gap-3">
            <Button
              variant="outline"
              size="sm"
              onClick={handleBack}
              className="jwt-h-8 jwt-w-8 jwt-p-0"
            >
              <ArrowLeft className="jwt-h-4 jwt-w-4" />
            </Button>
            <span>Token Details</span>
          </div>
        }
        description="Detailed information about the selected token."
      >
        <div className="jwt-flex jwt-flex-col sm:jwt-flex-row jwt-gap-2">
          {tokenDetails?.token.status === 'Active' && (
            <Dialog open={openRevokeDialog} onOpenChange={setOpenRevokeDialog}>
              <DialogTrigger asChild>
                <Button variant="outline" className="jwt-w-full sm:jwt-w-auto">
                  <Ban className="jwt-h-4 jwt-w-4" />
                  Revoke Token
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Revoke Token</DialogTitle>
                  <DialogDescription>
                    Are you sure you want to revoke this token? This action will prevent the token
                    from being used for authentication.
                  </DialogDescription>
                </DialogHeader>
                <DialogFooter className="sm:jwt-justify-end">
                  <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                    <Button variant="outline" onClick={() => setOpenRevokeDialog(false)}>
                      Cancel
                    </Button>
                    <Button variant="outline" onClick={handleRevoke}>
                      Revoke
                    </Button>
                  </div>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          )}

          <Dialog open={openDeleteDialog} onOpenChange={setOpenDeleteDialog}>
            <DialogTrigger asChild>
              <Button variant="destructive" className="jwt-w-full sm:jwt-w-auto">
                <Trash2 className="jwt-h-4 jwt-w-4" />
                Delete Token
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Delete Token</DialogTitle>
                <DialogDescription>
                  Are you sure you want to delete this token?
                  <br />
                  When deleting a token the whole tokens family is deleted as well.
                  <br />
                  <br />
                  <strong>This action cannot be undone.</strong>
                </DialogDescription>
              </DialogHeader>
              <DialogFooter className="sm:jwt-justify-end">
                <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                  <Button variant="outline" onClick={() => setOpenDeleteDialog(false)}>
                    Cancel
                  </Button>
                  <Button variant="destructive" onClick={handleDelete}>
                    Delete
                  </Button>
                </div>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </Header>

      <div className="jwt-space-y-8">
        {/* User Information */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-4">
          <SubHeading title="User Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-4">
              <FieldType label="Username">
                {token.user_link ? (
                  <a href={token.user_link} className="jwt-text-primary hover:jwt-underline">
                    {token.user_display_name}
                  </a>
                ) : (
                  token.user_nicename
                )}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User ID">{token.user_id}</FieldType>
            </div>
          </div>
        </div>

        {/* Token Information */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-4">
          <SubHeading title="Token Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Status">
                <span
                  className={cn(
                    'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                    'jwt-font-medium jwt-border jwt-text-xs',
                    {
                      'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                        token.status === 'Active',
                      'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                        token.status === 'Revoked',
                      'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200':
                        token.status === 'Expired'
                    }
                  )}
                >
                  {token.status}
                </span>
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Family">{token.token_family}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Hash">{token.hash}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Hash">{token.refresh_token_hash}</FieldType>
            </div>
          </div>
        </div>

        {/* Dates */}
        <div className="jwt-space-y-4">
          <SubHeading title="Dates" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Created">
                <DateTimeCell value={token.issued_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Expires">
                <DateTimeCell value={token.expires_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Last Used">
                {token.last_used_at ? <DateTimeCell value={token.last_used_at} /> : <p>Never</p>}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Expires">
                <DateTimeCell value={token.refresh_token_expires_at} />
              </FieldType>
            </div>
            {token.revoked_at && (
              <div className="jwt-space-y-2">
                <FieldType label="Revoked">
                  <DateTimeCell value={token.revoked_at} />
                </FieldType>
              </div>
            )}
          </div>
        </div>

        {/* Client Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Client Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="IP Address">{token.ip_address}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User Agent">{token.user_agent}</FieldType>
            </div>
          </div>
        </div>

        {/* Related Tokens */}
        {tokenDetails.has_related_tokens && relatedTokens.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Related Tokens</CardTitle>
              <CardDescription>Other tokens from the same token family</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-overflow-x-auto">
                <Table>
                  <TableHeader className="jwt-bg-gray-50">
                    <TableRow className="hover:jwt-bg-transparent">
                      <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Created</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Expires</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Last Used</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">IP Address</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {relatedTokens.map((relatedToken) => {
                      const badgeStyles = {
                        Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
                        Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
                        Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
                      };

                      return (
                        <TableRow
                          key={relatedToken.id}
                          className="hover:jwt-bg-muted/50 jwt-cursor-pointer"
                          onClick={() => handleTokenSelect(relatedToken.id)}
                        >
                          <TableCell className="jwt-py-5">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                badgeStyles[relatedToken.status as keyof typeof badgeStyles]
                              )}
                            >
                              {relatedToken.status}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.issued_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.expires_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            {relatedToken.last_used_at ? (
                              <DateTimeCell value={relatedToken.last_used_at} />
                            ) : (
                              'Never'
                            )}
                          </TableCell>
                          <TableCell className="jwt-py-5">{relatedToken.ip_address}</TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                {relatedTokensPagination.total_pages > 1 && (
                  <div className="jwt-mt-4">
                    <Pagination
                      page={relatedTokensPage}
                      totalPages={relatedTokensPagination.total_pages}
                      onPageChange={handleRelatedTokensPageChange}
                    />
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Usage History */}
        {usageHistory.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Usage History</CardTitle>
              <CardDescription>
                All the events that have been logged for this token.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-space-y-4">
                <div className="jwt-overflow-x-auto">
                  <Table>
                    <TableHeader className="jwt-bg-gray-50">
                      <TableRow className="hover:jwt-bg-transparent">
                        <TableHead className="jwt-text-xs jwt-font-medium">Event Type</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Method</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Path</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">IP</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Date</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {usageHistory.map((event) => (
                        <TableRow key={event.id}>
                          <TableCell className="jwt-py-4">
                            <span className="jwt-capitalize">
                              {event.event_type.replace(/_/g, ' ')}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                {
                                  'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                                    event.event_status === 1,
                                  'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                                    event.event_status === 2
                                }
                              )}
                            >
                              {event.event_status === 1 ? 'Success' : 'Failed'}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded jwt-px-2 jwt-py-1',
                                'jwt-text-xs jwt-font-medium',
                                {
                                  'jwt-bg-blue-100 jwt-text-blue-700':
                                    event.request_method === 'GET',
                                  'jwt-bg-green-100 jwt-text-green-700':
                                    event.request_method === 'POST',
                                  'jwt-bg-yellow-100 jwt-text-yellow-700':
                                    event.request_method === 'PUT',
                                  'jwt-bg-red-100 jwt-text-red-700':
                                    event.request_method === 'DELETE'
                                }
                              )}
                            >
                              {event.request_method}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4 jwt-max-w-[200px] jwt-truncate">
                            {event.request_path}
                          </TableCell>
                          <TableCell className="jwt-py-4">{event.ip_address}</TableCell>
                          <TableCell className="jwt-py-4">{event.timestamp}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                  {usageHistoryPagination.total_pages > 1 && (
                    <div className="jwt-mt-4">
                      <Pagination
                        page={usageHistoryPage}
                        totalPages={usageHistoryPagination.total_pages}
                        onPageChange={handleUsageHistoryPageChange}
                      />
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </PageWrapper>
  );
}

import { useEffect, useState, useCallback } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Ban, Trash2 } from 'lucide-react';

import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { Button } from '@/components/ui/button';
import { tokenAPI } from '@/services/api';
import type { TokenDetails, Token } from '@/types/token';
import type { TokenUsageEvent } from '@/types/api';
import { DateTimeCell } from '@/components/tokens/tokens-table';
import { useToast } from '@/components/hooks/use-toast';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger
} from '@/components/ui/dialog';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow
} from '@/components/ui/table';
import { cn } from '@/lib/utils';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { SubHeading } from '@/components/ui/sub-heading';
import { Pagination } from '@/components/ui/pagination';

export default function TokenDetailsPage() {
  const [searchParams, setSearchParams] = useSearchParams();
  const tokenId = searchParams.get('tokenId');
  const [loading, setLoading] = useState(true);
  const [tokenDetails, setTokenDetails] = useState<TokenDetails | null>(null);
  const [relatedTokens, setRelatedTokens] = useState<Token[]>([]);
  const [usageHistory, setUsageHistory] = useState<TokenUsageEvent[]>([]);
  const [relatedTokensPage, setRelatedTokensPage] = useState(1);
  const [usageHistoryPage, setUsageHistoryPage] = useState(1);
  const [relatedTokensPagination, setRelatedTokensPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const [usageHistoryPagination, setUsageHistoryPagination] = useState({
    total: 0,
    total_pages: 0
  });
  const { toast } = useToast();
  const navigate = useNavigate();
  const [openRevokeDialog, setOpenRevokeDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const perPage = 20;

  const fetchUsageHistory = useCallback(
    async (id: number, page: number) => {
      try {
        const usageData = await tokenAPI.getUsageHistory(id, page, perPage);
        setUsageHistory(usageData.items);
        setUsageHistoryPagination({
          total: usageData.pagination.total,
          total_pages: usageData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching usage history:', error);
        toast({
          title: 'Error',
          description: 'Failed to load usage history',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchRelatedTokens = useCallback(
    async (id: number, page: number) => {
      try {
        const relatedData = await tokenAPI.getRelatedTokens(id, page, perPage);
        setRelatedTokens(relatedData.items);
        setRelatedTokensPagination({
          total: relatedData.pagination.total,
          total_pages: relatedData.pagination.total_pages
        });
      } catch (error) {
        console.error('Error fetching related tokens:', error);
        toast({
          title: 'Error',
          description: 'Failed to load related tokens',
          variant: 'destructive'
        });
      }
    },
    [perPage, toast]
  );

  const fetchTokenDetails = useCallback(
    async (id: number) => {
      try {
        const details = await tokenAPI.getDetails(id);
        setTokenDetails(details);

        if (details.has_related_tokens) {
          await fetchRelatedTokens(id, 1);
        }

        await fetchUsageHistory(id, 1);
      } catch (error) {
        console.error('Error fetching token details:', error);
        toast({
          title: 'Error',
          description: 'Failed to load token data',
          variant: 'destructive'
        });
      }
    },
    [toast, fetchRelatedTokens, fetchUsageHistory]
  );

  useEffect(() => {
    if (!tokenId) return;

    setLoading(true);
    fetchTokenDetails(parseInt(tokenId, 10)).finally(() => setLoading(false));
  }, [tokenId, fetchTokenDetails]);

  useEffect(() => {
    if (!tokenId || !tokenDetails?.has_related_tokens) return;

    fetchRelatedTokens(parseInt(tokenId, 10), relatedTokensPage);
  }, [tokenId, relatedTokensPage, tokenDetails?.has_related_tokens, fetchRelatedTokens]);

  useEffect(() => {
    if (!tokenId) return;

    fetchUsageHistory(parseInt(tokenId, 10), usageHistoryPage);
  }, [tokenId, usageHistoryPage, fetchUsageHistory]);

  const handleBack = () => {
    const newParams = new URLSearchParams(searchParams);
    newParams.delete('tokenId');
    setSearchParams(newParams);
  };

  const handleTokenSelect = (selectedTokenId: string | number) => {
    const newParams = new URLSearchParams(searchParams);
    newParams.set('tokenId', selectedTokenId.toString());
    setSearchParams(newParams);
    // Reset pagination when selecting a new token
    setRelatedTokensPage(1);
    setUsageHistoryPage(1);
  };

  const handleRevoke = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.revoke(parseInt(tokenId, 10));
      const details = await tokenAPI.getDetails(parseInt(tokenId, 10));
      setTokenDetails(details);
      setOpenRevokeDialog(false);
      toast({
        title: 'Token Revoked',
        description: 'The token has been successfully revoked.'
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to revoke the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleDelete = async () => {
    if (!tokenId) return;

    try {
      await tokenAPI.delete(parseInt(tokenId, 10));
      setOpenDeleteDialog(false);
      toast({
        title: 'Token Deleted',
        description: 'The token has been successfully deleted.'
      });
      const newParams = new URLSearchParams(searchParams);
      newParams.delete('tokenId');
      navigate(`/tokens?${newParams.toString()}`);
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to delete the token. Please try again.',
        variant: 'destructive'
      });
    }
  };

  const handleRelatedTokensPageChange = (newPage: number) => {
    setRelatedTokensPage(newPage);
  };

  const handleUsageHistoryPageChange = (newPage: number) => {
    setUsageHistoryPage(newPage);
  };

  if (loading) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Loading...</div>
      </PageWrapper>
    );
  }

  if (!tokenDetails) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Token not found</div>
      </PageWrapper>
    );
  }

  const { token } = tokenDetails;

  const FieldType = (props: { label: string; children: React.ReactNode }) => {
    return (
      <div className="jwt-space-y-1">
        <h4 className="jwt-text-sm jwt-font-medium jwt-text-gray-600">
          {props.label}
        </h4>
        <div className="jwt-text-sm jwt-text-gray-900">{props.children}</div>
      </div>
    );
  };

  return (
    <PageWrapper>
      <Header
        title={
          <div className="jwt-flex jwt-items-center jwt-gap-3">
            <Button
              variant="outline"
              size="sm"
              onClick={handleBack}
              className="jwt-h-8 jwt-w-8 jwt-p-0"
            >
              <ArrowLeft className="jwt-h-4 jwt-w-4" />
            </Button>
            <span>Token Details</span>
          </div>
        }
        description="Detailed information about the selected token."
      >
        <div className="jwt-flex jwt-flex-col sm:jwt-flex-row jwt-gap-2">
          {tokenDetails?.token.status === 'Active' && (
            <Dialog open={openRevokeDialog} onOpenChange={setOpenRevokeDialog}>
              <DialogTrigger asChild>
                <Button variant="outline" className="jwt-w-full sm:jwt-w-auto">
                  <Ban className="jwt-h-4 jwt-w-4" />
                  Revoke Token
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Revoke Token</DialogTitle>
                  <DialogDescription>
                    Are you sure you want to revoke this token? This action will prevent the token
                    from being used for authentication.
                  </DialogDescription>
                </DialogHeader>
                <DialogFooter className="sm:jwt-justify-end">
                  <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                    <Button variant="outline" onClick={() => setOpenRevokeDialog(false)}>
                      Cancel
                    </Button>
                    <Button variant="outline" onClick={handleRevoke}>
                      Revoke
                    </Button>
                  </div>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          )}

          <Dialog open={openDeleteDialog} onOpenChange={setOpenDeleteDialog}>
            <DialogTrigger asChild>
              <Button variant="destructive" className="jwt-w-full sm:jwt-w-auto">
                <Trash2 className="jwt-h-4 jwt-w-4" />
                Delete Token
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Delete Token</DialogTitle>
                <DialogDescription>
                  Are you sure you want to delete this token?
                  <br />
                  When deleting a token the whole tokens family is deleted as well.
                  <br />
                  <br />
                  <strong>This action cannot be undone.</strong>
                </DialogDescription>
              </DialogHeader>
              <DialogFooter className="sm:jwt-justify-end">
                <div className="jwt-flex jwt-flex-col-reverse sm:jwt-flex-row jwt-gap-2">
                  <Button variant="outline" onClick={() => setOpenDeleteDialog(false)}>
                    Cancel
                  </Button>
                  <Button variant="destructive" onClick={handleDelete}>
                    Delete
                  </Button>
                </div>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </Header>

      <div className="jwt-space-y-8">
        {/* User Information */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-4">
          <SubHeading title="User Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-4">
              <FieldType label="Username">
                {token.user_link ? (
                  <a href={token.user_link} className="jwt-text-primary hover:jwt-underline">
                    {token.user_display_name}
                  </a>
                ) : (
                  token.user_nicename
                )}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User ID">{token.user_id}</FieldType>
            </div>
          </div>
        </div>

        {/* Token Information */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-4">
          <SubHeading title="Token Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Status">
                <span
                  className={cn(
                    'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                    'jwt-font-medium jwt-border jwt-text-xs',
                    {
                      'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                        token.status === 'Active',
                      'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                        token.status === 'Revoked',
                      'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200':
                        token.status === 'Expired'
                    }
                  )}
                >
                  {token.status}
                </span>
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Family">{token.token_family}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Token Hash">{token.hash}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Hash">{token.refresh_token_hash}</FieldType>
            </div>
          </div>
        </div>

        {/* Dates */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-4">
          <SubHeading title="Dates" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="Created">
                <DateTimeCell value={token.issued_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Expires">
                <DateTimeCell value={token.expires_at} />
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Last Used">
                {token.last_used_at ? <DateTimeCell value={token.last_used_at} /> : <p>Never</p>}
              </FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="Refresh Token Expires">
                <DateTimeCell value={token.refresh_token_expires_at} />
              </FieldType>
            </div>
            {token.revoked_at && (
              <div className="jwt-space-y-2">
                <FieldType label="Revoked">
                  <DateTimeCell value={token.revoked_at} />
                </FieldType>
              </div>
            )}
          </div>
        </div>

        {/* Client Information */}
        <div className="jwt-space-y-4">
          <SubHeading title="Client Information" />
          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
            <div className="jwt-space-y-2">
              <FieldType label="IP Address">{token.ip_address}</FieldType>
            </div>
            <div className="jwt-space-y-2">
              <FieldType label="User Agent">{token.user_agent}</FieldType>
            </div>
          </div>
        </div>

        {/* Related Tokens */}
        {tokenDetails.has_related_tokens && relatedTokens.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Related Tokens</CardTitle>
              <CardDescription>Other tokens from the same token family</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-overflow-x-auto">
                <Table>
                  <TableHeader className="jwt-bg-gray-50">
                    <TableRow className="hover:jwt-bg-transparent">
                      <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Created</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Expires</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">Last Used</TableHead>
                      <TableHead className="jwt-text-xs jwt-font-medium">IP Address</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {relatedTokens.map((relatedToken) => {
                      const badgeStyles = {
                        Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
                        Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
                        Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
                      };

                      return (
                        <TableRow
                          key={relatedToken.id}
                          className="hover:jwt-bg-muted/50 jwt-cursor-pointer"
                          onClick={() => handleTokenSelect(relatedToken.id)}
                        >
                          <TableCell className="jwt-py-5">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                badgeStyles[relatedToken.status as keyof typeof badgeStyles]
                              )}
                            >
                              {relatedToken.status}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.issued_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            <DateTimeCell value={relatedToken.expires_at} />
                          </TableCell>
                          <TableCell className="jwt-py-5">
                            {relatedToken.last_used_at ? (
                              <DateTimeCell value={relatedToken.last_used_at} />
                            ) : (
                              'Never'
                            )}
                          </TableCell>
                          <TableCell className="jwt-py-5">{relatedToken.ip_address}</TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
                {relatedTokensPagination.total_pages > 1 && (
                  <div className="jwt-mt-4">
                    <Pagination
                      page={relatedTokensPage}
                      totalPages={relatedTokensPagination.total_pages}
                      onPageChange={handleRelatedTokensPageChange}
                    />
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Usage History */}
        {usageHistory.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Usage History</CardTitle>
              <CardDescription>
                All the events that have been logged for this token.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="jwt-space-y-4">
                <div className="jwt-overflow-x-auto">
                  <Table>
                    <TableHeader className="jwt-bg-gray-50">
                      <TableRow className="hover:jwt-bg-transparent">
                        <TableHead className="jwt-text-xs jwt-font-medium">Event Type</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Method</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Path</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">IP</TableHead>
                        <TableHead className="jwt-text-xs jwt-font-medium">Date</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {usageHistory.map((event) => (
                        <TableRow key={event.id}>
                          <TableCell className="jwt-py-4">
                            <span className="jwt-capitalize">
                              {event.event_type.replace(/_/g, ' ')}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
                                'jwt-text-xs jwt-font-medium jwt-border',
                                {
                                  'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
                                    event.event_status === 1,
                                  'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
                                    event.event_status === 2
                                }
                              )}
                            >
                              {event.event_status === 1 ? 'Success' : 'Failed'}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4">
                            <span
                              className={cn(
                                'jwt-inline-flex jwt-items-center jwt-rounded jwt-px-2 jwt-py-1',
                                'jwt-text-xs jwt-font-medium',
                                {
                                  'jwt-bg-blue-100 jwt-text-blue-700':
                                    event.request_method === 'GET',
                                  'jwt-bg-green-100 jwt-text-green-700':
                                    event.request_method === 'POST',
                                  'jwt-bg-yellow-100 jwt-text-yellow-700':
                                    event.request_method === 'PUT',
                                  'jwt-bg-red-100 jwt-text-red-700':
                                    event.request_method === 'DELETE'
                                }
                              )}
                            >
                              {event.request_method}
                            </span>
                          </TableCell>
                          <TableCell className="jwt-py-4 jwt-max-w-[200px] jwt-truncate">
                            {event.request_path}
                          </TableCell>
                          <TableCell className="jwt-py-4">{event.ip_address}</TableCell>
                          <TableCell className="jwt-py-4">{event.timestamp}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                  {usageHistoryPagination.total_pages > 1 && (
                    <div className="jwt-mt-4">
                      <Pagination
                        page={usageHistoryPage}
                        totalPages={usageHistoryPagination.total_pages}
                        onPageChange={handleUsageHistoryPageChange}
                      />
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </PageWrapper>
  );
}

