import { cn } from '@/components/lib/utils';
import { HomeIcon, KeyRoundIcon, SettingsIcon, ServerIcon } from 'lucide-react';
import { useTab } from '@/App';
import { Logo } from './logo';

const navigationItems = [
  {
    title: 'Home',
    tab: 'home',
    icon: HomeIcon
  },
  {
    title: 'Tokens',
    tab: 'tokens',
    icon: KeyRoundIcon
  },
  {
    title: 'Settings',
    tab: 'settings',
    icon: SettingsIcon
  },
  {
    title: 'System',
    tab: 'system',
    icon: ServerIcon
  }
];

export function AppTopbar() {
  const context = useTab();
  const isWhitelabeled = window.wpApiSettings.is_whitelabeled;

  if (!context) return null;
  const { currentTab, setCurrentTab } = context;

  const handleClick = (e: React.MouseEvent<HTMLAnchorElement>, tab: string) => {
    e.preventDefault();
    setCurrentTab(tab);
    const newUrl = `${window.location.pathname}?page=jwt-auth-pro&tab=${tab}`;
    window.history.pushState({}, '', newUrl);
  };

  return (
    <header className="jwt-flex jwt-h-16 jwt-items-center jwt-border-b jwt-border-gray-200 jwt-bg-white jwt-px-4 md:jwt-px-10">
      <div className="jwt-flex jwt-w-full jwt-mx-auto jwt-items-center jwt-justify-between">
        {/* Logo and Title */}
        <div className="jwt-flex jwt-items-center jwt-gap-2">
          {!isWhitelabeled && <Logo />}
          {isWhitelabeled && (
            <span className="jwt-text-lg jwt-font-semibold">JWT Authentication Pro</span>
          )}
        </div>

        {/* Navigation */}
        <nav className="jwt-flex jwt-items-center jwt-gap-1">
          {navigationItems.map((item) => {
            const isActive = currentTab === item.tab;
            return (
              <a
                key={item.tab}
                href={`?page=jwt-auth-pro&tab=${item.tab}`}
                onClick={(e) => handleClick(e, item.tab)}
                className={cn(
                  'jwt-group jwt-flex jwt-items-center jwt-gap-x-2 jwt-rounded-lg jwt-px-4 jwt-py-2 jwt-text-sm jwt-font-medium jwt-text-gray-600 hover:jwt-bg-gray-100 hover:jwt-text-gray-900 jwt-transition-colors',
                  isActive && 'jwt-bg-gray-100 jwt-text-gray-900 jwt-border jwt-border-gray-200'
                )}
              >
                <item.icon className="jwt-h-4 jwt-w-4" aria-hidden="true" />
                <span>{item.title}</span>
              </a>
            );
          })}
        </nav>
      </div>
    </header>
  );
}

import * as React from 'react';
import { ColumnDef, flexRender, getCoreRowModel, useReactTable } from '@tanstack/react-table';
import { format } from 'date-fns';
import { cn } from '@/lib/utils';
import { MoreHorizontal } from 'lucide-react';
import { useSearchParams } from 'react-router-dom';

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow
} from '@/components/ui/table';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from '@/components/ui/select';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle
} from '@/components/ui/dialog';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger
} from '@/components/ui/dropdown-menu';
import { Button } from '@/components/ui/button';
import { useToast } from '@/components/hooks/use-toast';
import { Token } from '@/types/token';
import { tokenAPI } from '@/services/api';

interface TokensTableProps {
  filters: {
    status: string;
    username: string;
    creationDate: Date | undefined;
    expireDate: Date | undefined;
    revokedDate: Date | undefined;
  };
}

interface PaginationState {
  currentPage: number;
  totalPages: number;
  totalItems: number;
  pageSize: number;
}

const pageSizeOptions = [10, 25, 50, 100];

interface DateTimeCellProps {
  value: string | null;
}

export const DateTimeCell = ({ value }: DateTimeCellProps) => {
  if (!value) return '-';

  // Match time patterns like: 2:35 am, 2:35 PM, 14:35, etc.
  const timeRegex = /\d{1,2}:\d{2}(?:\s*[AaPp][Mm])?$/;
  const timeMatch = value.match(timeRegex);

  if (!timeMatch) return '-';

  const timeIndex = value.lastIndexOf(timeMatch[0]);
  const formatted = {
    date: value.substring(0, timeIndex).trim(),
    time: timeMatch[0]
  };

  return (
    <div className="jwt-flex jwt-flex-col">
      <span>{formatted.date}</span>
      <span className="jwt-text-muted-foreground jwt-text-sm">{formatted.time}</span>
    </div>
  );
};

export function TokensTable({ filters }: TokensTableProps) {
  const [searchParams, setSearchParams] = useSearchParams();
  const [data, setData] = React.useState<Token[]>([]);
  const [loading, setLoading] = React.useState(true);
  const [selectedToken, setSelectedToken] = React.useState<Token | null>(null);
  const [isRevokeDialogOpen, setIsRevokeDialogOpen] = React.useState(false);
  const { toast } = useToast();
  const [pagination, setPagination] = React.useState<PaginationState>({
    currentPage: 1,
    totalPages: 1,
    totalItems: 0,
    pageSize: 10
  });

  const handleViewDetails = (token: Token) => {
    // Preserve current filters and add tokenId
    const newParams = new URLSearchParams(searchParams);
    newParams.set('tokenId', token.id.toString());
    newParams.set('status', filters.status);
    if (filters.username) newParams.set('username', filters.username);
    if (filters.creationDate) newParams.set('creationDate', filters.creationDate.toISOString());
    if (filters.expireDate) newParams.set('expireDate', filters.expireDate.toISOString());

    setSearchParams(newParams);
  };

  const columns: ColumnDef<Token>[] = [
    {
      accessorKey: 'user_nicename',
      header: 'Username',
      cell: ({ row }) => {
        const user_link = row.original.user_link;
        const user_nicename = row.original.user_nicename;
        return user_link ? (
          <a href={user_link} className="jwt-text-primary hover:jwt-underline">
            {user_nicename}
          </a>
        ) : (
          user_nicename
        );
      }
    },
    {
      accessorKey: 'issued_at',
      header: 'Created',
      cell: ({ row }) => <DateTimeCell value={row.original.issued_at} />
    },
    {
      accessorKey: 'expires_at',
      header: 'Expired',
      cell: ({ row }) => <DateTimeCell value={row.original.expires_at} />
    },
    {
      accessorKey: 'last_used_at',
      header: 'Last Used',
      cell: ({ row }) =>
        row.original.last_used_at ? <DateTimeCell value={row.original.last_used_at} /> : 'Never'
    },
    {
      accessorKey: 'status',
      header: 'Status',
      cell: ({ row }) => {
        const status = row.original.status;
        const badgeStyles = {
          Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
          Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
          Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
        };
        return (
          <span
            className={cn(
              'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
              'jwt-text-xs jwt-font-medium jwt-border',
              badgeStyles[status as keyof typeof badgeStyles]
            )}
          >
            {status}
          </span>
        );
      }
    },
    {
      id: 'actions',
      header: 'Actions',
      cell: ({ row }) => {
        return (
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="jwt-h-8 jwt-w-8 jwt-p-0">
                <span className="jwt-sr-only">Open menu</span>
                <MoreHorizontal className="jwt-h-4 jwt-w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem
                onClick={(e) => {
                  e.stopPropagation();
                  handleViewDetails(row.original);
                }}
              >
                Details
              </DropdownMenuItem>
              <DropdownMenuItem
                onClick={(e) => {
                  e.stopPropagation();
                  setSelectedToken(row.original);
                  setIsRevokeDialogOpen(true);
                }}
              >
                Revoke
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        );
      }
    }
  ];

  const fetchTokens = React.useCallback(
    async (page: number = 1, pageSize: number = pagination.pageSize) => {
      try {
        setLoading(true);
        const response = await tokenAPI.list({
          page,
          per_page: pageSize,
          status:
            filters.status === 'all'
              ? undefined
              : (filters.status as 'active' | 'expired' | 'revoked'),
          username: filters.username || undefined,
          created_at: filters.creationDate?.toISOString(),
          expires_at: filters.expireDate?.toISOString()
        });
        setData(response.items);
        setPagination((prev) => ({
          currentPage: page,
          totalPages: Math.ceil(response.pagination.total / pageSize),
          totalItems: response.pagination.total,
          pageSize: pageSize
        }));
      } catch (error) {
        console.error('Failed to fetch tokens:', error);
      } finally {
        setLoading(false);
      }
    },
    [filters]
  );

  React.useEffect(() => {
    fetchTokens(1, pagination.pageSize);
  }, [fetchTokens]);

  const handleRevoke = async () => {
    if (!selectedToken) return;

    try {
      // Ensure the ID is a number
      const tokenId =
        typeof selectedToken.id === 'string' ? parseInt(selectedToken.id, 10) : selectedToken.id;
      await tokenAPI.revoke(tokenId);
      toast({
        title: 'Token Revoked',
        description: 'The token has been successfully revoked.'
      });
      // Refresh the table
      fetchTokens(pagination.currentPage, pagination.pageSize);
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to revoke the token. Please try again.',
        variant: 'destructive'
      });
    } finally {
      setIsRevokeDialogOpen(false);
      setSelectedToken(null);
    }
  };

  const table = useReactTable({
    data,
    columns: columns,
    getCoreRowModel: getCoreRowModel()
  });

  const handlePageChange = (page: number) => {
    fetchTokens(page, pagination.pageSize);
  };

  const handlePageSizeChange = (newSize: string) => {
    const size = parseInt(newSize, 10);
    fetchTokens(1, size);
  };

  if (loading) {
    return <div className="jwt-text-center jwt-py-4">Loading...</div>;
  }

  return (
    <div className="jwt-rounded-lg jwt-border jwt-bg-white">
      <div className="jwt-overflow-x-auto">
        <Table>
          <TableHeader className="jwt-bg-gray-50">
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id} className="hover:jwt-bg-transparent">
                {headerGroup.headers.map((header) => (
                  <TableHead key={header.id} className="jwt-text-xs jwt-font-medium">
                    {header.isPlaceholder
                      ? null
                      : flexRender(header.column.columnDef.header, header.getContext())}
                  </TableHead>
                ))}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row) => (
                <TableRow
                  key={row.id}
                  data-state={row.getIsSelected() && 'selected'}
                  className="hover:jwt-bg-muted/50 jwt-cursor-pointer"
                  onClick={() => handleViewDetails(row.original)}
                >
                  {row.getVisibleCells().map((cell) => (
                    <TableCell key={cell.id} className="jwt-py-3">
                      {flexRender(cell.column.columnDef.cell, cell.getContext())}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={columns.length} className="jwt-h-24 jwt-text-center">
                  No tokens found.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      <Dialog open={isRevokeDialogOpen} onOpenChange={setIsRevokeDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle> Are you sure you want to revoke this token?</DialogTitle>
            <DialogDescription>
              When a token is revoked, it will no longer be valid for authentication and this action
              cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setIsRevokeDialogOpen(false);
                setSelectedToken(null);
              }}
            >
              Cancel
            </Button>
            <Button variant="destructive" onClick={handleRevoke}>
              Revoke
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {pagination.totalPages > 1 && (
        <div className="jwt-flex jwt-items-center jwt-justify-between jwt-border-t jwt-bg-gray-50/50 jwt-p-4">
          <div className="jwt-flex jwt-items-center jwt-gap-2 jwt-text-sm jwt-text-gray-600">
            <span>Show</span>
            <Select value={pagination.pageSize.toString()} onValueChange={handlePageSizeChange}>
              <SelectTrigger className="jwt-w-[80px] jwt-h-8 jwt-border-gray-300">
                <SelectValue />
              </SelectTrigger>
              <SelectContent className="jwt-w-[80px]">
                {pageSizeOptions.map((size) => (
                  <SelectItem key={size} value={size.toString()}>
                    {size}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <span>per page</span>
          </div>

          <div className="jwt-flex jwt-items-center jwt-gap-6">
            <span className="jwt-text-sm jwt-text-muted-foreground">
              {(pagination.currentPage - 1) * pagination.pageSize + 1}-
              {Math.min(pagination.currentPage * pagination.pageSize, pagination.totalItems)} of{' '}
              {pagination.totalItems}
            </span>

            <div className="jwt-flex jwt-gap-2">
              <button
                onClick={() => handlePageChange(pagination.currentPage - 1)}
                disabled={pagination.currentPage === 1}
                className="jwt-p-2 jwt-text-sm jwt-font-medium jwt-text-muted-foreground hover:jwt-text-primary disabled:jwt-opacity-50"
              >
                Previous
              </button>
              <div className="jwt-flex jwt-items-center jwt-gap-2">
                {Array.from({ length: pagination.totalPages }, (_, i) => i + 1)
                  .filter(
                    (page) =>
                      page === 1 ||
                      page === pagination.totalPages ||
                      Math.abs(page - pagination.currentPage) <= 1
                  )
                  .map((page, i, arr) => (
                    <React.Fragment key={page}>
                      {i > 0 && arr[i - 1] !== page - 1 && (
                        <span className="jwt-text-muted-foreground">...</span>
                      )}
                      <button
                        onClick={() => handlePageChange(page)}
                        className={`jwt-min-w-[32px] jwt-p-2 jwt-text-sm jwt-font-medium ${
                          page === pagination.currentPage
                            ? 'jwt-bg-primary jwt-text-primary-foreground'
                            : 'jwt-text-muted-foreground hover:jwt-text-primary'
                        } jwt-rounded-md`}
                      >
                        {page}
                      </button>
                    </React.Fragment>
                  ))}
              </div>
              <button
                onClick={() => handlePageChange(pagination.currentPage + 1)}
                disabled={pagination.currentPage === pagination.totalPages}
                className="jwt-p-2 jwt-text-sm jwt-font-medium jwt-text-muted-foreground hover:jwt-text-primary disabled:jwt-opacity-50"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

import { useEffect, useState } from 'react';
