import { useEffect, useState } from 'react';
import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { Input } from '@/components/ui/input';
import { Switch } from '@/components/ui/switch';
import { Button } from '@/components/ui/button';
import { useToast } from '@/components/hooks/use-toast';
import { settingsAPI } from '@/services/api';
import { Settings, SettingsOptions } from '@/types/api';
import { SubHeading } from '@/components/ui/sub-heading';
import { SettingRow } from '@/components/setting-row';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue
} from '@/components/ui/select';

export default function SettingsPage() {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [settings, setSettings] = useState<Settings | null>(null);
  const [options, setOptions] = useState<SettingsOptions | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    const loadData = async () => {
      try {
        const [settingsData, optionsData] = await Promise.all([
          settingsAPI.get(),
          settingsAPI.getOptions()
        ]);
        setSettings(settingsData);
        setOptions(optionsData);
      } catch (error) {
        toast({
          title: 'Error',
          description: 'Failed to load settings. Please try again.',
          variant: 'destructive'
        });
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [toast]);

  const handleSave = async () => {
    if (!settings) return;

    try {
      setSaving(true);
      await settingsAPI.update(settings);
      toast({
        title: 'Success',
        description: 'Settings saved successfully.'
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to save settings. Please try again.',
        variant: 'destructive'
      });
    } finally {
      setSaving(false);
    }
  };

  if (loading || !settings || !options) {
    return (
      <PageWrapper>
        <div className="jwt-text-center jwt-py-4">Loading...</div>
      </PageWrapper>
    );
  }

  return (
    <PageWrapper>
      <Header
        title="Configuration Settings"
        description="Configure JWT authentication, security policies, and data management preferences."
      />

      <div className="jwt-space-y-8">
        {/* Data Management Settings */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-6">
          <SubHeading title="Data Management" />
          <dl className="jwt-divide-y jwt-divide-gray-200">
            <SettingRow
              title="Delete data on deactivation"
              description="Remove all plugin data when deactivating JWT Auth Pro"
            >
              <Switch
                checked={settings.data_management.delete_on_deactivation}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    data_management: {
                      ...settings.data_management,
                      delete_on_deactivation: checked
                    }
                  })
                }
              />
            </SettingRow>

            <SettingRow
              title="Analytics Retention"
              description="Number of days to keep analytics data"
            >
              <Select
                value={settings.data_management.analytics_retention_days.toString()}
                onValueChange={(value) =>
                  setSettings({
                    ...settings,
                    data_management: {
                      ...settings.data_management,
                      analytics_retention_days: parseInt(value, 10)
                    }
                  })
                }
              >
                <SelectTrigger className="jwt-w-[180px]">
                  <SelectValue placeholder="Select retention period" />
                </SelectTrigger>
                <SelectContent>
                  {options.analytics_retention.map((option) => (
                    <SelectItem key={option.value.toString()} value={option.value.toString()}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </SettingRow>

            <SettingRow
              title="Anonymize IP Addresses"
              description="Anonymize IP addresses in logs and analytics"
            >
              <Switch
                checked={settings.data_management.anonymize_ip}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    data_management: {
                      ...settings.data_management,
                      anonymize_ip: checked
                    }
                  })
                }
              />
            </SettingRow>
          </dl>
        </div>

        {/* Token Settings */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-6">
          <SubHeading title="Token Settings" />
          <dl className="jwt-divide-y jwt-divide-gray-200">
            <SettingRow
              title="JWT Token Expiration"
              description="Number of days before JWT tokens expire"
            >
              <Select
                value={(settings.token_settings.jwt_expiration / (24 * 60 * 60)).toString()}
                onValueChange={(value) =>
                  setSettings({
                    ...settings,
                    token_settings: {
                      ...settings.token_settings,
                      jwt_expiration: parseInt(value, 10) * 24 * 60 * 60
                    }
                  })
                }
              >
                <SelectTrigger className="jwt-w-[180px]">
                  <SelectValue placeholder="Select expiration" />
                </SelectTrigger>
                <SelectContent>
                  {options.jwt_expiration.map((option) => (
                    <SelectItem key={option.value.toString()} value={option.value.toString()}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </SettingRow>

            <SettingRow
              title="Refresh Token Expiration"
              description="Number of days before refresh tokens expire"
            >
              <Select
                value={(
                  settings.token_settings.refresh_token_expiration /
                  (24 * 60 * 60)
                ).toString()}
                onValueChange={(value) =>
                  setSettings({
                    ...settings,
                    token_settings: {
                      ...settings.token_settings,
                      refresh_token_expiration: parseInt(value, 10) * 24 * 60 * 60
                    }
                  })
                }
              >
                <SelectTrigger className="jwt-w-[180px]">
                  <SelectValue placeholder="Select expiration" />
                </SelectTrigger>
                <SelectContent>
                  {options.refresh_token_expiration.map((option) => (
                    <SelectItem key={option.value.toString()} value={option.value.toString()}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </SettingRow>

            <SettingRow title="Signing Algorithm" description="Algorithm used to sign JWT tokens">
              <Select
                value={settings.token_settings.signing_algorithm}
                onValueChange={(value) =>
                  setSettings({
                    ...settings,
                    token_settings: {
                      ...settings.token_settings,
                      signing_algorithm: value
                    }
                  })
                }
              >
                <SelectTrigger className="jwt-w-[180px]">
                  <SelectValue placeholder="Select algorithm" />
                </SelectTrigger>
                <SelectContent>
                  {options.signing_algorithm.map((option) => (
                    <SelectItem key={option.value.toString()} value={option.value.toString()}>
                      {option.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </SettingRow>

            <SettingRow
              title="Enable CORS"
              description="Allow Cross-Origin Resource Sharing for the REST API endpoints"
            >
              <Switch
                checked={settings.token_settings.enable_cors}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    token_settings: {
                      ...settings.token_settings,
                      enable_cors: checked
                    }
                  })
                }
              />
            </SettingRow>
          </dl>
        </div>

        {/* User Settings */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-6">
          <SubHeading title="Security Actions" />
          <dl className="jwt-divide-y jwt-divide-gray-200">
            <SettingRow
              title="Revoke on Password Change"
              description="Automatically revoke all tokens when a user changes their password"
            >
              <Switch
                checked={settings.user_settings.revoke_on_password_change}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    user_settings: {
                      ...settings.user_settings,
                      revoke_on_password_change: checked
                    }
                  })
                }
              />
            </SettingRow>

            <SettingRow
              title="Revoke on Role Change"
              description="Automatically revoke all tokens when a user's role is changed"
            >
              <Switch
                checked={settings.user_settings.revoke_on_role_change}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    user_settings: {
                      ...settings.user_settings,
                      revoke_on_role_change: checked
                    }
                  })
                }
              />
            </SettingRow>

            <SettingRow
              title="Revoke on Email Change"
              description="Automatically revoke all tokens when a user changes their email address"
            >
              <Switch
                checked={settings.user_settings.revoke_on_email_change}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    user_settings: {
                      ...settings.user_settings,
                      revoke_on_email_change: checked
                    }
                  })
                }
              />
            </SettingRow>

            <SettingRow
              title="Delete on User Delete"
              description="Delete all tokens when a user is deleted"
            >
              <Switch
                checked={settings.user_settings.delete_on_user_delete}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    user_settings: {
                      ...settings.user_settings,
                      delete_on_user_delete: checked
                    }
                  })
                }
              />
            </SettingRow>
          </dl>
        </div>

        {/* Rate Limit Settings */}
        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-6">
          <SubHeading title="Rate Limiting" />
          <dl className="jwt-divide-y jwt-divide-gray-200">
            <SettingRow
              title="Enable Rate Limiting"
              description="Enable or disable rate limiting for all JWT endpoints"
            >
              <Switch
                checked={settings.rate_limit_settings.enabled}
                onCheckedChange={(checked) =>
                  setSettings({
                    ...settings,
                    rate_limit_settings: {
                      ...settings.rate_limit_settings,
                      enabled: checked
                    }
                  })
                }
              />
            </SettingRow>

            <SettingRow
              title="Maximum Requests"
              description="Maximum number of requests allowed within the time window"
            >
              <Input
                type="number"
                min={1}
                max={10000}
                value={settings.rate_limit_settings.max_requests}
                onChange={(e) =>
                  setSettings({
                    ...settings,
                    rate_limit_settings: {
                      ...settings.rate_limit_settings,
                      max_requests: parseInt(e.target.value, 10) || 60
                    }
                  })
                }
                className="jwt-w-[180px]"
                placeholder="60"
              />
            </SettingRow>

            <SettingRow title="Time Window" description="Time window in minutes for rate limiting">
              <Input
                type="number"
                min={1}
                max={1440}
                value={settings.rate_limit_settings.window_minutes}
                onChange={(e) =>
                  setSettings({
                    ...settings,
                    rate_limit_settings: {
                      ...settings.rate_limit_settings,
                      window_minutes: parseInt(e.target.value, 10) || 1
                    }
                  })
                }
                className="jwt-w-[180px]"
                placeholder="1"
              />
            </SettingRow>
          </dl>
        </div>

        {/* Save Button - Sticky at bottom */}
        <div className="jwt-sticky jwt-bottom-0 jwt-z-10 jwt-flex jwt-justify-end jwt-border-t jwt-bg-white jwt-p-4 jwt-shadow-lg">
          <Button onClick={handleSave} disabled={saving} className="jwt-min-w-[120px]">
            {saving ? 'Saving...' : 'Save Changes'}
          </Button>
        </div>
      </div>
    </PageWrapper>
  );
}

