import { WordPressConfig } from '@/types/api';

interface WordPressCoreCardProps {
  config: WordPressConfig;
}

interface InfoItemProps {
  label: string;
  value: string | boolean;
}

function InfoItem({ label, value }: InfoItemProps) {
  return (
    <div className="jwt-flex jwt-items-center jwt-justify-between jwt-p-3 jwt-bg-gray-50 jwt-rounded-lg">
      <span className="jwt-text-sm">{label}</span>
      <span className="jwt-text-sm jwt-font-medium jwt-text-muted-foreground">
        {typeof value === 'boolean' ? (value ? 'Yes' : 'No') : value}
      </span>
    </div>
  );
}

export function WordPressCoreCard({ config }: WordPressCoreCardProps) {
  return (
    <div className="jwt-p-6 jwt-pt-0">
      <div className="jwt-space-y-3">
        <InfoItem label="Site URL" value={config.site_url} />
        <InfoItem label="Home URL" value={config.home_url} />
        <InfoItem label="Multisite" value={config.is_multisite} />
        <InfoItem label="Environment" value={config.environment_type} />
        <InfoItem label="Language" value={config.language} />
        <InfoItem label="Memory Limit" value={config.memory_limit} />
        <InfoItem label="Max Upload Size" value={config.max_upload_size} />
      </div>
    </div>
  );
}

