import * as React from 'react';
import { ChevronDown, ChevronUp } from 'lucide-react';
import { cn } from '@/lib/utils';

interface CollapsibleSectionProps {
  title: string;
  children: React.ReactNode;
  defaultOpen?: boolean;
  className?: string;
}

export function CollapsibleSection({ 
  title, 
  children, 
  defaultOpen = true,
  className 
}: CollapsibleSectionProps) {
  const [isOpen, setIsOpen] = React.useState(defaultOpen);

  return (
    <div className={cn("jwt-rounded-lg jwt-border jwt-bg-white", className)}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="jwt-flex jwt-w-full jwt-items-center jwt-justify-between jwt-p-6 jwt-text-left hover:jwt-bg-gray-50 jwt-transition-colors"
      >
        <h3 className="jwt-text-lg jwt-font-semibold jwt-text-gray-900">
          {title}
        </h3>
        {isOpen ? (
          <ChevronUp className="jwt-h-5 jwt-w-5 jwt-text-gray-500" />
        ) : (
          <ChevronDown className="jwt-h-5 jwt-w-5 jwt-text-gray-500" />
        )}
      </button>

      {isOpen && (
        <div className="jwt-border-t jwt-border-gray-200">
          <div className="jwt-p-6 jwt-pt-0">
            <div className="jwt-mt-6 jwt-space-y-6">
              {children}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

