   444→                  <TableBody>
   445→                    {relatedTokens.map((relatedToken) => {
   446→                      const badgeStyles = {
   447→                        Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
   448→                        Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
   449→                        Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
