   500→        )}
   501→
   502→        {/* Usage History */}
   503→        {usageHistory.length > 0 && (
   504→          <Card>
   505→            <CardHeader>
   506→              <CardTitle className="jwt-text-lg jwt-font-medium">Usage History</CardTitle>
   507→              <CardDescription>
   508→                All the events that have been logged for this token.
   509→              </CardDescription>
   510→            </CardHeader>
   511→            <CardContent>
   512→              <div className="jwt-space-y-4">
   513→                <div className="jwt-overflow-x-auto">
   514→                  <Table>
   515→                    <TableHeader className="jwt-bg-gray-50">
   516→                      <TableRow className="hover:jwt-bg-transparent">
   517→                        <TableHead className="jwt-text-xs jwt-font-medium">Event Type</TableHead>
   518→                        <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
   519→                        <TableHead className="jwt-text-xs jwt-font-medium">Method</TableHead>
