   534→                            <span
   535→                              className={cn(
   536→                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
   537→                                'jwt-text-xs jwt-font-medium jwt-border',
   538→                                {
   539→                                  'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
   540→                                    event.event_status === 1,
   541→                                  'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
   542→                                    event.event_status === 2
