import { cn } from '@/components/lib/utils';
import {
  HomeIcon,
  KeyRoundIcon,
  SettingsIcon,
  PanelLeftCloseIcon,
  PanelLeftIcon,
  ServerIcon
} from 'lucide-react';
import { useTab } from '@/App';
import { useState } from 'react';
import { Logo } from './logo';

const navigationItems = [
  {
    title: 'Home',
    tab: 'home',
    icon: HomeIcon
  },
  {
    title: 'Tokens',
    tab: 'tokens',
    icon: KeyRoundIcon
  },
  {
    title: 'Settings',
    tab: 'settings',
    icon: SettingsIcon
  },
  {
    title: 'System',
    tab: 'system',
    icon: ServerIcon
  }
];

export function AppSidebar() {
  const context = useTab();
  const [isExpanded, setIsExpanded] = useState(true);
  const isWhitelabeled = window.wpApiSettings.is_whitelabeled;

  if (!context) return null;
  const { currentTab, setCurrentTab } = context;

  const handleClick = (e: React.MouseEvent<HTMLAnchorElement>, tab: string) => {
    e.preventDefault();
    setCurrentTab(tab);
    const newUrl = `${window.location.pathname}?page=jwt-auth-pro&tab=${tab}`;
    window.history.pushState({}, '', newUrl);
  };

  return (
    <aside
      className={cn(
        'jwt-flex jwt-h-full jwt-flex-col jwt-border-r jwt-border-gray-200 jwt-bg-white jwt-transition-all jwt-duration-300',
        isExpanded ? 'jwt-w-[250px]' : 'jwt-w-[64px]'
      )}
    >
      {/* Logo, Title and Toggle */}
      <div className="jwt-flex jwt-items-center jwt-justify-between jwt-p-4">
        <div className={cn('jwt-flex jwt-items-center jwt-gap-2', !isExpanded && 'jwt-hidden')}>
          {!isWhitelabeled && <Logo />}
          {isWhitelabeled && (
            <span className="jwt-text-lg jwt-font-semibold">JWT Authentication</span>
          )}
        </div>
        {/* Toggle Icon */}
        <div
          onClick={() => setIsExpanded(!isExpanded)}
          className={cn(
            'jwt-cursor-pointer jwt-text-gray-500 hover:jwt-text-gray-900',
            !isExpanded && 'jwt-mx-auto'
          )}
          title={isExpanded ? 'Collapse sidebar' : 'Expand sidebar'}
        >
          {isExpanded ? (
            <PanelLeftCloseIcon className="jwt-h-5 jwt-w-5" />
          ) : (
            <PanelLeftIcon className="jwt-h-5 jwt-w-5" />
          )}
        </div>
      </div>

      {/* Navigation */}
      <nav className="jwt-flex-1 jwt-space-y-1 jwt-p-2">
        {navigationItems.map((item) => {
          const isActive = currentTab === item.tab;
          return (
            <a
              key={item.tab}
              href={`?page=jwt-auth-pro&tab=${item.tab}`}
              onClick={(e) => handleClick(e, item.tab)}
              className={cn(
                'jwt-group jwt-flex jwt-items-center jwt-gap-x-3 jwt-rounded-lg jwt-px-3 jwt-py-2 jwt-text-sm jwt-font-medium jwt-text-gray-600 hover:jwt-bg-gray-100 hover:jwt-text-gray-900',
                'jwt-justify-start',
                isActive && 'jwt-bg-gray-100 jwt-text-gray-900'
              )}
              title={item.title}
            >
              <item.icon className="jwt-h-5 jwt-w-5 jwt-shrink-0" aria-hidden="true" />
              <span
                className={cn('jwt-transition-all', isExpanded ? 'jwt-inline-block' : 'jwt-hidden')}
              >
                {item.title}
              </span>
            </a>
          );
        })}
      </nav>
    </aside>
  );
}

