import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { HistoricalData } from '@/types/analytics';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer
} from 'recharts';

interface AnalyticsChartsProps {
  historicalData: HistoricalData[];
  defaultPeriod?: '7d' | '30d' | '90d';
  onPeriodChange?: (period: '7d' | '30d' | '90d') => void;
}

export function AnalyticsCharts({
  historicalData,
  defaultPeriod = '7d',
  onPeriodChange
}: AnalyticsChartsProps) {
  const [period, setPeriod] = useState<'7d' | '30d' | '90d'>(defaultPeriod);

  const handlePeriodChange = (value: string) => {
    const newPeriod = value as '7d' | '30d' | '90d';
    setPeriod(newPeriod);
    onPeriodChange?.(newPeriod);
  };

  return (
    <Tabs defaultValue={period} className="jwt-w-full" onValueChange={handlePeriodChange}>
      <div className="jwt-flex jwt-justify-end">
        <TabsList className="jwt-h-10 jwt-w-fit jwt-bg-[#F3F4F6] jwt-p-[3px] jwt-rounded-[12px] jwt-flex jwt-gap-0 jwt-shadow-sm">
          <TabsTrigger
            value="7d"
            className="jwt-h-[34px] jwt-px-4 jwt-rounded-[10px] jwt-text-[14px] jwt-font-medium jwt-text-[#6B7280] jwt-data-[state=active]:bg-white jwt-data-[state=active]:text-[#111827] jwt-data-[state=active]:shadow-sm"
          >
            Last 7 Days
          </TabsTrigger>
          <TabsTrigger
            value="30d"
            className="jwt-h-[34px] jwt-px-4 jwt-rounded-[10px] jwt-text-[14px] jwt-font-medium jwt-text-[#6B7280] jwt-data-[state=active]:bg-white jwt-data-[state=active]:text-[#111827] jwt-data-[state=active]:shadow-sm"
          >
            Last 30 Days
          </TabsTrigger>
          <TabsTrigger

'use client';

import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import {
  Loader2,
  Cpu,
  Database,
  Cog,
  LucideIcon,
  Palette,
  Package,
  Server,
  Globe,
  Bug,
  Zap,
  Settings,
  Table
} from 'lucide-react';
import type { SystemInfo, SystemVersionInfo } from '@/types/api';
import { ServerInfoCard } from '@/components/system/server-info-card';
import { PHPConfigCard } from '@/components/system/php-config-card';
import { WordPressCoreCard } from '@/components/system/wordpress-core-card';
import { WordPressThemeCard } from '@/components/system/wordpress-theme-card';
import { WordPressDebugCard } from '@/components/system/wordpress-debug-card';
import { WordPressPerformanceCard } from '@/components/system/wordpress-performance-card';
import { DatabaseConfigCard } from '@/components/system/database-config-card';
import { DatabaseTablesCard } from '@/components/system/database-tables-card';
import { PluginsCard } from '@/components/system/plugins-card';
import { CollapsibleCard } from '@/components/ui/collapsible-card';

interface SystemInfoItemProps {
  icon: LucideIcon;
  label: string;
  version: SystemVersionInfo;
}

function SystemInfoItem({ icon: Icon, label, version }: SystemInfoItemProps) {
  return (
    <div className="jwt-flex jwt-items-center jwt-gap-4 jwt-p-3 jwt-bg-gray-50 jwt-rounded-lg">
      <Icon className="jwt-h-5 jwt-w-5 jwt-text-gray-500" />
      <div className="jwt-flex-1">
        <div className="jwt-flex jwt-items-center jwt-justify-between">
          <span className="jwt-text-sm jwt-font-medium">{label}</span>
          <div className="jwt-flex jwt-items-center jwt-gap-2">
            <span className="jwt-text-sm jwt-text-muted-foreground">{version.version}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

interface SystemOverviewCardProps {
  isLoading: boolean;
  data: SystemInfo | null;
}

export function SystemOverviewCard({ isLoading, data }: SystemOverviewCardProps) {
  if (isLoading || !data) {
    return (
      <Card>
        <CardContent>
          <div className="jwt-flex jwt-justify-center jwt-items-center jwt-h-[200px]">
            <Loader2 className="jwt-h-6 jwt-w-6 jwt-animate-spin" />
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="jwt-space-y-6">
      {/* Version Overview */}
      <Card>
        <CardHeader>
          <div className="jwt-flex jwt-items-center jwt-space-x-2">
            <div className="jwt-p-2 jwt-bg-primary/10 jwt-rounded-full">
              <Cog className="jwt-h-4 jwt-w-4 jwt-text-primary" />
            </div>
            <CardTitle className="jwt-text-base jwt-font-medium">Version Overview</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <div className="jwt-space-y-3">
            <SystemInfoItem icon={Cpu} label="PHP Version" version={data.php_version} />
            <SystemInfoItem icon={Cog} label="WordPress Version" version={data.wordpress_version} />
            <SystemInfoItem icon={Database} label="MySQL" version={data.mysql_version} />
          </div>
        </CardContent>
      </Card>

      {/* WordPress Information */}
      <CollapsibleCard
        title="Theme Information"
        icon={<Palette className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <WordPressThemeCard theme={data.theme} />
      </CollapsibleCard>

      <CollapsibleCard
        title="Plugins"
        icon={<Package className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <PluginsCard plugins={data.plugins} />
      </CollapsibleCard>

      {/* Server Information */}
      <CollapsibleCard
        title="Server Information"
        icon={<Server className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <ServerInfoCard server={data.server} />
      </CollapsibleCard>

      <CollapsibleCard
        title="PHP Configuration"
        icon={<Cpu className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <PHPConfigCard php={data.php} />
      </CollapsibleCard>

      {/* WordPress Configuration */}
      <CollapsibleCard
        title="WordPress Core"
        icon={<Globe className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <WordPressCoreCard config={data.wordpress} />
      </CollapsibleCard>

      <CollapsibleCard
        title="Debug Settings"
        icon={<Bug className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <WordPressDebugCard constants={data.constants} />
      </CollapsibleCard>

      <CollapsibleCard
        title="Performance Settings"
        icon={<Zap className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <WordPressPerformanceCard constants={data.constants} />
      </CollapsibleCard>

      {/* Database Information */}
      <CollapsibleCard
        title="Database Configuration"
        icon={<Settings className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <DatabaseConfigCard charset={data.database.charset} collate={data.database.collate} />
      </CollapsibleCard>

      <CollapsibleCard
        title="Table Configuration"
        icon={<Table className="jwt-h-4 jwt-w-4 jwt-text-primary" />}
      >
        <DatabaseTablesCard prefix={data.database.prefix} tables={data.database.tables} />
      </CollapsibleCard>
    </div>
  );
}

