import { PHPConfig } from '@/types/api';

interface PHPConfigCardProps {
  php: PHPConfig;
}

interface InfoItemProps {
  label: string;
  value: string | boolean;
}

function InfoItem({ label, value }: InfoItemProps) {
  return (
    <div className="jwt-flex jwt-items-center jwt-justify-between jwt-p-3 jwt-bg-gray-50 jwt-rounded-lg">
      <span className="jwt-text-sm">{label}</span>
      <span className="jwt-text-sm jwt-font-medium jwt-text-muted-foreground">
        {typeof value === 'boolean' ? (value ? 'Yes' : 'No') : value}
      </span>
    </div>
  );
}

export function PHPConfigCard({ php }: PHPConfigCardProps) {
  return (
    <div className="jwt-p-6 jwt-pt-0">
      <div className="jwt-space-y-3">
        <InfoItem label="Memory Limit" value={php.memory_limit} />
        <InfoItem label="Max Execution Time" value={`${php.max_execution_time}s`} />
        <InfoItem label="Post Max Size" value={php.post_max_size} />
        <InfoItem label="Upload Max Filesize" value={php.upload_max_filesize} />
        <InfoItem label="Max Input Vars" value={php.max_input_vars} />
        <InfoItem label="Allow URL fopen" value={php.allow_url_fopen === '1'} />
        <InfoItem label="Display Errors" value={php.display_errors === '1'} />
        <InfoItem label="OPcache Enabled" value={php.opcache_enabled} />
      </div>
    </div>
  );
}

