    10→import { Button } from '@/components/ui/button';
    11→import { useToast } from '@/components/hooks/use-toast';
    12→import { ClipboardCopy, Download } from 'lucide-react';
    13→
    14→export default function SystemPage() {
    15→  const [isLoading, setIsLoading] = useState(true);
    16→  const [systemInfo, setSystemInfo] = useState<SystemInfo | null>(null);
    17→  const { toast } = useToast();
    18→
    19→  useEffect(() => {
    20→    let isMounted = true;
    21→
    22→    const fetchData = async () => {
    23→      try {
    24→        setIsLoading(true);
    25→        const systemData = await systemAPI.getInfo();
    26→
    27→        if (isMounted) {
    28→          setSystemInfo(systemData);
    29→        }
    30→      } catch (error) {
    31→        console.error('Error fetching data:', error);
    32→      } finally {
    33→        if (isMounted) {
    34→          setIsLoading(false);
    35→        }
    36→      }
    37→    };
    38→
    39→    void fetchData();
    40→
    41→    return () => {
    42→      isMounted = false;
    43→    };
    44→  }, []);
    45→
    46→  const formatSystemInfo = (info: SystemInfo): string => {
    47→    return `
    48→### JWT Auth Pro System Information ###
    49→
    50→WordPress Environment:
    51→- WordPress Version: ${info.wordpress_version.version}
    52→- Site URL: ${info.wordpress.site_url}
    53→- Home URL: ${info.wordpress.home_url}
    54→- Multisite: ${info.wordpress.is_multisite ? 'Yes' : 'No'}
    55→- Memory Limit: ${info.wordpress.memory_limit}
    56→- Language: ${info.wordpress.language}
    57→- Timezone: ${info.wordpress.timezone.string} (${info.wordpress.timezone.gmt_offset})
    58→
    59→Server Environment:
    60→- PHP Version: ${info.php_version.version}
    61→- MySQL Version: ${info.mysql_version.version}
    62→- Web Server: ${info.server.software}
    63→- Operating System: ${info.server.os}
    64→- Architecture: ${info.server.architecture}
    65→- SSL Installed: ${info.server.ssl_installed ? 'Yes' : 'No'}
    66→
    67→PHP Configuration:
    68→- Memory Limit: ${info.php.memory_limit}
    69→- Max Execution Time: ${info.php.max_execution_time}
    70→- Post Max Size: ${info.php.post_max_size}
    71→- Upload Max Filesize: ${info.php.upload_max_filesize}
    72→- Max Input Vars: ${info.php.max_input_vars}
    73→- Display Errors: ${info.php.display_errors}
    74→- OPCache Enabled: ${info.php.opcache_enabled ? 'Yes' : 'No'}
    75→
    76→WordPress Constants:
    77→- WP_DEBUG: ${info.constants.WP_DEBUG ? 'Yes' : 'No'}
    78→- WP_DEBUG_LOG: ${info.constants.WP_DEBUG_LOG ? 'Yes' : 'No'}
    79→- WP_DEBUG_DISPLAY: ${info.constants.WP_DEBUG_DISPLAY ? 'Yes' : 'No'}
    80→- WP_CACHE: ${info.constants.WP_CACHE ? 'Yes' : 'No'}
    81→
    82→Active Theme:
    83→- Name: ${info.theme.name}
    84→- Version: ${info.theme.version}
    85→- Author: ${info.theme.author}
    86→
    87→Active Plugins: ${info.plugins.active}/${info.plugins.total}
    88→${info.plugins.items
    89→  .filter((plugin) => plugin.status === 'active')
    90→  .map((plugin) => `- ${plugin.name} ${plugin.version} by ${plugin.author}`)
    91→  .join('\n')}
    92→
    93→Inactive Plugins: ${info.plugins.total - info.plugins.active}/${info.plugins.total}
    94→${info.plugins.items
    95→  .filter((plugin) => plugin.status === 'inactive')
    96→  .map((plugin) => `- ${plugin.name} ${plugin.version} by ${plugin.author}`)
    97→  .join('\n')}
    98→
    99→### End System Information ###
   100→    `.trim();
   101→  };
   102→
   103→  const handleCopySystemInfo = async () => {
   104→    if (!systemInfo) return;
   105→
   106→    try {
   107→      const formattedInfo = formatSystemInfo(systemInfo);
   108→      await navigator.clipboard.writeText(formattedInfo);
   109→      toast({
   110→        title: 'Success',
   111→        description: 'System information copied to clipboard'
   112→      });
   113→    } catch (error) {
   114→      console.error('Copy failed:', error);
   115→      toast({
   116→        title: 'Error',
   117→        description: 'Failed to copy system information',
   118→        variant: 'destructive'
   119→      });
   120→    }
   121→  };
   122→
   123→  return (
   124→    <PageWrapper>
   125→      <Header
   126→        title="System Information"
   127→        description="View environment details, server configuration, and system diagnostics."
   128→      >
   129→        <div className="jwt-flex jwt-gap-2">
   130→          <Button
   131→            onClick={handleCopySystemInfo}
   132→            disabled={isLoading || !systemInfo}
   133→            variant="outline"
   134→            className="jwt-flex jwt-items-center jwt-gap-2"
   135→          >
   136→            <ClipboardCopy className="jwt-h-4 jwt-w-4" />
   137→            Copy System Info
   138→          </Button>
   139→          <Button
   140→            onClick={handleCopySystemInfo}
