   378→        {/* Dates */}
   379→        <div className="jwt-rounded-lg jwt-border jwt-bg-white jwt-p-6 jwt-space-y-4">
   380→          <SubHeading title="Dates" />
   381→          <div className="jwt-grid jwt-gap-5 md:jwt-grid-cols-2">
   382→            <div className="jwt-space-y-2">
   383→              <FieldType label="Created">
   384→                <DateTimeCell value={token.issued_at} />
   385→              </FieldType>
