   388→              <FieldType label="Expires">
   389→                <DateTimeCell value={token.expires_at} />
   390→              </FieldType>
   391→            </div>
   392→            <div className="jwt-space-y-2">
   393→              <FieldType label="Last Used">
   394→                {token.last_used_at ? <DateTimeCell value={token.last_used_at} /> : <p>Never</p>}
   395→              </FieldType>
