   426→        {tokenDetails.has_related_tokens && relatedTokens.length > 0 && (
   427→          <Card>
   428→            <CardHeader>
   429→              <CardTitle className="jwt-text-lg jwt-font-semibold">Related Tokens</CardTitle>
