   435→                  <TableHeader className="jwt-bg-gray-50">
   436→                    <TableRow className="hover:jwt-bg-transparent">
   437→                      <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
   438→                      <TableHead className="jwt-text-xs jwt-font-medium">Created</TableHead>
   439→                      <TableHead className="jwt-text-xs jwt-font-medium">Expires</TableHead>
   440→                      <TableHead className="jwt-text-xs jwt-font-medium">Last Used</TableHead>
   441→                      <TableHead className="jwt-text-xs jwt-font-medium">IP Address</TableHead>
   442→                    </TableRow>
   443→                  </TableHeader>
   444→                  <TableBody>
   445→                    {relatedTokens.map((relatedToken) => {
   446→                      const badgeStyles = {
   447→                        Active: 'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200',
   448→                        Revoked: 'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200',
   449→                        Expired: 'jwt-bg-yellow-100 jwt-text-yellow-700 jwt-border-yellow-200'
   450→                      };
   451→
   452→                      return (
   453→                        <TableRow
   454→                          key={relatedToken.id}
   455→                          className="hover:jwt-bg-muted/50 jwt-cursor-pointer"
   456→                          onClick={() => handleTokenSelect(relatedToken.id)}
   457→                        >
   458→                          <TableCell className="jwt-py-5">
   459→                            <span
   460→                              className={cn(
   461→                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
   462→                                'jwt-text-xs jwt-font-medium jwt-border',
   463→                                badgeStyles[relatedToken.status as keyof typeof badgeStyles]
   464→                              )}
   465→                            >
   466→                              {relatedToken.status}
   467→                            </span>
   468→                          </TableCell>
   469→                          <TableCell className="jwt-py-5">
   470→                            <DateTimeCell value={relatedToken.issued_at} />
   471→                          </TableCell>
   472→                          <TableCell className="jwt-py-5">
   473→                            <DateTimeCell value={relatedToken.expires_at} />
   474→                          </TableCell>
   475→                          <TableCell className="jwt-py-5">
   476→                            {relatedToken.last_used_at ? (
   477→                              <DateTimeCell value={relatedToken.last_used_at} />
   478→                            ) : (
   479→                              'Never'
   480→                            )}
   481→                          </TableCell>
   482→                          <TableCell className="jwt-py-5">{relatedToken.ip_address}</TableCell>
   483→                        </TableRow>
   484→                      );
   485→                    })}
   486→                  </TableBody>
   487→                </Table>
   488→                {relatedTokensPagination.total_pages > 1 && (
   489→                  <div className="jwt-mt-4">
   490→                    <Pagination
   491→                      page={relatedTokensPage}
   492→                      totalPages={relatedTokensPagination.total_pages}
   493→                      onPageChange={handleRelatedTokensPageChange}
   494→                    />
   495→                  </div>
   496→                )}
   497→              </div>
   498→            </CardContent>
   499→          </Card>
   500→        )}
   501→
   502→        {/* Usage History */}
   503→        {usageHistory.length > 0 && (
   504→          <Card>
   505→            <CardHeader>
   506→              <CardTitle className="jwt-text-lg jwt-font-semibold">Usage History</CardTitle>
   507→              <CardDescription>
   508→                All the events that have been logged for this token.
   509→              </CardDescription>
   510→            </CardHeader>
   511→            <CardContent>
   512→              <div className="jwt-space-y-4">
   513→                <div className="jwt-overflow-x-auto">
   514→                  <Table>
   515→                    <TableHeader className="jwt-bg-gray-50">
   516→                      <TableRow className="hover:jwt-bg-transparent">
   517→                        <TableHead className="jwt-text-xs jwt-font-medium">Event Type</TableHead>
   518→                        <TableHead className="jwt-text-xs jwt-font-medium">Status</TableHead>
   519→                        <TableHead className="jwt-text-xs jwt-font-medium">Method</TableHead>
   520→                        <TableHead className="jwt-text-xs jwt-font-medium">Path</TableHead>
   521→                        <TableHead className="jwt-text-xs jwt-font-medium">IP</TableHead>
   522→                        <TableHead className="jwt-text-xs jwt-font-medium">Date</TableHead>
   523→                      </TableRow>
   524→                    </TableHeader>
   525→                    <TableBody>
   526→                      {usageHistory.map((event) => (
   527→                        <TableRow key={event.id}>
   528→                          <TableCell className="jwt-py-4">
   529→                            <span className="jwt-capitalize">
   530→                              {event.event_type.replace(/_/g, ' ')}
   531→                            </span>
   532→                          </TableCell>
   533→                          <TableCell className="jwt-py-4">
   534→                            <span
   535→                              className={cn(
   536→                                'jwt-inline-flex jwt-items-center jwt-rounded-full jwt-px-2.5 jwt-py-0.5',
   537→                                'jwt-text-xs jwt-font-medium jwt-border',
   538→                                {
   539→                                  'jwt-bg-green-100 jwt-text-green-700 jwt-border-green-200':
   540→                                    event.event_status === 1,
   541→                                  'jwt-bg-red-100 jwt-text-red-700 jwt-border-red-200':
   542→                                    event.event_status === 2
