import { Token } from './token';
import { PaginationData } from './pagination';

export interface ApiResponse<T> {
  success: boolean;
  data: T;
  message?: string;
}

export interface TokenListResponse {
  items: Token[];
  pagination: PaginationData;
}

export interface SystemVersionInfo {
  version: string;
  status: 'optimal' | 'warning' | 'error';
}

export interface PluginInfo {
  name: string;
  version: string;
  status: 'active' | 'inactive';
  author: string;
}

export interface ThemeInfo {
  name: string;
  version: string;
  author: string;
  status: 'active';
}

export interface ServerInfo {
  software: string;
  os: string;
  architecture: string;
  ip: string;
  hostname: string;
  ssl_installed: boolean;
}

export interface PHPConfig {
  memory_limit: string;
  max_execution_time: string;
  post_max_size: string;
  upload_max_filesize: string;
  max_input_vars: string;
  allow_url_fopen: string;
  display_errors: string;
  max_input_time: string;
  session: {
    save_handler: string;
    save_path: string;
  };
  opcache_enabled: boolean;
}

export interface WordPressConfig {
  version: string;
  site_url: string;
  home_url: string;
  is_multisite: boolean;
  max_upload_size: string;
  memory_limit: string;
  permalink_structure: string;
  language: string;
  timezone: {
    string: string;
    gmt_offset: string;
  };
  admin_email: string;
  environment_type: string;
}

export interface DatabaseInfo {
  version: string;
  charset: string;
  collate: string;
  prefix: string;
  tables: {
    base_prefix: string;
    prefix_length: number;
  };
}

export interface WordPressConstants {
  WP_DEBUG: boolean;
  WP_DEBUG_LOG: boolean;
  WP_DEBUG_DISPLAY: boolean;
  SCRIPT_DEBUG: boolean;
  WP_CACHE: boolean;
  CONCATENATE_SCRIPTS: boolean;
  COMPRESS_SCRIPTS: boolean;
  COMPRESS_CSS: boolean;
  WP_LOCAL_DEV: boolean;
}

export interface SystemInfo {
  php_version: SystemVersionInfo;

