'use client';

import { useEffect, useState } from 'react';
import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { systemAPI } from '@/services/api';
import type { SystemInfo } from '@/types/api';
import { CollapsibleSection } from '@/components/ui/collapsible-section';
import { SettingItem } from '@/components/ui/setting-item';
import { Button } from '@/components/ui/button';
import { useToast } from '@/components/hooks/use-toast';
import { ClipboardCopy, Download } from 'lucide-react';

export default function SystemPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [systemInfo, setSystemInfo] = useState<SystemInfo | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    let isMounted = true;

    const fetchData = async () => {
      try {
        setIsLoading(true);
        const systemData = await systemAPI.getInfo();

        if (isMounted) {
          setSystemInfo(systemData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        if (isMounted) {
          setIsLoading(false);
        }
      }
    };

    void fetchData();

    return () => {
      isMounted = false;
    };
  }, []);

  const formatSystemInfo = (info: SystemInfo): string => {
    return `
### JWT Auth Pro System Information ###

WordPress Environment:
- WordPress Version: ${info.wordpress_version.version}
- Site URL: ${info.wordpress.site_url}
- Home URL: ${info.wordpress.home_url}
- Multisite: ${info.wordpress.is_multisite ? 'Yes' : 'No'}
- Memory Limit: ${info.wordpress.memory_limit}
- Language: ${info.wordpress.language}
- Timezone: ${info.wordpress.timezone.string} (${info.wordpress.timezone.gmt_offset})

Server Environment:
- PHP Version: ${info.php_version.version}
- MySQL Version: ${info.mysql_version.version}
- Web Server: ${info.server.software}
- Operating System: ${info.server.os}
- Architecture: ${info.server.architecture}
- SSL Installed: ${info.server.ssl_installed ? 'Yes' : 'No'}

PHP Configuration:
- Memory Limit: ${info.php.memory_limit}
- Max Execution Time: ${info.php.max_execution_time}
- Post Max Size: ${info.php.post_max_size}
- Upload Max Filesize: ${info.php.upload_max_filesize}
- Max Input Vars: ${info.php.max_input_vars}
- Display Errors: ${info.php.display_errors}
- OPCache Enabled: ${info.php.opcache_enabled ? 'Yes' : 'No'}

WordPress Constants:
- WP_DEBUG: ${info.constants.WP_DEBUG ? 'Yes' : 'No'}
- WP_DEBUG_LOG: ${info.constants.WP_DEBUG_LOG ? 'Yes' : 'No'}
- WP_DEBUG_DISPLAY: ${info.constants.WP_DEBUG_DISPLAY ? 'Yes' : 'No'}
- WP_CACHE: ${info.constants.WP_CACHE ? 'Yes' : 'No'}

Active Theme:
- Name: ${info.theme.name}
- Version: ${info.theme.version}
- Author: ${info.theme.author}

Active Plugins: ${info.plugins.active}/${info.plugins.total}
${info.plugins.items
  .filter((plugin) => plugin.status === 'active')
  .map((plugin) => `- ${plugin.name} ${plugin.version} by ${plugin.author}`)
  .join('\n')}

Inactive Plugins: ${info.plugins.total - info.plugins.active}/${info.plugins.total}
${info.plugins.items
  .filter((plugin) => plugin.status === 'inactive')
  .map((plugin) => `- ${plugin.name} ${plugin.version} by ${plugin.author}`)
  .join('\n')}

### End System Information ###
    `.trim();
  };

  const handleCopySystemInfo = async () => {
    if (!systemInfo) return;

    try {
      const formattedInfo = formatSystemInfo(systemInfo);
      await navigator.clipboard.writeText(formattedInfo);
      toast({
        title: 'Success',
        description: 'System information copied to clipboard'
      });
    } catch (error) {
      console.error('Copy failed:', error);
      toast({
        title: 'Error',
        description: 'Failed to copy system information',
        variant: 'destructive'
      });
    }
  };

  return (
    <PageWrapper>
      <Header
        title="System Information"
        description="View environment details, server configuration, and system diagnostics."
      >
        <div className="jwt-flex jwt-gap-2">
          <Button
            onClick={handleCopySystemInfo}
            disabled={isLoading || !systemInfo}
            variant="outline"
            className="jwt-flex jwt-items-center jwt-gap-2"
          >
            <ClipboardCopy className="jwt-h-4 jwt-w-4" />
            Copy System Info
          </Button>
          <Button
            onClick={handleCopySystemInfo}
            disabled={isLoading || !systemInfo}
            variant="outline"
            className="jwt-flex jwt-items-center jwt-gap-2"
          >
            <Download className="jwt-h-4 jwt-w-4" />
            Download Info
          </Button>
        </div>
      </Header>
      {isLoading ? (
        <div className="jwt-text-center jwt-py-8">
          <div className="jwt-text-gray-500">Loading system information...</div>
        </div>
      ) : systemInfo ? (
        <div className="jwt-space-y-6">
          {/* Version Overview */}
          <CollapsibleSection title="Version Overview">
            <SettingItem label="Plugin Version" value="1.2.5" />
            <SettingItem label="PHP Version" value={systemInfo.php_version.version} />
            <SettingItem label="WordPress Version" value={systemInfo.wordpress_version.version} />
            <SettingItem label="MySQL Version" value={systemInfo.mysql_version.version} />
          </CollapsibleSection>

          {/* Theme Information */}
          <CollapsibleSection title="Theme Information" defaultOpen={false}>
            <SettingItem label="Active Theme" value={systemInfo.theme.name} />
            <SettingItem label="Theme Version" value={systemInfo.theme.version} />
            <SettingItem label="Theme Author" value={systemInfo.theme.author} />
            <SettingItem label="Parent Theme" value={systemInfo.theme.parent || 'N/A'} />
          </CollapsibleSection>

          {/* Active Plugins */}
          <CollapsibleSection title="Active Plugins" defaultOpen={false}>
            {systemInfo.plugins.items
              .filter(plugin => plugin.status === 'active')
              .map((plugin, index) => (
                <SettingItem 
                  key={index} 
                  label={plugin.name} 
                  value={`v${plugin.version}`} 
                />
              ))}
          </CollapsibleSection>

          {/* Server Environment */}
          <CollapsibleSection title="Server Environment" defaultOpen={false}>
            <SettingItem label="Web Server" value={systemInfo.server.software} />
            <SettingItem label="Operating System" value={systemInfo.server.os} />
            <SettingItem label="Architecture" value={systemInfo.server.architecture} />
            <SettingItem label="SSL Installed" value={systemInfo.server.ssl_installed ? 'Yes' : 'No'} />
          </CollapsibleSection>

          {/* PHP Configuration */}
          <CollapsibleSection title="PHP Configuration" defaultOpen={false}>
            <SettingItem label="Memory Limit" value={systemInfo.php.memory_limit} />
            <SettingItem label="Max Execution Time" value={`${systemInfo.php.max_execution_time}s`} />
            <SettingItem label="Post Max Size" value={systemInfo.php.post_max_size} />
            <SettingItem label="Upload Max Filesize" value={systemInfo.php.upload_max_filesize} />
            <SettingItem label="Max Input Vars" value={systemInfo.php.max_input_vars.toString()} />
            <SettingItem label="Display Errors" value={systemInfo.php.display_errors ? 'Yes' : 'No'} />
            <SettingItem label="OPCache Enabled" value={systemInfo.php.opcache_enabled ? 'Yes' : 'No'} />
          </CollapsibleSection>

          {/* WordPress Settings */}
          <CollapsibleSection title="WordPress Settings" defaultOpen={false}>
            <SettingItem label="Site URL" value={systemInfo.wordpress.site_url} />
            <SettingItem label="Home URL" value={systemInfo.wordpress.home_url} />
            <SettingItem label="Multisite" value={systemInfo.wordpress.is_multisite ? 'Yes' : 'No'} />
            <SettingItem label="Environment" value={systemInfo.wordpress.environment_type} />
            <SettingItem label="Language" value={systemInfo.wordpress.language} />
            <SettingItem label="Memory Limit" value={systemInfo.wordpress.memory_limit} />
            <SettingItem label="Max Upload Size" value={systemInfo.wordpress.max_upload_size} />
            <SettingItem label="Timezone" value={`${systemInfo.wordpress.timezone.string} (${systemInfo.wordpress.timezone.gmt_offset})`} />
            <SettingItem label="WP_DEBUG" value={systemInfo.constants.WP_DEBUG ? 'Yes' : 'No'} />
            <SettingItem label="WP_DEBUG_LOG" value={systemInfo.constants.WP_DEBUG_LOG ? 'Yes' : 'No'} />
            <SettingItem label="WP_DEBUG_DISPLAY" value={systemInfo.constants.WP_DEBUG_DISPLAY ? 'Yes' : 'No'} />
            <SettingItem label="WP_CACHE" value={systemInfo.constants.WP_CACHE ? 'Yes' : 'No'} />
          </CollapsibleSection>
        </div>
      ) : (
        <div className="jwt-text-center jwt-py-8">
          <div className="jwt-text-red-500">Failed to load system information</div>
        </div>
      )}
    </PageWrapper>
  );
}

